-- ----------------------------
-- Table structure for category_comics
-- ----------------------------
DROP TABLE IF EXISTS `category_comics`;
CREATE TABLE `category_comics`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `comic_id` bigint(20) UNSIGNED NOT NULL,
  `category_id` bigint(20) UNSIGNED NOT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `category_comics_comic_id_foreign`(`comic_id`) USING BTREE,
  INDEX `category_comics_category_id_foreign`(`category_id`) USING BTREE,
  CONSTRAINT `category_comics_category_id_foreign` FOREIGN KEY (`category_id`) REFERENCES `categories` (`id`) ON DELETE CASCADE ON UPDATE RESTRICT,
  CONSTRAINT `category_comics_comic_id_foreign` FOREIGN KEY (`comic_id`) REFERENCES `comics` (`id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 23 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = DYNAMIC;

