-- ----------------------------
-- Table structure for chapters
-- ----------------------------
DROP TABLE IF EXISTS `chapters`;
CREATE TABLE `chapters`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Tieu de',
  `comic_id` int(11) NOT NULL COMMENT 'Thuoc truyen nao',
  `description` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Mo ta',
  `chapter_content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Noi dung truyen',
  `status` int(11) NOT NULL COMMENT 'Trang thai 0-Dang cap nhat, 1-da hoan thanh',
  `slug_chapter` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Slug chapter',
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of chapters
-- ----------------------------
INSERT INTO `chapters` VALUES (1, 'Mở đầu', 1, 'Chương mở đầu của tập 1', '<p style=\"text-align:justify\">Trộm mộ chẳng phải việc tham quan ngắm cảnh, chẳng phải ng&acirc;m thơ c&acirc;u đối, chẳng phải th&ecirc;u hoa dệt v&oacute;c, kh&ocirc;ng thể nh&atilde; nhặn khi&ecirc;m nhường, kh&ocirc;ng thể ung dung nh&agrave;n tản, chẳng thể tuần tự theo t&ocirc;n ti &quot;thi&ecirc;n địa qu&acirc;n th&acirc;n sư&quot; cho được. Trộm mộ l&agrave; một thứ kĩ thuật, một thứ kĩ thuật ph&aacute; hoại. Tự thủa xưa, khi c&aacute;c qu&yacute; tộc x&acirc;y dựng lăng mộ, ắt đ&atilde; t&igrave;m đủ mọi c&aacute;ch để ph&ograve;ng ngừa trộm cắp, vậy n&ecirc;n hẳn l&agrave; c&oacute; bao nhiểu thủ đoạn cũng đ&atilde; d&ugrave;ng hết cả rồi, trong mộ thường sắp đặt đủ loại cạm bẫy &aacute;m kh&iacute;, n&agrave;o đ&aacute; tảng, c&aacute;t tr&ocirc;i, n&agrave;o t&ecirc;n độc, rắn rết, hố ch&ocirc;ng...nhiều kh&ocirc;ng kể xiết. Đến đời nh&agrave; Minh, chịu ảnh hưởng của kỹ thuật phương T&acirc;y, một số ng&ocirc;i mộ lớn thậm ch&iacute; đ&atilde; sử dụng cả hệ thống b&aacute;nh răng r&ograve;ng rọc của phương T&acirc;y, đặc biệt l&agrave; lăng của c&aacute;c ho&agrave;ng đế nh&agrave; Thanh, c&oacute; thể coi l&agrave; những kiệt t&aacute;c gom th&acirc;u kỹ thuật ph&ograve;ng trộm của mấy ng&agrave;n năm cũng xứng. Đại qu&acirc;n phiệt T&ocirc;n Điện Anh muốn khai quật Đ&ocirc;ng lăng để lấy v&agrave;ng bạc sung v&agrave;o qu&acirc;n ph&iacute;, đ&atilde; huy động cả một đội qu&acirc;n, vừa đ&agrave;o bới vừa nổ m&igrave;n cũng phải mất năm s&aacute;u ng&agrave;y trời mới xong, độ kiện cố của mấy lăng ấy thế n&agrave;o chắc kh&ocirc;ng cần n&oacute;i cũng c&oacute; thể mường tưởng ra được. B&agrave;i to&aacute;n d&agrave;nh cho những kẻ trộm mộ thời bấy giờ l&agrave; phải nghĩ ra trăm phương ngh&igrave;n kể ph&aacute; giải những cạm bẫy đ&oacute;, đề v&agrave;o mộ t&igrave;m kiếm b&aacute;u vật. Thế nhưng hiện nay, việc t&igrave;m ra mộ cổ c&ograve;n kh&oacute; hơn việc khai quật mộ nhiều lần, những ng&ocirc;i mộ lớn x&acirc;y theo kiểu đắp nấm dựng bia r&otilde; r&agrave;ng th&igrave; đ&atilde; bị người ta đ&agrave;o bới gần hết từ l&acirc;u rồi. C&ograve;n nếu muốn t&igrave;m mộ cổ đ&atilde; v&ugrave;i s&acirc;u trong l&ograve;ng đất nhiều năm, lại kh&ocirc;ng c&oacute; dấu mốc g&igrave; tr&ecirc;n mặt đất, th&igrave; phải cần đến một số kỹ thuật nhất định cũng như v&agrave;i thứ c&ocirc;ng cụ đặc biệt, c&aacute;c loại như khoan sắt, đinh tre, xẻng Lạc Dương, th&aacute;m &acirc;m trảo, hắc triết tử, to&agrave;n địa long nhất loạt đều từ đ&acirc;y m&agrave; ra. Ngo&agrave;i ra, cũng c&oacute; một số cao thủ lại kh&ocirc;ng dựa v&agrave;o mấy thứ ấy, c&oacute; người t&igrave;m mộ bằng c&aacute;ch lần t&igrave;m đầu mối được ghi ch&eacute;p trong thư tịch cổ xưa, cũng c&oacute; một số rất &iacute;t người hiểu được b&iacute; thuật, c&oacute; thể t&igrave;m ra mộ huyệt bằng khả năng xem phong thủy, đọc mạch tương đốicủa đồi n&uacute;i s&ocirc;ng ng&ograve;i, bản th&acirc;n tối ch&iacute;nh l&agrave; thuộc loại cuối c&ugrave;ng n&agrave;y. T&ocirc;i đ&atilde; đặt ch&acirc;n qua khắp mọi nẻo đường, trải qua v&ocirc; số chuyện ly kỳ qu&aacute;i dị, nếu đem kể từng chuyện ra, chắc hẳn sẽ khiến kẻ thấy kinh hồn, người nghe bạt v&iacute;a, n&oacute;i cho c&ugrave;ng th&igrave; những sự t&iacute;ch lấp biển lật s&ocirc;ng, xẻ đất che trời, h&igrave;nh rồng tướng hổ như vậy kể ra đ&acirc;u cũng phải tầm thường.</p>\r\n\r\n<p>Những sự t&iacute;ch ấy bắt đầu phải kể từ cuốn s&aacute;ch cũ Thập lục tự &Acirc;m dương Phong thuỳ b&iacute; thuật &ocirc;ng nội t&ocirc;i để lại, cuốn t&agrave;n thư n&agrave;y, chẳng biết cớ g&igrave; m&agrave; nửa cuối lại bị người ta x&eacute; toẹt đi rồi, chỉ c&ograve;n lại phần đầu viết về b&iacute; thuật phong thuỷ. Những điều trong s&aacute;ch, qu&aacute; nửa l&agrave; c&aacute;c b&iacute; thuật độc m&ocirc;n để đọc hiểu bố cục phong thuỷ của mộ t&aacute;ng...</p>', 1, 'mo-dau', '2022-12-26 09:37:44', '2022-12-26 10:53:06');
INSERT INTO `chapters` VALUES (2, 'Chương 1: Ả người giấy và anh bạn chuột', 1, 'Chương 1 phần 1', '<p style=\"text-align:justify\">Nội t&ocirc;i t&ecirc;n gọi l&agrave; Hồ Quốc Hoa. Tổ ti&ecirc;n nh&agrave; họ Hồ l&agrave; địa chủ c&oacute; tiếng trong v&ugrave;ng, v&agrave;o thời huy ho&agrave;ng nhất đ&atilde; từng mua hơn bốn mươi gian nh&agrave; liền kề nhau ở ba con ng&otilde; trong th&agrave;nh, trong họ cũng c&oacute; người l&agrave;m quan, c&oacute; người đi bu&ocirc;n b&aacute;n, cũng từng quy&ecirc;n tiền hỗ trợ c&ocirc;ng việc t&agrave;o vận v&agrave; cung cấp th&oacute;c l&uacute;a cho triều đ&igrave;nh M&atilde;n Thanh.</p>\r\n\r\n<p>Tục ngữ c&oacute; c&acirc;u: &quot;Kh&ocirc;ng ai gi&agrave;u ba họ, kh&ocirc;ng ai kh&oacute; ba đời&quot; kể cũng đ&uacute;ng thật, trong nh&agrave; d&ugrave; c&oacute; n&uacute;i v&agrave;ng n&uacute;i bạc, th&igrave; cũng kh&ocirc;ng trụ được khi c&oacute; những đứa ph&aacute; gia chi tử chỉ biết ăn t&agrave;n ph&aacute; hại.</p>\r\n\r\n<p>Đến những năm D&acirc;n Quốc, Hồ gia truyền tới đời &ocirc;ng t&ocirc;i th&igrave; gia đạo suy vi, thoạt ti&ecirc;n l&agrave; chia nh&agrave;, gai sản chia cho &ocirc;ng cũng kh&ocirc;ng &iacute;t, đủ để ăn no mặc ấm suốt đời, thế nhưng &ocirc;ng lại kh&ocirc;ng chịu học điều hay lẽ tốt, đương nhi&ecirc;n điều n&agrave;y c&oacute; ảnh hưởng đến ho&agrave;n cảnh x&atilde; hội l&uacute;c bấy giờ, trước th&igrave; say m&ecirc; cờ bạc, sau lại nghiện Ph&uacute;c Thọ cao (1), cuối c&ugrave;ng cả gia t&agrave;i bị &ocirc;ng đem ti&ecirc;u cho sạch b&aacute;ch.</p>\r\n\r\n<p>Thời trai trẻ h&uacute;t h&iacute;t ăn chơi, kh&ocirc;ng tệ n&agrave;o &ocirc;ng kh&ocirc;ng phạm, cuối c&ugrave;ng th&igrave; quẫn đến nỗi một cắc chẳng c&ograve;n. Người ta một khi đ&atilde; l&ecirc;n cơn nghiện th&igrave; v&ograve; ruột bứt gan, sao m&agrave; nhịn nổi, nhưng kh&ocirc;ng c&oacute; tiền th&igrave; ai cho &ocirc;ng h&uacute;t? Trước kia l&uacute;c c&oacute; tiền c&oacute; của, từ thằng chủ cho đến con hầu trong ổ h&uacute;t hễ thấy &ocirc;ng l&agrave; xoen xo&eacute;t gọi &quot;Hồ đại gia&quot;, tiếp đ&atilde; &acirc;n cần chu đ&aacute;o lắm, nhưng đến khi kh&ocirc;ng xu d&iacute;nh t&uacute;i, th&igrave; ch&uacute;ng coi &ocirc;ng l&agrave; thằng ăn m&agrave;y, ch&uacute;ng vừa chửi vừa mắng v&agrave; đ&aacute;nh đuổi, hắt &ocirc;ng như thể hắt hủi vậy.</p>\r\n\r\n<p>Người ta nhược bằng t&uacute;ng quẫn qu&aacute;, th&igrave; mấy mớ quan niệm về đạo đức li&ecirc;m sỉ cũng c&oacute; s&aacute; g&igrave;, nội t&ocirc;i cũng nghĩ được một c&aacute;ch, đi lừa tiền &ocirc;ng cậu. Cậu của &ocirc;ng t&ocirc;i biết &ocirc;ng l&agrave; thằng nghiện ngập ph&aacute; gia chi tử, thường ng&agrave;y th&igrave; một h&agrave;o một cắc chẳng cho, nhưng lần n&agrave;y &ocirc;ng phỉnh cậu, bảo rằng sắp lấy vợ, xin cậu gom cho &iacute;t tiền.</p>\r\n\r\n<p>&Ocirc;ng cậu nghe n&oacute;i vậy, x&uacute;c động đến nỗi nhạt nho&agrave; nước mắt, thằng ch&aacute;u bất hiếu giờ n&agrave;y mới con như cũng l&agrave;m được một việc tử tế, nếu kiếm được một c&ocirc; ch&aacute;u d&acirc;u hiền đức tr&ocirc;ng coi n&oacute; cẩn thận, dạy dỗ n&oacute; c&oacute; khi về sau lại kh&aacute; l&ecirc;n cũng kh&ocirc;ng chừng.</p>\r\n\r\n<p>Vậy l&agrave; &ocirc;ng cậu đưa nội t&ocirc;i hai mươi đồng Đại Dương, dặn rằng lấy vợ th&igrave; sau phải sống cho đ&agrave;ng ho&agrave;ng, chớ d&iacute;nh v&agrave;o Ph&uacute;c thọ cao nữa, để mấy h&ocirc;m nữa rảnh, &ocirc;ng sẽ đ&iacute;ch th&acirc;n tới nh&agrave; xem mặt ch&aacute;u d&acirc;u.</p>\r\n\r\n<p>&Ocirc;ng nội Hồ Quốc Hoa của t&ocirc;i c&oacute; rất nhiều tr&ograve; ma m&atilde;nh, đễ đối ph&oacute; với cậu, sau khi về nh&agrave;, &ocirc;ng liền v&agrave;o th&ocirc;n t&igrave;m một tay thợ chuy&ecirc;n l&agrave;m mấy thứ người giấy, ngựa giấy (ch&iacute;nh l&agrave; loại để đốt cho người chết). L&atilde;o thợ n&agrave;y tay nghề rất cao, chỉ cần l&agrave; thứ c&oacute; thể tả được l&agrave; l&atilde;o c&oacute; thể l&agrave;m ra giống hệt như thế.</p>\r\n\r\n<p>Theo y&ecirc;u cầu của &ocirc;ng nội, l&atilde;o h&agrave;ng m&atilde; l&agrave;m ra một c&ocirc; g&aacute;i bằng giấy phết hồ, sau đ&oacute; d&ugrave;ng m&agrave;u nước vẽ l&ecirc;n l&ocirc;ng m&agrave;y, mắt, mũi, &aacute;o quần, đầu t&oacute;c, nh&igrave;n từ xa, th&igrave; &ocirc;i th&ocirc;i, tr&ocirc;ng giống y như người thật vậy.</p>\r\n\r\n<p>&Ocirc;ng nội t&ocirc;i c&otilde;ng người giấy về nh&agrave;, đặt v&agrave;o giường trong, lại đắp chăn l&ecirc;n, trong bụng tự lấy l&agrave;m y&ecirc;n t&acirc;m lắm, đợi mấy h&ocirc;m nữa &ocirc;ng cậu đến, th&igrave; cứ viện cớ vợ ốm, nằm liệt giường kh&ocirc;ng thể tiếp kh&aacute;ch, để &ocirc;ng cậu đứng nh&igrave;n từ xa l&agrave; được. Ngắm thấy đắc &yacute;, &ocirc;ng bất gi&aacute;c h&aacute;t rống l&ecirc;n v&agrave;i c&acirc;u, rồi chuồn lu&ocirc;n v&agrave;o th&agrave;nh h&uacute;t thuốc.</p>\r\n\r\n<p>Mấy h&ocirc;m sau, &ocirc;ng cậu quả nhi&ecirc;n đến thật, lại c&ograve;n mua vải v&oacute;c b&aacute;nh tr&aacute;i thăm c&ocirc; ch&aacute;u d&acirc;u, nội t&ocirc;i mang những lời nhẽ đ&atilde; toan t&iacute;nh từ trước ra lần lữa tho&aacute;i th&aacute;c, than rằng vợ kh&ocirc;ng khoẻ, kh&ocirc;ng tiếp kh&aacute;ch được, rồi v&eacute;n r&egrave;m để cậu đứng ngo&agrave;i nh&igrave;n, sau đ&oacute; lại lập tức bu&ocirc;ng ngay.</p>\r\n\r\n<p>&Ocirc;ng cậu kh&ocirc;ng vừa l&ograve;ng, mới nhủ, c&aacute;i thằng ranh con n&agrave;y lại đối ph&oacute; với cậu m&agrave;y như thế &agrave;? Kh&ocirc;ng được! H&ocirc;m nay dứt kho&aacute;t phải gặp được ch&aacute;u d&acirc;u mới th&ocirc;i, c&oacute; bệnh th&igrave; cho tiền mời thầy lang đến chữa!</p>\r\n\r\n<p>&Ocirc;ng nội sống chết n&iacute;u &ocirc;ng cậu lại kh&ocirc;ng cho v&agrave;o, nhưng c&agrave;ng n&iacute;u k&eacute;o th&igrave; &ocirc;ng cậu c&agrave;ng sinh nghi, đ&ocirc;i b&ecirc;n cứ thế co k&eacute;o, cuối c&ugrave;ng kh&ocirc;ng ngăn được, &ocirc;ng cậu chạy xộc v&agrave;o nh&agrave; trong, vừa nh&igrave;n l&ecirc;n giường, su&yacute;t nữa th&igrave; chết khiếp, một bộ mặt đ&agrave;n b&agrave; trắng bệch, c&ograve;n phết đỏ tr&ograve;n hai m&aacute;, đ&ocirc;i mắt trợn ngược, nh&igrave;n thẳng l&ecirc;n trần nh&agrave;, r&otilde; r&agrave;ng l&agrave; một ả người giấy.</p>\r\n\r\n<p>M&ugrave;a xu&acirc;n năm ấy đ&atilde; xảy ra rất nhiều chuyện, vụ &ocirc;ng Hồ Quốc Hoa mua người giấy gạt tiền bại lộ, &ocirc;ng cậu giận qu&aacute; ho&aacute; bệnh, kh&ocirc;ng ngồi dậy nổi, chưa đến ba h&ocirc;m th&igrave; rũ &aacute;o chầu trời.</p>\r\n\r\n<p>Họ h&agrave;ng b&egrave; bạn của Hồ gia, ai cũng ph&ograve;ng nội t&ocirc;i như ph&ograve;ng trộm, chớ cho vay tiền, ngay đến cơm thừa cũng kh&ocirc;ng cho &ocirc;ng lấy một miếng. &Ocirc;ng đ&agrave;nh b&aacute;n nốt đ&ocirc;i tủ gỗ đ&agrave;n lấy hai đồng bạc, chiếc tủ n&agrave;y l&agrave; của hồi m&ocirc;n của cụ b&agrave;, &ocirc;ng vốn định giữ lại để tưởng niệm, chưa nỡ đem đi cầm, nhưng khi cơn nghiện đ&atilde; ph&aacute;t t&aacute;c, th&igrave; cũng kh&ocirc;ng c&ograve;n nghĩ ngợi được nhiều thế nữa rồi. Cầm hai đồng bạc ấy đi mua một d&uacute;m Ph&uacute;c thọ cao, chạy về nh&agrave; liện vội v&atilde; s&agrave; v&agrave;o giường ch&acirc;m điếu thuốc h&iacute;t mạnh hai hơi, cơ thể bỗng dưng nhẹ bẫng, tựa hồ như đang chấp chới tr&ecirc;n m&acirc;y.</p>\r\n\r\n<p>L&uacute;c ấy, &ocirc;ng cảm thấy sướng như l&ecirc;n ti&ecirc;n, dăm ba c&aacute;i chuyện bị người ta khinh rẻ, ức ***********, rồi bị mắng bị chửi h&agrave;ng ng&agrave;y đều vặt v&atilde;nh cả. H&iacute;t th&ecirc;m hai hơi tiếp, &ocirc;ng bỗng ph&aacute;t hiện ra năm tr&ecirc;n c&aacute;i giường n&aacute;t bươm của m&igrave;nh c&ograve;n c&oacute; một đống đen đ&uacute;a x&ugrave; x&igrave;, nh&igrave;n lại cho kỹ, ho&aacute; ra g&oacute;c giường c&oacute; một con chuột cống to tướng đang chồm hỗm. Con chuột n&agrave;y &yacute; chừng phải sống l&acirc;u rồi, ria m&eacute;p n&oacute; đ&atilde; trắng cả, m&igrave;nh n&oacute; phải xấp xỉ con m&egrave;o. N&oacute; đang nẳm h&iacute;t thứ kh&oacute;i bốc ra từ tẩu thuốc của Hồ Quốc Hoa, dường như cũng biết đến lợi &iacute;ch của thuộc phiện, lỗi mũi cứ khịt khịt tham lam tận hưởng.</p>\r\n\r\n<p>Nội t&ocirc;i bỗng thấy hứng th&uacute; lắm, quay ra bảo con chuột: &quot;M&agrave;y cũng nghiện thuốc hả? Xem ra tao với m&agrave;y l&agrave; người đồng đạo rồi.&quot; N&oacute;i đoạn &ocirc;ng lại h&uacute;t một hơi, rồi phả thứ kh&oacute;i nồng nặc ấy v&agrave;o con chuột, con chuột dường như biết &ocirc;ng t&ocirc;i kh&ocirc;ng c&oacute; &aacute;c &yacute;, cũng chẳng sợ &ocirc;ng, b&egrave;n nghển cổ đ&oacute;n lấy l&agrave;n kh&oacute;i đặc đương phả v&agrave;o m&igrave;nh. Qu&aacute; trưa, dường như đ&atilde; thoả m&atilde;n được cơn nghiện, con chuột cũng lững thững b&ograve; đi.</p>\r\n\r\n<p>Mấy ng&agrave;y liền đều như vậy, h&ocirc;m n&agrave;o con chuột cũng b&ograve; đến c&ugrave;ng h&iacute;t thuốc với &ocirc;ng nội, &ocirc;ng t&ocirc;i đi đ&acirc;u cũng bị người ta khinh miệt, chung quanh chẳng c&oacute; lấy một tấm bạn t&acirc;m t&igrave;nh, thấy con chuột quần m&igrave;nh như vậy đ&acirc;m ra th&iacute;ch ch&iacute;, c&oacute; lần con chuột đến hơi muộn, &ocirc;ng cũng gắng nhịn cơn nghiện m&agrave; chờ n&oacute;.</p>\r\n\r\n<p>Thế nhưng niềm vui ngắn ngủi chẳng t&agrave;y gang, nh&agrave; &ocirc;ng nội b&acirc;y giờ chỉ c&ograve;n lại chiếc giường trơ v&agrave; bốn bức tường, chẳng c&ograve;n đồng n&agrave;o m&agrave; mua thuốc nữa, &ocirc;ng ủ rũ v&igrave; hết c&aacute;ch, than thở với con chuột: &quot;Chuột ơi chuột &agrave;, h&ocirc;m nay l&agrave; ng&agrave;y c&ugrave;ng th&aacute;ng kiệt rồi, chẳng c&ograve;n tiền mua Ph&uacute;c thọ cao nữa đ&acirc;u, e rằng chẳng được c&ugrave;ng m&agrave;y ngửi lại c&aacute;i m&ugrave;i nồng n&agrave;n ấy nữa.&quot; N&oacute;i dứt &ocirc;ng cứ sụt sịt kh&ocirc;ng th&ocirc;i.</p>\r\n\r\n<p>Con chuột nghe những lời &ocirc;ng n&oacute;i, hai mắt long lanh chớp chớp như nghĩ ngợi g&igrave;, rồi bỗng quay ngoắt bỏ đi. Đến khi trời tối, n&oacute; ngậm một đồng tiền bạc quay về nh&agrave; b&ecirc;n cạnh gối &ocirc;ng nội, &ocirc;ng nội vừa mừng rỡ lại vừa kinh ngạc, liền chạy cả đ&ecirc;m v&agrave;o th&agrave;nh mua lấy một miếng Ph&uacute;c thọ cao, về nh&agrave; l&agrave; đốt ngay b&ecirc;n đ&egrave;n, c&ugrave;ng con chuột h&uacute;t một trận đ&atilde; đời no sướng.</p>\r\n\r\n<p>Ng&agrave;y h&ocirc;m sau con chuột lại ngậm ba đồng bạc tha về, &ocirc;ng nội mừng rơn kh&ocirc;n tả, nhớ lại một điển cố được học từ thời c&ograve;n trong trường tư thục trước đ&acirc;y, b&egrave;n n&oacute;i với n&oacute;: &quot;Kẻ biết Quản Trọng, ch&iacute;nh B&agrave;o Th&uacute;c Nha đ&oacute; vậy, nay người biết ta bần h&agrave;n m&agrave; hậu đ&atilde;i ta, thật l&agrave; tri kỉ của ta vậy, nếu chẳng ch&ecirc; bai, ch&uacute;ng ta h&atilde;y kết nghĩa kim lan đi!&quot; Từ đ&oacute; &ocirc;ng với chuột xưng huynh gọi đệ, gọi l&agrave; &quot;anh chuột&quot;, ăn uống c&ugrave;ng chia, thuốc h&uacute;t chung hưởng, lại c&ograve;n l&agrave;m cho con chuột c&aacute;i ổ bằng b&ocirc;ng, để n&oacute; c&ugrave;ng ngủ tr&ecirc;n giường.</p>\r\n\r\n<p>Người chuột nương tựa, như đ&ocirc;i bạn t&acirc;m đầu &yacute; hợp, con chuột h&agrave;ng ng&agrave;y đều đi tha bạc về, &iacute;t th&igrave; một hai đồng, nhiều th&igrave; bốn năm đồng, từ đ&oacute; &ocirc;ng nội chẳng c&ograve;n g&aacute;nh lo cơm &aacute;o nữa. Bao năm về sau, mỗi khi nội t&ocirc;i nhớ lại, vẫn n&oacute;i rằng những chuỗi ng&agrave;y ấy l&agrave; qu&atilde;ng thời gian vui sướng nhất đời &ocirc;ng.</p>\r\n\r\n<p>Bẵng nửa năm tr&ocirc;i qua, &ocirc;ng t&ocirc;i ng&agrave;y một gi&agrave;u c&oacute; nhưng trớ tr&ecirc;u thay chẳng phải c&oacute; c&acirc;u &quot;Gi&agrave;u sang gặp thầy bạn v&agrave;ng, sa cơ lỡ vận gặp to&agrave;n tiểu nh&acirc;n&quot; đ&oacute; sao, cũng chỉ biết tr&aacute;ch c&aacute;i số &ocirc;ng nội Hồ Quốc Hoa của t&ocirc;i l&agrave; số nghiệp số hạn, bị một thằng tiểu nh&acirc;n nh&ograve;m ng&oacute;.</p>\r\n\r\n<p>Trong th&ocirc;n c&oacute; thằng v&ocirc; lại Vương Nhị, thằng n&agrave;y đ&acirc;u b&igrave; được với &ocirc;ng t&ocirc;i, &ocirc;ng nội ch&iacute; &iacute;t cũng từng gi&agrave;u c&oacute;, d&ugrave; g&igrave; cũng từng l&agrave; &quot;Hồ đại thiếu gia&quot; ng&oacute;t ngh&eacute;t hai chục năm, c&ograve;n thằng Vương Nhị kia th&igrave; đ&acirc;u c&oacute; c&aacute;i ph&uacute;c ấy, tổ ti&ecirc;n t&aacute;m đời nh&agrave; hắn kể cả hắn b&acirc;y giờ đều chưa từng mặc nổi c&aacute;i quần n&agrave;o l&agrave;nh lặn. Vương Nhị thấy &ocirc;ng t&ocirc;i gia nghiệp suy bại th&igrave; &quot;ch&aacute;y h&agrave;ng x&oacute;m, vỗ tay reo mừng&quot;, thỉnh thoảng lại v&ocirc; duy&ecirc;n v&ocirc; cớ bức *********** l&agrave;m nhục &ocirc;ng nội, bắt nạt Hồ đại thiếu gia năm xưa mấy phen cho hả dạ.</p>\r\n\r\n<p>Gần đ&acirc;y Vương Nghị lấy l&agrave;m lạ, &ocirc;ng t&ocirc;i l&agrave; một t&ecirc;n ngh&egrave;o đ&oacute;i chẳng c&oacute; c&ocirc;ng ăn việc l&agrave;m, đồ đạc lại đều đem đi cầm cố cả, ngay đến họ h&agrave;ng th&acirc;n th&iacute;ch cũng đ&atilde; chết gần hết, chẳng hiểu cớ g&igrave; m&agrave; vẫn h&uacute;t thuốc được như thường? Tiền mua thuốc lấy đ&acirc;u ra? Hắn b&egrave;n nghĩ bụng: n&oacute;i kh&ocirc;ng chừng thằng ấy lại đi ăn trộm. Ta phải r&igrave;nh n&oacute; xem sao, đợi l&uacute;c n&oacute; th&oacute; đồ nh&agrave; người ta, m&igrave;nh bắt quả tang rồi b&aacute;o cho quan phủ, kiếm mấy đồng Đại Dương tiền thưởng cũng tốt.</p>\r\n\r\n<p>Thế nhưng r&igrave;nh một thời gian, ph&aacute;t hiện &ocirc;ng nội ngoại trừ những khi thi thoảng v&agrave;o th&agrave;nh mua đồ ăn v&agrave; thuốc h&uacute;t, th&igrave; căn bản l&agrave; kh&ocirc;ng bước ch&acirc;n ra khỏi cửa, m&agrave; cũng chẳng qua lại với bất kỳ người n&agrave;o. C&agrave;ng t&ograve; m&ograve; muốn biết tiền ở đ&acirc;u ra, Vương Nhị lại c&agrave;ng thấy bứt rứt kh&ocirc;ng y&ecirc;n.</p>\r\n\r\n<p>Một h&ocirc;m, nh&acirc;n l&uacute;c &ocirc;ng nội ra ngo&agrave;i mua đồ, Vương Nhị thừa cơ tr&egrave;o tường nhảy v&agrave;o nh&agrave; trong, lục lọi mọi nơi gầm b&agrave;n hốc tủ h&ograve;ng t&igrave;m ra xem &ocirc;ng t&ocirc;i rốt cuộc c&oacute; b&iacute; mật g&igrave;. Đột nhi&ecirc;n nh&igrave;n thấy tr&ecirc;n giường c&oacute; một con chuột to x&ugrave; đang ngủ, hắn b&egrave;n tiện tay vất lu&ocirc;n v&agrave;o trong si&ecirc;u nước đang đun tr&ecirc;n l&ograve;, sau đ&oacute; đậy nắp lại, nghĩ bụng họ Hồ kia về nh&agrave; uống nước, ta sẽ nấp b&ecirc;n ngo&agrave;i xem c&oacute; tr&ograve; hay ho.</p>\r\n\r\n<p>Chẳng đợi Vương Nhị chạy ra, &ocirc;ng nội đ&atilde; về đến nh&agrave;, vừa vặn dồn chặn hắn lại b&ecirc;n trong. Vừa thấy con chuột bị luộc ch&iacute;n trong si&ecirc;u nước, tức thời mắt &ocirc;ng liền đỏ sọc l&ecirc;n, vớ ngay con dao th&aacute;i rau, ch&eacute;m v&agrave;o thằng vộ lại họ Vương kia mười mấy nh&aacute;t. May m&agrave; nội t&ocirc;i l&agrave; kẻ nghiện ngập, ch&acirc;n tay yếu ớt n&ecirc;n tuy Vương Nhị bị ch&eacute;m tr&uacute;ng mấy nh&aacute;t dao nhưng cũng kh&ocirc;ng c&oacute; vết thương n&agrave;o ch&iacute; mạng cả, Vương Nhị m&aacute;u me b&ecirc; bết khắp người, chạy thục mạng tới đội bảo an cầu cứu, đội trưởng đội bảo an l&agrave; họ h&agrave;ng với một tay qu&acirc;n phiệt địa phương, bấy giờ đang mởi tay qu&acirc;n phiệt n&agrave;y nhắm rượu, t&ecirc;n đội trưởng biết tin liền nội giận đ&ugrave;ng đ&ugrave;ng, giữa ban ng&agrave;y ban mặt h&agrave;nh h&ugrave;ng giết ngừơi c&ograve;n coi vương ph&aacute;p ra g&igrave; nữa? Vội sai ngay mấy t&ecirc;n thuộc hạ đi bắt tr&oacute;i nội t&ocirc;i về.</p>\r\n\r\n<p>&Ocirc;ng nội bị giải về đồn, t&ecirc;n đội trưởng nạt giọng qu&aacute;t hỏi, cớ sao cầm dao h&agrave;nh hung đ&ograve;i giết Vương Nhị.</p>\r\n\r\n<p>&Ocirc;ng t&ocirc;i nước mắt lưng tr&ograve;ng, kh&oacute;c nấc kể lại đầu đu&ocirc;i sự t&igrave;nh, cuối c&ugrave;ng n&oacute;i với giọng đầy ai o&aacute;n: &quot;Nhớ khi xưa t&ocirc;i khốn khổ khốn nạn, kh&ocirc;ng c&oacute; anh chuột t&ocirc;i đ&atilde; chẳng sống được đến ng&agrave;y h&ocirc;m nay, n&agrave;o ngờ chỉ sơ suất một ch&uacute;t đ&atilde; để anh mất mạng, tuy kh&ocirc;ng phải do t&ocirc;i giết nhưng anh chuột lai v&igrave; t&ocirc;i m&agrave; chết. T&ocirc;i m&agrave; phụ người bạn hiền dưới ch&iacute;n suối n&agrave;y th&igrave; t&igrave;nh nghĩa ở đ&acirc;u? Một m&igrave;nh t&ocirc;i l&agrave;m một m&igrave;nh t&ocirc;i chịu, đ&atilde; ch&eacute;m c&aacute;i thằng kh&ocirc;ng Vương Nhị vậy rồi, giờ muốn phạt muốn giết xin nghe đại nh&acirc;n ph&aacute;n xử, chỉ xin đại nh&acirc;n cho t&ocirc;i về nh&agrave; an t&aacute;ng anh chuột, rồi t&ocirc;i chết cũng nhắm mắt.&quot;</p>\r\n\r\n<p>Kh&ocirc;ng đợi t&ecirc;n đội trưởng n&oacute;i g&igrave;, tay qu&acirc;n phiệt b&ecirc;n cạnh đ&atilde; cảm động xu&yacute;t xoa, n&oacute;i với &ocirc;ng t&ocirc;i: &quot;Mẹ kiếp! Kh&ocirc;ng vong ơn ch&iacute;nh l&agrave; nh&acirc;n, kh&ocirc;ng phụ l&ograve;ng tức l&agrave; nghĩa, đối với con chuột m&agrave; c&ograve;n như vậy huống hồ đối với con người? Ta niệm t&igrave;nh cậu c&oacute; nh&acirc;n c&oacute; nghĩa, giờ lại kh&ocirc;ng chốn nương th&acirc;n, từ r&agrave;y h&atilde;y theo ta t&ograve;ng qu&acirc;n l&agrave;m ph&oacute; quan đi.&quot;</p>\r\n\r\n<p>S&uacute;ng ống l&agrave; ch&iacute;nh quyền, trong thời loạn lạc, lời của kẻ nắm giữ binh quyền ch&iacute;nh l&agrave; vương ph&aacute;p. Tay qu&acirc;n phiệt ra lệnh cho bọn thủ hạ d&ugrave;ng roi quất Vương Nhị một trận te tua để nội t&ocirc;i b&otilde; tức, sau đ&oacute; cho &ocirc;ng về an t&aacute;ng con chuột. &Ocirc;ng t&ocirc;i trở về lấy hộp gỗ liệm x&aacute;c bạn chuột, rồi đ&agrave;o một c&aacute;i hố ch&ocirc;n đi, kh&oacute;c tỉ t&ecirc; hồi l&acirc;u rồi mới dứt &aacute;o đi theo t&ecirc;n qu&acirc;n phiệt kia.</p>\r\n\r\n<p>Tục ngữ n&oacute;i hay thật: &quot;Đ&oacute;i thời khoai r&aacute;y cũng ngon, no thời x&ocirc;i thịt bấy c&ograve;n dửng dưng&quot;. Con ngừơi ta một khi ngh&egrave;o khổ đi&ecirc;u đứng, người kh&aacute;c dẫu chỉ cho b&aacute;t ch&aacute;o, miếng cơm th&igrave; hắn cũng cảm tạ ơn đức v&ocirc; c&ugrave;ng, huống hồ con chuột kia lại mang về cho &ocirc;ng t&ocirc;i biết bao nhi&ecirc;u tiền của. Đương nhi&ecirc;n tiền ấy cũng chỉ l&agrave; tiền ăn cắp, th&aacute;nh nh&acirc;n dạy dẫu chết cũng chẳng uống nước trộm d&ograve;ng, c&oacute; điều đ&oacute; l&agrave; ti&ecirc;u chuẩn đạo đức của những bậc v&atilde;ng th&aacute;nh ti&ecirc;n hiền, cổ nh&acirc;n c&ograve;n hiếm người l&agrave;m được, huống hồ l&agrave; hạng ph&agrave;m phu tục tử như Hồ Quốc Hoa! Trước thường nghe h&uacute;t thuốc trong ph&ograve;ng, l&acirc;u ng&agrave;y chuột ruồi nghiện cả, lời n&agrave;y t&igrave;nh chẳng hề sai ch&uacute;t n&agrave;o!</p>\r\n\r\n<p>--------------------------------</p>\r\n\r\n<p>Ch&uacute; th&iacute;ch:</p>\r\n\r\n<p>1. Ph&uacute;c thọ cao: vốn t&ecirc;n của một vị thuốc, l&agrave;m từ vỏ quả thẩu (tức &quot;c&ugrave; t&uacute;c&quot;, gọi theo người Trung Hoa, hay &quot;nha phiến, a phiến&quot; dịch theo từ &quot;opium&quot; trong tiếng Anh), c&oacute; t&aacute;c dụng l&agrave;m dịu cơn đau, dễ ngủ...(mọi ch&uacute; th&iacute;ch đều của dịch giả)</p>', 1, 'chuong-1-a-nguoi-giay-va-anh-ban-chuot', '2022-12-26 09:56:17', '2022-12-26 10:53:15');
INSERT INTO `chapters` VALUES (3, 'Chapter 1: Câu chuyện 1', 9, 'Câu chuyện số 1', '<p>C&Acirc;U CHUY&Ecirc;N 1: THẬT V&Agrave; ĐEN<br />\r\n<br />\r\nCHƯƠNG 2: L&Agrave;M HẾT HƠI NHƯNG CỨ CHƠI CHO SƯỚNG<br />\r\n<br />\r\n************************************************************************************<br />\r\n<br />\r\nCon H&agrave; la l&ecirc;n<br />\r\n<br />\r\n- What? M&agrave;y bị nho từ điển &agrave;?<br />\r\n<br />\r\nĐ&aacute;m con g&aacute;i ch&uacute;ng t&ocirc;i ngu ngơ &ugrave;a theo<br />\r\n<br />\r\n- đ&uacute;ng đ&oacute;, M&agrave;y bị nho từ điển hả My?<br />\r\n<br />\r\nCon My trợn mắt chả n&oacute;i g&igrave; thế l&agrave; t&ocirc;i được đ&agrave; tiến tới.<br />\r\n<br />\r\n- M&agrave;y định ăn m&agrave; &eacute;o cho tụi tao, tiền tụi tao phải đưa cho m&agrave;y hơn nữa m&agrave;y cũng đ&acirc;u c&oacute; l&agrave;m.<br />\r\n<br />\r\nCả đ&aacute;m con g&aacute;i &ugrave;a theo:<br />\r\n<br />\r\n- Th&igrave; ra l&agrave; thế.<br />\r\n<br />\r\nNhờ c&oacute; c&aacute;i miệng u &aacute;m của t&ocirc;i n&ecirc;n My đ&atilde; trở th&agrave;nh t&acirc;m điểm của ng&agrave;y h&ocirc;m ấy. Mỗi người một miệng tạo n&ecirc;n đầy đủ c&aacute;c loại ấm thanh huy&ecirc;n n&aacute;o.<br />\r\n<br />\r\n<br />\r\n- Con My độc &aacute;c gh&ecirc; ha. Gớm, ban vệ sinh m&agrave;. To&agrave;n đi dụ thầy c&ocirc; th&ocirc;i. đ&uacute;ng đ&oacute; đ&uacute;ng đ&oacute;. Tao gh&eacute;t n&oacute; l&acirc;u lắm rồi. Tẩy chay n&oacute; đi tụi m&agrave;y ơi.<br />\r\n<br />\r\n[ &agrave;o &agrave;o ] Như một sạt lở đất d&aacute;ng xuống đầu ch&uacute;ng t&ocirc;i. My đột nhi&ecirc;n đầy &aacute;m kh&iacute;.<br />\r\n<br />\r\nN&oacute; cầm chổi dơ v&agrave;o mặt ch&uacute;ng t&ocirc;i.<br />\r\n<br />\r\n- Đứa n&agrave;o kh&ocirc;ng l&agrave;m =&gt; nhịn<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i im re nh&igrave;n nhau rồi tiếp tục dọn dẹp.<br />\r\n<br />\r\n[ t&ugrave;ng t&ugrave;ng ] Cuối c&ugrave;ng buổi tập thế dục to&agrave;n trường cũng tới. Ch&uacute;ng t&ocirc;i kh&ocirc;ng muốn bị My &eacute;p kh&oacute;a cửa n&ecirc;n nhanh ch&uacute;ng lấy tua rua đi ra ngo&agrave;i.<br />\r\n<br />\r\n- Linh, đợi tao với - T&ocirc;i h&eacute;t to&aacute;ng l&ecirc;n<br />\r\n<br />\r\n- Ừ, nhanh l&ecirc;n. Tao đợi<br />\r\n<br />\r\noạch oạch. Cầu thang dường như sắp sụp vậy. Ch&uacute;ng t&ocirc;i chen lấn x&ocirc; đẩy nhau, c&ograve;n chưa kể bọn con trai lớp t&ocirc;i đ&aacute; b&oacute;ng về cũng đang bối rối chen l&ecirc;n lớp. Người l&ecirc;n kẻ xuống chật chội v&ocirc; c&ugrave;ng nhưng cứ nghĩ đến c&aacute;i cảnh ở lớp đ&oacute;ng cửa tắt điện bị mấy đứa dọa ma th&igrave; ch&uacute;ng t&ocirc;i lại c&agrave;ng c&oacute; quyết t&acirc;m. Kết quả l&agrave; My phải ở trong lớp tắt điện một m&igrave;nh [ h&uacute; hồn ]<br />\r\n<br />\r\nChiều:<br />\r\n<br />\r\n- lớp ch&uacute;ng ta đẹp qu&aacute; - bọn con trai reo l&ecirc;n.<br />\r\n<br />\r\nBọn con g&aacute;i ch&uacute;ng t&ocirc;i xua tay như xua ruồi<br />\r\n<br />\r\n<br />\r\n- Kh&ocirc;ng c&oacute; phần của ch&uacute;ng m&agrave;y đ&acirc;u<br />\r\n<br />\r\n- ơ - tụi n&oacute; h&oacute;a đ&aacute;<br />\r\n<br />\r\nĐột nhi&ecirc;n c&ocirc; chủ nhiệm chạy v&agrave;o.<br />\r\n<br />\r\n- Thứ 7 v&agrave; CN tuẩn n&agrave;y lớp ch&uacute;ng ta đi cắm trại ở đồi th&ocirc;ng nh&eacute;<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i hốt hoảng<br />\r\n<br />\r\n- c&aacute;i g&igrave;?<br />\r\n<br />\r\nCon My &yacute; kiến<br />\r\n<br />\r\n- Tụi em đ&atilde; trang tr&iacute;<br />\r\n<br />\r\n- nhưng.. th&ocirc;i c&aacute;c em cứ ăn hết đống kia đi khỏi phải mời mọc g&igrave; hết. Thứ 7 tập ch&uacute;ng tr&ecirc;n lớp l&uacute;c 7h.<br />\r\n<br />\r\nN&oacute;i rồi c&ocirc; &ocirc;m một t&uacute;i b&aacute;nh kẹo to đ&ugrave;ng vừa đi vừa ăn. Kết quả ch&uacute;ng t&ocirc;i chỉ c&oacute; thể ăn chung 2 g&oacute;i bim bim [ cảm gi&aacute;c như biến th&agrave;nh người v&ocirc; gia cư ]<br />\r\n<br />\r\nBực qu&aacute;, ch&uacute;ng t&ocirc;i thi nhau l&ocirc;i hết bong b&oacute;ng xuống đập tan t&agrave;nh rồi chơi ma [ zui gh&ecirc; ]<br />\r\n<br />\r\nĐột nhi&ecirc;n, c&ocirc; chủ nhiệm quay lại<br />\r\n<br />\r\n- Nguy rồi c&aacute;c em ơi<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i giật m&igrave;nh<br />\r\n<br />\r\nThe end</p>', 1, 'chapter-1-cau-chuyen-1', '2023-01-31 18:15:52', '2023-01-31 18:15:52');
INSERT INTO `chapters` VALUES (4, 'Chapter 2: Câu chuyện 1', 9, 'Chapter số 2 của câu chuyện số 1', '<p>C&Acirc;U CHUYỆN 1 (2)<br />\r\n<br />\r\nCHƯƠNG 2: GẮN KẾT<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i giật m&igrave;nh<br />\r\n<br />\r\n- Nguy g&igrave; c&ocirc;?<br />\r\n<br />\r\nKh&ocirc;ng ch&uacute;t chần chừ c&ocirc; Hiệu h&eacute;t to&aacute;ng l&ecirc;n.<br />\r\n<br />\r\n- Ng&agrave;y mai mỗi lớp sẽ c&oacute; một tiết mục để ch&agrave;o mừng 20-11<br />\r\n<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i h&oacute;a đ&aacute; kh&ocirc;ng biết cười hay kh&oacute;c<br />\r\n<br />\r\nMột b&ecirc;n th&igrave;<br />\r\n<br />\r\n- 5A2 đ&atilde; c&oacute; dịp tỏa s&aacute;ng<br />\r\n<br />\r\nMột b&ecirc;n th&igrave;<br />\r\n<br />\r\n- M&uacute;a b&agrave;i g&igrave; b&acirc;y giờ?<br />\r\n<br />\r\nTrong l&uacute;c cấp b&aacute;ch đ&oacute; -Con Nguyệt - người nổi tiếng lắm chuyện đ&atilde; cho ra một s&aacute;ng kiến thừa thải.<br />\r\n<br />\r\n<br />\r\n- Hay lớp m&igrave;nh giả ốm đi<br />\r\n<br />\r\nCon My chĩa mỏ v&agrave;o.<br />\r\n<br />\r\n- Đi kh&aacute;m m&agrave; kh&ocirc;ng c&oacute; bệnh th&igrave; sao?<br />\r\n<br />\r\nT&ocirc;i c&ograve;n ngu hơn<br />\r\n<br />\r\n- Ăn thật nhiều đ&aacute; l&agrave; ốm ro&agrave;i.<br />\r\n<br />\r\nCả lớp liếc t&ocirc;i bằng một cạp mắt k&igrave; lạ. Con Linh đứng l&ecirc;n ph&aacute;t biểu.<br />\r\n<br />\r\n- Vậy l&agrave; con L&ecirc; [ t&ocirc;i ] sẽ hỗ trợ cho lớp m&igrave;nh v&agrave;i c&acirc;n đ&aacute;.</p>', 1, 'chapter-2-cau-chuyen-1', '2023-01-31 18:17:43', '2023-01-31 18:17:43');
INSERT INTO `chapters` VALUES (5, 'Chapter 3 : Câu chuyện 1', 9, 'Chapter 3 của câu chuyện số 1', '<p>C&acirc;u chuyện 1<br />\r\n<br />\r\nChương 4 - Tao bắt c&aacute; bằng 4 c&aacute;i xoong<br />\r\n<br />\r\nMấy h&ocirc;m sau<br />\r\n<br />\r\ngiờ ra chơi<br />\r\n<br />\r\n- [ bộp ] H&agrave;, sao thế? - t&ocirc;i đập vai n&oacute;<br />\r\n<br />\r\n- Tao bắt c&aacute; 4 tay rồi m&agrave;y ơi.<br />\r\n<br />\r\n- Bắt cả 4 tay? Kể coi, những loại c&aacute; n&agrave;o?<br />\r\n<br />\r\n- Khoa, Hiệp, Thi&ecirc;n,Nguy&ecirc;n<br />\r\n<br />\r\n_______________________________________________________________________________________________________<br />\r\n<br />\r\nCh&uacute; th&iacute;ch:<br />\r\n<br />\r\nKhoa: C&ocirc;ng tử nh&agrave; gi&agrave;u hay khoe khoang rất l&ugrave;n giống Suneo trong doremon<br />\r\n<br />\r\nHiệp: Cổng tử c&acirc;n đất ngừi iu Linh [ chắc c&aacute;i n&igrave; m&iacute; p&aacute;c biết &ograve;i ]<br />\r\n<br />\r\n_______________________________________________________________________________________________________<br />\r\n<br />\r\n<br />\r\n- M&agrave;y m&agrave; cũng love c&ocirc;ng tử ch&acirc;n đất &aacute;? Tao nghe nhầm &agrave;?<br />\r\n<br />\r\n- M&agrave;y......... đ&acirc;u - n&oacute; n&oacute;i l&yacute; nh&iacute;<br />\r\n<br />\r\nT&ocirc;i hỏi lại<br />\r\n<br />\r\n- M&agrave;y n&oacute;i c&aacute;i g&igrave;?<br />\r\n<br />\r\nN&oacute; h&iacute;t một hơi rồi k&ecirc;u to&aacute;ng l&ecirc;n<br />\r\n<br />\r\n- M&agrave;y đ[&eacute;o] nghe nhầm đ&acirc;u<br />\r\n<br />\r\nt&ocirc;i giật m&igrave;nh, đột nhi&ecirc;n t&ocirc;i ph&aacute;t hiện m&igrave;nh v&agrave; H&agrave; đang l&agrave; điểm t&acirc;m nhầm t&acirc;m điểm của cả lớp. Những con mắt hiếu k&igrave; cứ như rực s&aacute;ng l&ecirc;n v&agrave; rồi cho đến khi n&oacute; s&aacute;ng qu&aacute; th&igrave; phải t&igrave;m b&oacute;ng tối che n&oacute; lại.<br />\r\n<br />\r\n- Tụi m&agrave;y khai đi. C&aacute;i giề? - Con Nguyệt trợn mắt<br />\r\n<br />\r\nCả bọn x&uacute;m v&ocirc;<br />\r\n<br />\r\n- C&aacute;i g&igrave; thế, c&agrave;i g&igrave; thế<br />\r\n<br />\r\nCon H&agrave; im re, do k&iacute;ch động n&ecirc;n t&ocirc;i buột miệng<br />\r\n<br />\r\n- H&agrave; bắt c&aacute; 4<br />\r\n<br />\r\n<br />\r\n[ bụp ] con H&agrave; bịt miệng tao lại<br />\r\n<br />\r\n- M&agrave;y phải giữ mồm giữ miệng chứ?<br />\r\n<br />\r\n- ưm.<br />\r\n<br />\r\n- H&agrave; bắt c&aacute; bốn c&aacute;i g&igrave; hả - con Linh lại gần<br />\r\n<br />\r\n- Hả hả hả - cả bọn ng&agrave;y c&agrave;ng gần ch&uacute;ng t&ocirc;i<br />\r\n<br />\r\nT&ocirc;i xua tay<br />\r\n<br />\r\n- Tụi m&agrave;y x&iacute;ch ra tao mới no&aacute;i - tụi h&eacute;t l&ecirc;n<br />\r\n<br />\r\nCả bọn x&iacute;ch ra<br />\r\n<br />\r\n- Thế mới đ&uacute;ng chứ - Tui thở ph&agrave;o<br />\r\n<br />\r\nCon My l&ecirc;n tiếng<br />\r\n<br />\r\n- L&ecirc;, tụi tao đi ra rồi đ&oacute;, no&aacute;i đi<br />\r\n<br />\r\n- &Agrave;, chuyện l&agrave; con h&agrave; bắt c&aacute; bằng 4 c&aacute;i, c&aacute;i - tui lắp bắp<br />\r\n<br />\r\n- c&aacute;i g&igrave;? - Tụi n&oacute; lại gần<br />\r\n<br />\r\n- Bằng c&aacute;i xoong<br />\r\n<br />\r\n- c&aacute;i xoong? - bọn n&oacute; trợn mắt<br />\r\n<br />\r\n- hahahahahahaha - Tụi n&oacute; cười<br />\r\n<br />\r\nV&agrave; cũng nhờ c&aacute;i mồm của t&ocirc;i H&agrave; đ&atilde; được nổi tiếng</p>', 1, 'chapter-3-cau-chuyen-1', '2023-01-31 18:18:59', '2023-01-31 18:18:59');
INSERT INTO `chapters` VALUES (6, 'Chapter 4 : Câu chuyện 1', 9, 'Chapter số 4 của câu chuyện số 1', '<p>&Ograve; &Oacute; O O........<br />\r\n<br />\r\nV&agrave;i h&ocirc;m sau<br />\r\n<br />\r\n- M&agrave;y biết g&igrave; chưa?<br />\r\n<br />\r\n- Chưa..<br />\r\n<br />\r\n- Con H&agrave; đang bắt c&aacute; bằng 4 c&aacute;i xoong đấy<br />\r\n<br />\r\n- M&agrave;y đi&ecirc;n &agrave;?<br />\r\n<br />\r\n- M&agrave;y đi&ecirc;n th&igrave; c&oacute;, h&ocirc;m qua tao đi ngang qua lớp 5a2 th&igrave; nghe được mấy chị b&agrave;n t&aacute;n đ&oacute;. Ch&iacute;nh chị H&agrave; n&oacute;i cơ m&agrave;.<br />\r\n<br />\r\n- Vậy &agrave;? Để tao đi kể cho mấy đưa nghe<br />\r\n<br />\r\n- Mấy đưa kia ơi, m&agrave;y biết tin g&igrave; chưa?<br />\r\n<br />\r\n- rồi rồi, c&oacute; phải si&ecirc;u thị vừa giảm gi&aacute; v&agrave;i mặt h&agrave;ng phải kh&ocirc;ng?<br />\r\n<br />\r\n- Kh&ocirc;ng, chị H&agrave; bắt c&aacute; bằng.....<br />\r\n<br />\r\nCả bọn đồng thanh<br />\r\n<br />\r\n- 4 c&aacute;i xoong.<br />\r\n<br />\r\n- ủa? sao m&agrave;y biết hay zợ?<br />\r\n<br />\r\n-Th&igrave; tao cập nhật tin tức sớm chứ sao. Với lại chuyện n&agrave;y ai chả biết.<br />\r\n<br />\r\n- ỪM ừm<br />\r\n<br />\r\n<br />\r\n- C&ocirc;ng nhận chị H&agrave; kh&ugrave;ng gh&ecirc; ha<br />\r\n<br />\r\n- hahahahahahaha<br />\r\n<br />\r\nTr&ecirc;n đ&acirc;y l&agrave; một số ph&aacute;t biểu cũng như &yacute; kiến tiểu biểu nhất của con g&aacute;i v&agrave; dưới đ&acirc;y l&agrave; của con trai<br />\r\n<br />\r\n- M&agrave;y ơi, mụ H&agrave; đi&ecirc;n nhể?<br />\r\n<br />\r\n- Ừ, bắt c&aacute; bằng lưới th&igrave; tao nghe rồi nhưng bắt c&aacute; bằng xoong th&igrave; tao chưa nghe qua<br />\r\n<br />\r\n- th&igrave; đ&oacute;, con H&agrave; n&oacute; bị m&aacute;t m&agrave;.<br />\r\n<br />\r\n- Mấy anh n&oacute;i g&igrave; vậy.<br />\r\n<br />\r\n- em ghe tin chị H&agrave; lớp 5a2 chưa?<br />\r\n<br />\r\n- Chị H&agrave; b&ecirc;n cạnh lớp 5A3 tụi anh ấy hả?<br />\r\n<br />\r\n- ừ<br />\r\n<br />\r\n- em nghe rồi<br />\r\n<br />\r\n- Vậy thử kế coi<br />\r\n<br />\r\n- Ừm, chuyện bắt c&aacute; của chị H&agrave; phải h&ocirc;ng anh?<br />\r\n<br />\r\n- ừ.<br />\r\n<br />\r\n- c&ocirc;ng nhận chỉ đi&ecirc;n dễ sợ<br />\r\n<br />\r\ncả bọn &ugrave;a l&ecirc;n<br />\r\n<br />\r\n- Đ&uacute;ng đ&oacute; đ&uacute;ng đ&oacute;<br />\r\n<br />\r\nv&agrave; đương nhi&ecirc;n sớm muộn th&igrave; c&aacute;i chuyện n&agrave;y cũng đến tai H&agrave;. Từ h&ocirc;m được nổi tiếng đến giờ T&ocirc;i v&agrave; Linh lu&ocirc;n b&aacute;m s&aacute;t theo con H&agrave; thứ nhất l&agrave; để ăn b&aacute;m, thứ hai l&agrave; để c&oacute; được nhiều sự ch&uacute; &yacute;. Đơn giản v&igrave; ch&uacute;ng t&ocirc;i FA m&agrave;....<br />\r\n<br />\r\n- Tụi m&agrave;y cứ đi theo tao ho&agrave;i zậy - con Ha nổi đ&oacute;a<br />\r\n<br />\r\n- Th&iacute;ch th&igrave; đi th&ocirc;i. - ch&uacute;ng t&ocirc;i chọc giận n&oacute;<br />\r\n<br />\r\n- C&uacute;t gi&ugrave;m, đường ai nấy đi - n&oacute; n&oacute;i<br />\r\n<br />\r\n- vậy c&aacute;i đường n&agrave;y l&agrave; do m&agrave;y x&acirc;y hả - Con Linh l&ecirc;n tiếng<br />\r\n<br />\r\nT&ocirc;i th&ecirc;m mắm muối<br />\r\n<br />\r\n- Đ&uacute;ng đ&oacute;, chỉ l&agrave; tr&ugrave;ng hợp th&ocirc;i chứ tụi tao đ[&eacute;o ]thi theo m&agrave;y l&agrave;m g&igrave; cả<br />\r\n<br />\r\nN&oacute; im lặng tiếp tục đi tiếp. Ch&uacute;ng t&ocirc;i vẫn lặng lẽ b&aacute;m s&aacute;t theo<br />\r\n<br />\r\n- Tụi m&agrave;y l&agrave; bitch hả? Hay sao m&agrave; dai vậy.<br />\r\n<br />\r\n- Bitch l&agrave;m sao dai bằng đỉa - Linh n&oacute;i<br />\r\n<br />\r\n<br />\r\nT&ocirc;i tiếp lời<br />\r\n<br />\r\n- Đ&uacute;ng đ&oacute;<br />\r\n<br />\r\nN&oacute;i rồi t&ocirc;i n&oacute;i nhỏ v&agrave;o tai con H&agrave;<br />\r\n<br />\r\n- Tao c&ograve;n chưa kịp c&ocirc;ng bố chuyện t&igrave;nh đẹp như mơ của m&agrave;y với Nguy&ecirc;n đ&acirc;u nh&eacute;<br />\r\n<br />\r\nN&oacute; xanh mặt rồi cười kiểu như miễn cưỡng<br />\r\n<br />\r\n- Tao mời tụi m&agrave;y đi ăn một trầu nha<br />\r\n<br />\r\nđến nơi, con H&agrave; lấy menu ra rồi dạng ch&acirc;n dạng tay như mấy &ocirc;ng tỷ ph&uacute;<br />\r\n<br />\r\n- Muốn ăn g&igrave;... gọi<br />\r\n<br />\r\n- Tao muốn 2 tr&agrave; sữa ; 2 su-kem ; 3 b&aacute;nh tr&aacute;ng trộn...ờm... 6 c&acirc;y vi&ecirc;n chi&ecirc;n - t&ocirc;i lấy menu từ tay con H&agrave; rồi gọi<br />\r\n<br />\r\n- Đợi x&iacute;u tao đi mua - con Ha lững thững đi<br />\r\n<br />\r\nĐến khi con H&agrave; đi v&agrave;o biển người, con Linh chạy lại hỏi.<br />\r\n<br />\r\n- Vừa n&atilde;y m&agrave;y n&oacute;i g&igrave; l&agrave;m con H&agrave; xanh mặt thế?<br />\r\n<br />\r\n- Kh&ocirc;ng n&oacute;i<br />\r\n<br />\r\n- n&oacute;i đi m&agrave;<br />\r\n<br />\r\nN&oacute; n&agrave;i nỉ cả chục c&acirc;u, t&ocirc;i đi&ecirc;n qu&aacute; h&eacute;t to<br />\r\n<br />\r\n- ăn đi rồi tao n&oacute;i<br />\r\n<br />\r\ncả đ&aacute;m người nh&igrave;n t&ocirc;i bằng một &aacute;nh mắt đ[&eacute;o] b&igrave;nh thường<br />\r\n<br />\r\n[ Chuyển cảnh ] c&ugrave;ng l&uacute;c đ&oacute;<br />\r\n<br />\r\n- Ch&uacute; ơi cho ch&aacute;u...... - con H&agrave; chưa n&oacute;i xong th&igrave;<br />\r\n<br />\r\n<br />\r\n- Cho ch&uacute; hỏi x&iacute;u ha, ch&aacute;u bắt c&aacute; bằng xoong như thế n&agrave;o zậy? Chỉ ch&uacute; đi<br />\r\n<br />\r\nMấy người xung quanh &ugrave;a theo<br />\r\n<br />\r\n- đ&uacute;ng đ&oacute; đ&uacute;ng đ&oacute;<br />\r\n<br />\r\n- Ch&aacute;u..... - con H&agrave; lắm bắp<br />\r\n<br />\r\n- Ch&aacute;u sao cer? - Mấy ch&uacute; xung quanh cũng lại gần H&agrave; hỏi?<br />\r\n<br />\r\nN&oacute; chảy hết mồ h&ocirc;i l&ugrave;i l&ugrave;i rồi chạy thật nhanh về ph&iacute;a ch&uacute;ng t&ocirc;i đưa ch&uacute;ng t&ocirc;i mỗi đứa 10k n&oacute;i nhỏ:<br />\r\n<br />\r\n- Tao đ[&eacute;o] rảnh để hầu tụi m&agrave;y, bye nh&eacute;<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i h&oacute;a đ&aacute;<br />\r\n<br />\r\nChiều<br />\r\n<br />\r\n- Nh&ocirc; H&agrave; - t&ocirc;i cười<br />\r\n<br />\r\nN&oacute; quay lại mỉm cười với t&ocirc;i<br />\r\n<br />\r\n- Căm ơn, nhờ m&agrave;y tao mới được nổi tiếng<br />\r\n<br />\r\nN&oacute;i rồi n&oacute; lại cho t&ocirc;i 10k rồi thở d&agrave;i<br />\r\n<br />\r\n- vĩnh biệt<br />\r\n<br />\r\nT&ocirc;i ch&ugrave;i kh&ocirc; nước miếng nghĩ thầm<br />\r\n<br />\r\n- Lẽ n&agrave;o n&oacute; vẫn c&ograve;n muốn nổi tiếng hơn????? Ok, mai m&igrave;nh sẽ cho n&oacute; nổi tiếng nhất quả đất lu&ocirc;n.</p>', 1, 'chapter-4-cau-chuyen-1', '2023-01-31 18:20:17', '2023-01-31 18:20:17');
INSERT INTO `chapters` VALUES (7, 'Chương 1: Chiếc hài thơm', 2, 'Chapter 1 thuộc phần 2 truyện', '<p>Một dạo sau khi về đến Bắc Kinh, t&ocirc;i kh&ocirc;ng gặp Shirley Dương đ&acirc;u cả, c&oacute; lẽ c&ocirc; n&agrave;ng bận t&igrave;m b&aacute;c sĩ chữa trị cho gi&aacute;o sư Trần, hoặc phải lo liệu chuyện h&acirc;u sự cho những người gặp nạn. Chuyến đi n&agrave;y đội khảo cổ đ&atilde; chết mất mấy người. c&aacute;c cơ quan hữu quan đương nhi&ecirc;n phải điều tra, t&ocirc;i sợ bị người ta điều tra ra m&igrave;nh l&agrave; M&ocirc; Kim Hiệu &uacute;y, b&egrave;n cố hết sức n&eacute; nặng t&igrave;m nhẹ, to&agrave;n n&oacute;i chuyện loanh quanh ngo&agrave;i lề. Bản th&acirc;n việc tiến v&agrave;o sa mạc l&agrave;m c&ocirc;ng t&aacute;c khảo cổ đ&atilde; c&oacute; hệ số nguy hiểm rất cao rồi, nhưng một lượt chết đến bốn người, một thầy gi&aacute;o ba học sinh, lại c&ograve;n th&ecirc;m một &ocirc;ng gi&aacute;o sư ph&aacute;t rồ ph&aacute;t dại, v&agrave;o thời điểm ấy cũng được xem l&agrave; một sự kiện lớn.</p>\r\n\r\n<p>Chuyện qua rồi th&ocirc;i khỏi kể rườm r&agrave;. C&oacute; một h&ocirc;m Tuyền b&eacute;o kiếm đ&acirc;u được hai em xinh xinh đưa đi nhảy đầm, rủ t&ocirc;i đi chung, mấy h&ocirc;m trước đấy t&ocirc;i to&agrave;n bị &aacute;c mộng tra tấn cả đ&ecirc;m, đầu đau như b&uacute;a bổ, n&ecirc;n chẳng đi với cậu ta, m&agrave; nằm bẹp một m&igrave;nh tr&ecirc;n giường, bỗng nhi&ecirc;n c&oacute; tiếng g&otilde; cửa, t&ocirc;i đ&aacute;p một tiếng rồi b&ograve; ra khỏi giường, bụng thầm chửi rủa, con b&agrave; n&oacute;, chắc l&agrave; lại c&oacute; đứa n&agrave;o đến tra hỏi đ&acirc;y.</p>\r\n\r\n<p>Mở cửa mới biết, h&oacute;a ra l&agrave; c&ocirc; n&agrave;ng Shirley Dương l&acirc;u ng&agrave;y kh&ocirc;ng gặp, t&ocirc;i vội mời c&ocirc; v&agrave;o nh&agrave;,t&igrave;m được đến đ&acirc;y, Shirley Dương bảo Răng V&agrave;ng cho c&ocirc; địa chỉ.</p>\r\n\r\n<p>T&ocirc;i ngạc nhi&ecirc;n hỏi: &quot;C&ocirc; quen Răng V&agrave;ng &agrave;?&quot;</p>\r\n\r\n<p>Shirley Dương n&oacute;i: &quot; Cũng coi như quen biết sơ sơ, nhưng kh&ocirc;ng th&acirc;n thiết lắmCha t&ocirc;i hồi trước rất th&iacute;ch sưu tập đồ cổ, c&oacute; l&agrave;m ăn với hắn v&agrave;i vụ, m&agrave; hắn ta v&agrave; gi&aacute;o sư Trần cũng l&agrave; chỗ quen biết nữa. H&ocirc;m nay t&ocirc;i đến t&igrave;m anh l&agrave; để thanh to&aacute;n tiền c&ocirc;ng của anh v&agrave; anh Tuyền, độ hai h&ocirc;m nữa t&ocirc;i sẽ đưa gi&aacute;o sư Trần ra nước ngo&agrave;i chữa bệnh, dạo n&agrave;y t&ocirc;i c&ograve;n phải t&igrave;m hiểu một v&agrave;i việc, tạm thời ch&uacute;ng ta kh&ocirc;ng gặp nhau được nữa rồi!&quot;</p>\r\n\r\n<p>T&ocirc;i vốn chẳng c&ograve;n tr&ocirc;ng chờ g&igrave;, lại nghe c&ocirc; n&agrave;ng bảo trả tiền c&ocirc;ng, quả l&agrave; một niềm vui bất ngờ, song bề ngo&agrave;i t&ocirc;i vẫn l&agrave;m bộ kh&aacute;ch kh&iacute; n&oacute;i: &quot; Về nước &agrave;?Bệnh t&igrave;nh của gi&aacute;o sư Trần c&oacute; đỡ ch&uacute;t n&agrave;o kh&ocirc;ng? T&ocirc;i cũng đang định nay mai đi thăm đ&acirc;y. C&ocirc; xem giờ n&agrave;y c&ograve;n nhắc đến chuyện tiền nong, thật chẳng th&iacute;ch hợp ch&uacute;t n&agrave;o. Ch&uacute;ng t&ocirc;i cũng c&oacute; gi&uacute;p được g&igrave; mấy đ&acirc;u, to&agrave;n l&agrave; g&acirc;y th&ecirc;m phiền phức, người Mỹ c&aacute;c c&ocirc; cũng đ&acirc;u phải gi&agrave;u c&oacute; lắm m&agrave;, thật ngại qu&aacute; thế thanh to&aacute;n bằng tiền mặt chứ hả?&quot;</p>\r\n\r\n<p>Shirley đặt tiền l&ecirc;n b&agrave;n : &quot; tiền th&igrave; phải trả rồi, ch&uacute;ng ta đ&atilde; n&oacute;i từ trước rồi m&agrave;, thế nhưng... t&ocirc;i hy vọng anh hứa một chuyện&quot;</p>\r\n\r\n<p>T&ocirc;i nghĩ bụng kh&ocirc;ng hay rồi, e rằng c&ocirc; ả muốn trả th&ugrave; m&igrave;nh, hay lại muốn bới m&oacute;c g&igrave; đ&acirc;y, trong l&ograve;ng thầm nghĩ c&aacute;ch đối ph&oacute;, b&egrave;n thuận miệng đ&aacute;p lấy lệ: &quot;C&ocirc; th&igrave; c&oacute; việc g&igrave; cần nhờ đến t&ocirc;i chứ? Xem ra người nh&agrave; gi&agrave;u cũngc so lắm sự phiền to&aacute;i nhỉ, chắc kh&ocirc;ng phải nhờ t&ocirc;i ti&ecirc;u tiền gi&uacute;p đấy chứ!&quot;</p>\r\n\r\n<p>Shirley n&oacute;i: &quot; &Ocirc;ng cụ nh&agrave; anh v&agrave; nh&agrave; t&ocirc;i cũng coi như l&agrave; đồng nghiệp của nhau rồi. Ng&agrave;y trước &ocirc;ng ngoại t&ocirc;i rửa tay g&aacute;c kiếm, kh&ocirc;ng theo nghiệp đổ đấu nữa cũng l&agrave; v&igrave; c&aacute;i nghề M&ocirc; Kim Hiệu &yacute; n&agrave;y n&oacute; tổn &acirc;m đức qu&aacute;, kẻ cao số đến mấy cũng kh&oacute; tr&aacute;nh khỏi việc ngo&agrave;i &yacute; muốn. T&ocirc;i hi vọng t&ugrave; nay về sau anh cũng dừng ở đ&acirc;y th&ocirc;i, đừng đi đổ đấu nữa, mai n&agrave;y c&oacute; cơ hội c&aacute;c anh c&oacute; thể sang Mỹ, t&ocirc;i sẽ sắp xếp cho c&aacute;c anh... &quot;</p>\r\n\r\n<p>Nghe đến đ&acirc;y t&ocirc;i đ&atilde; thấy trong bụng kh&ocirc;ng được thoải m&aacute;i, c&ocirc; ả người MỸ n&agrave;y định dụ t&ocirc;i về đầu qu&acirc;n dưới trướng, sau n&agrave;y b&ocirc;n ba c&ugrave;ng c&ocirc; ả đ&acirc;y, tốt xấu g&igrave; Hồ B&aacute;t Nhất n&agrave;y cũng từng l&agrave; đại đội trưởng, n&uacute;p b&oacute;ng kẻ kh&aacute;c th&igrave; l&agrave;m sao kh&aacute; được, huống hồ lại cầu cạnh đ&agrave;n b&agrave;, vậy sau n&agrave;y chẳng phải việc g&igrave; cũng phải nhất nhất theo &yacute; ả hay sao, l&agrave;m người như thế c&ograve;n c&oacute; &yacute; nghĩa g&igrave; cơ chứ, thế l&agrave; t&ocirc;i liền ngắt lời c&ocirc; n&agrave;ng lu&ocirc;n: &quot; &Yacute; tốt của c&ocirc; t&ocirc;i xin nghi nhận. Nhưng c&ocirc; chỉ biết một m&agrave; kh&ocirc;ng biết hai. C&aacute;i nghề M&ocirc; kim HIệu &uacute;y đ&uacute;ng l&agrave; kh&ocirc;ng hay ho g&igrave; lắm, song Mao chủ tịch đ&atilde; dạy ch&uacute;ng ta rằng, sự vật n&agrave;o cũng đều c&oacute; hai mặt, việc tốt cũng c&oacute; thể trở th&agrave;nh xấu, việc xấu cũng c&oacute; thể biến th&agrave;nh tốt. đ&acirc;y gọi l&agrave; chủ nghĩa duy vật biện chứng. C&ocirc; đ&atilde; biết t&ocirc;i l&agrave; kẻ theo nghề đổ đấu, vậy c&oacute; v&agrave;i việc t&ocirc;i cũng chẳng giấu c&ocirc; l&agrave;m g&igrave;. T&ocirc;i l&agrave; người c&oacute; nguy&ecirc;n tắc v&agrave; lập trường, với những ng&ocirc;i mộ cổ đ&atilde; được đưa v&agrave;o danh s&aacute;ch bảo tồn v&agrave; được khai quật l&ecirc;n, t&ocirc;i tuyệt đối kh&ocirc;ng động đến. C&ograve;n những di t&iacute;ch hay ng&ocirc;i mộ cổ chưa bị ph&aacute;t hiện nằm s&acirc;u trong chốn rừng s&acirc;u n&uacute;i thẳm, b&ecirc;n trong v&ocirc; số b&aacute;u vật, th&igrave; chỉ c&oacute; người biết b&iacute; thuật phong thủy mới t&igrave;m được, nếu như kh&ocirc;ng đi đổ đấu những c&aacute;i đấu n&agrave;y, c&oacute; lẽ ch&uacute;ng sẽ m&atilde;i m&atilde;i ngủ s&acirc;u trong l&ograve;ng đất, vĩnh viễn kh&ocirc;ng c&oacute; cơ hội được ra dưới &aacute;nh mặt trời nữa đ&acirc;u. Ngo&agrave;i ra m&ocirc;i trường tự nhi&ecirc;n biến đổi x&acirc;m thực cũng l&agrave; một mối đe dọa lớn với những ng&ocirc;i mộ kh&ocirc;ng người hay biết n&agrave;y, t&ocirc;i tr&ocirc;ng vậy m&agrave; đau đớn l&ograve;ng... &quot;</p>\r\n\r\n<p>Shirley Dương thấy t&ocirc;i n&oacute;i năng h&ugrave;ng hồn, đ&agrave;nh gượng gạo đ&aacute;p: &quot; Được rồi, t&ocirc;i c&oacute; &yacute; tốt khuy&ecirc;n anh quay đầu l&agrave; bờ, ngờ đ&acirc;u anh lại c&ograve;n lắm l&yacute; lẽ thế. Đ&agrave;đ&agrave;o trộm mộ m&agrave; c&ograve;n l&yacute; sự, e l&agrave; tr&ecirc;n thế giới n&agrave;y kh&ocirc;ng c&oacute; kẻ thứ hai giỏi ngụy biện như anh đ&acirc;u. Anh c&oacute; kh&iacute; ph&aacute;ch như vậy, xem ra t&ocirc;i thực sự phải nh&igrave;n anh bằng con mắt kh&aacute;c rồi, những lời ban n&atilde;y coi như t&ocirc;i chưa n&oacute;i, khoản tiền n&agrave;y xem chừng anh cũng chẳng chịu nhận... &quot;</p>\r\n\r\n<p>T&ocirc;i cuống qu&yacute;t đưa tay l&ecirc;n chặn c&aacute;i t&uacute;i giấy đựng tiền: &quot; Khoan đ&atilde;, khoản tiền n&agrave;y coi như c&ocirc; cho t&ocirc;i vay vậy... cứ t&iacute;nh l&atilde;i theo l&atilde;i xuất của ng&acirc;n h&agrave;ng Nh&acirc;n d&acirc;n Trung Quốc đi&quot;</p>\r\n\r\n<p>Tối h&ocirc;m đ&oacute;, Tuyền b&eacute;o ngồi đếm từng tờ tiền một dưới &aacute;nh đ&egrave;n, đếm đi đếm lại, nhưng m&atilde;i vẫn chưa đếm xong, cũng chẳng thể tr&aacute;ch cậu ta được, lần đầu nh&igrave;n thấy một đống tiền to như thế n&agrave;y t&ocirc;i cũng thấy ch&oacute;ng cả mặt.</p>\r\n\r\n<p>Cuối c&ugrave;ng Tuyền b&eacute;o ngưng tay kh&ocirc;ng đếm nữa, cậu ta ch&acirc;m một điếu thuốc rồi vừa h&uacute;t vừa n&oacute;i: &quot; Cậu bảo t&ocirc;i phải n&oacute;i cậu thế n&agrave;o b&acirc;y giờ đ&acirc;y hả Hồ B&aacute;t Nhất, đ&uacute;ng l&agrave; kh&ocirc;n ba năm dại một giờ, sao cậu lại c&oacute; thể n&oacute;i đ&acirc;y l&agrave; tiền vay được cơ chứ? Thế đ&atilde; đ&agrave;nh, lại c&ograve;n trả l&atilde;i cho ả người Mỹ đấy nữa, t&ocirc;i nom chẳng bằng ch&uacute;ng ta chuồn th&ocirc;i, l&aacute;nh về miền Nam qu&ecirc; hương, để c&ocirc; ả kh&ocirc;ng bao giờ t&igrave;m được, tha hồ tức tối&quot;</p>\r\n\r\n<p>T&ocirc;i n&oacute;i: &quot; CẬu đ&uacute;ng l&agrave; chẳng kh&aacute; l&ecirc;n được, ch&uacute;t tiền mọn n&agrave;y th&igrave; thấm v&agrave;o đ&acirc;u, sắp tới t&ocirc;i đưa cậu đi đ&agrave;o mấy m&oacute;n h&agrave;ng ngon đổi bừa đối b&atilde;i, cũng đủ tiền trả c&ocirc; ta rồi. Thứ ch&uacute;ng ta thiếu hiện giờ ch&iacute;nh l&agrave; ch&uacute;t vốn liếng n&agrave;y đ&acirc;y, c&oacute; tiền rồi th&igrave; kh&ocirc;ng phải lo cơm từng bữa, c&oacute; kinh ph&iacute; rồi mới c&oacute; thể sắm sửa trang bị tốt. Từ giờ trở đi, hai ch&uacute;ng ta sẽ sửa sang lại quang g&aacute;nh, chuẩn bị kỹ lưỡng, t&ocirc;i nhất định phải đổ một c&aacute;i đấu thật lớn&quot;</p>\r\n\r\n<p>Hai ch&uacute;ng t&ocirc;i t&iacute;nh to&aacute;n, những ng&ocirc;i mộ cổ nằm s&acirc;u trong rừng thi&ecirc;ng nước độc kh&ocirc;ng phải dễ t&igrave;m, lại kh&ocirc;ng biết chắc khi n&agrave;o mới t&igrave;m ra được, chỗ tiền n&agrave;y tuy nhiều đấy, song cũng e miệng ăn n&uacute;i lở.</p>\r\n\r\n<p>Tuyền b&eacute;o l&agrave; người tương đối thực tế, cậu ta cho rằng kiểu bu&ocirc;n b&aacute;n của Răng V&agrave;ng cũng kh&ocirc;ng tồi, bu&ocirc;n b&aacute;n cổ vật đ&iacute;ch thị l&agrave; một ng&agrave;nh si&ecirc;u lợi nhuận, đặc biệt l&agrave; b&aacute;n cho mấy tay ngoại quốc, c&oacute; điều đ&aacute;m ngoại quốc hay tới Trung Quốc giờ cũng tinh ranh hơn nhiều, kh&ocirc;ng dễ g&igrave; lừa nổi họ, nhưng chỉ cần c&oacute; h&agrave;ng xịn, th&igrave; cũng kh&ocirc;ng lo họ kh&ocirc;ng dốc t&uacute;i ra mua.</p>\r\n\r\n<p>Tuyền b&eacute;o đề nghị: &quot; Nhất n&agrave;y, cậu bảo bọn m&igrave;nh đầu từ mở lấy một cửa h&agrave;ng th&igrave; sao nhỉ? Thu mua cổ vật minh kh&iacute; đem trưng b&aacute;n, l&agrave;m ăn được c&oacute; khi chẳng cần đi đổ đấu nữa, đổ đấu tuy kiếm tiền nhanh thật, nhưng mẹ cha n&oacute;, thật kh&ocirc;ng dễ l&agrave;m đ&acirc;u&quot;</p>\r\n\r\n<p>T&ocirc;i gật đầu: &quot; &Yacute; kiến n&agrave;y kh&ocirc;ng tồi đ&acirc;u. Đầu &oacute;c thằng b&eacute;o cậu cũng nhanh nhạy ra phết đấy. Giờ ta vốn liếng h&ograve;m h&ograve;m rồi, c&oacute; thể bắt đầu từ kinh doanh nhỏ, nh&acirc;n tiện học th&ecirc;m một ch&uacute;t kiến thức gi&aacute;m định đồ cổ&quot;</p>\r\n\r\n<p>Thế l&agrave; ch&uacute;ng t&ocirc;i đi khắp nơi t&igrave;m địa điểm mở của hiệu, nhưng m&atilde;i ko t&igrave;m được,sau đ&oacute; nghĩ lại thấy cũng chưa cần mở tiệm ngay, trước mắt cứ t&igrave;m &iacute;t h&agrave;ng rồi b&agrave;y sạp b&aacute;n trong Phan Gia Vi&ecirc;n cũng được.</p>\r\n\r\n<p>Đặc điểm của PGV ch&iacute;nh l&agrave; hỗn tạp,h&agrave;ng h&oacute;a Đ&ocirc;ng T&acirc;y kim cổ, to nhỏ thứ g&igrave; cũng c&oacute;, nhưng c&aacute;c m&oacute;n minh kh&iacute; thật qu&yacute; gi&aacute; th&igrave; &iacute;t khi gặp được,c&aacute;c m&oacute;n ấy thường được giao dịch ngầm, rất hiếm khi đem b&agrave;y ngo&agrave;i chợ.</p>\r\n\r\n<p>Mới bắt đầu, ch&uacute;ng t&ocirc;i nghe Răng V&agrave;ng m&aacute;ch nước, ra v&ugrave;ng ngoại &ocirc;, thu mua một &iacute;t đồ lặt vặt như chậu b&aacute;t b&igrave;nh cổ thời tiền Thanh, tiền cổ, b&igrave;nh đựng thuốc h&iacute;t, đồng hồ cũ hỏng... mang về b&aacute;n trong chợ đồ cổ.</p>\r\n\r\n<p>C&oacute; vẻ số t&ocirc;i ko phải l&agrave; số bu&ocirc;n b&aacute;n, nh&igrave;n h&agrave;ng ko chuẩn, l&uacute;c thu mua to&agrave;n coi những m&oacute;n đồ v&ocirc; gi&aacute; trị th&agrave;nh bảo bối, mua được mấy m&oacute;n đ&aacute;ng tiền một ch&uacute;t th&igrave; lại b&aacute;n gi&aacute; rẻ như h&agrave;ng phổ th&ocirc;ng, cứ như vậy chẳng kiếm được đồng n&agrave;o, thậm ch&iacute; c&ograve;n phải b&ugrave; lỗ ko &iacute;t.</p>\r\n\r\n<p>Cũng may thu mua những thứ lặt vặt ấy ko ti&ecirc;u tốn nhiều tiền lắm, lỗ một &iacute;t cũng chẳng đ&aacute;ng mấy, chủ yếu l&agrave; để luyện mắt nh&igrave;n h&agrave;ng, học th&ecirc;m kiến thức.Lăn lộn một thời gian ở Phan Gia Vi&ecirc;n, mới biết c&aacute;i ng&agrave;nh n&agrave;y s&acirc;u rộng đ&eacute;n nhường n&agrave;o, thậm ch&iacute; c&ograve;n phức tạp hơn cả phong thủy học, ko phải 1 sớm 1 chiều l&agrave; c&oacute; thể nắm bắt được.</p>\r\n\r\n<p>C&oacute; một h&ocirc;m, sắp tới trưa,khu chợ cổ c&oacute; ch&uacute;t vắng vẻ,chẳng mấy người qua lại, t&ocirc;i n&oacute;i với Tuyền b&eacute;o v&agrave; Răng V&agrave;ng quay lại một chỗ đ&aacute;nh b&agrave;i v&aacute;n b&agrave;i đang tới h&ograve;i quyết liệt, bỗng đằng trước c&oacute; một người đi tới, cứ đứng trước sạp h&agrave;ng cảu ch&uacute;ng t&ocirc;i đi đi lại lại.Tuyền b&eacute;o tưởng l&agrave; kh&aacute;ch đến xem h&agrave;ng, b&egrave;n hỏi: &quot;Thế n&agrave;o ,qu&yacute; b&aacute;c muốn xem mặt h&agrave;ng g&igrave; ạ?&quot;</p>\r\n\r\n<p>Người kh&aacute;ch ấp &uacute;ng: &quot;Chẳng xem g&igrave; cả, anh mua đồ cổ ko?&quot;</p>\r\n\r\n<p>T&ocirc;i ngước mắt thăm d&ograve;, xem chừng người đ&agrave;n &ocirc;ng n&agrave;y độ 36,37 g&igrave; đ&oacute;, nước da đỏ t&iacute;a, nh&igrave;n l&agrave; đo&agrave;n ngay được người n&agrave;y thưởng xuy&ecirc;n phải l&agrave;m viec nh&agrave; n&ocirc;ng ngo&agrave;i trời, g&atilde; ta ăn mặc qu&ecirc; m&ugrave;a,tay s&aacute;ch một c&aacute;i t&uacute;i da cũ r&iacute;ch, giọng n&oacute;i đặc &acirc;m điệu v&ugrave;ng cao nguy&ecirc;n hoang thổ.</p>\r\n\r\n<p>t&ocirc;i nghĩ bụng thằng cha n&agrave;y liệu c&oacute; được đồ cổ g&igrave;, đoạn đ&aacute;nh mắt sang ph&iacute;a Răng V&agrave;ng.Răng V&agrave;ng l&agrave; tay s&agrave;nh sỏi,mặc d&ugrave; ng&atilde; nh&agrave; qu&ecirc; ,tướng mạo th&ocirc; kệch qu&ecirc; m&ugrave;a hết chỗ n&oacute;i, nhưng hắn ko thể coi thường, liền nh&aacute;y mắt với t&ocirc;i ngầm bảo cứ giữ hắn lại, hỏi cho ra nhẽ rồi t&iacute;nh.</p>\r\n\r\n<p>T&ocirc;i r&uacute;t bao thuốc đưa cho g&atilde; nh&agrave; qu&ecirc; một điếu, ch&acirc;m lửa rồi mời g&atilde; ngồi xuống n&oacute;i chuyện.</p>\r\n\r\n<p>G&atilde; nh&agrave; qu&ecirc; r&otilde; r&agrave;ng chưa va chạm nhiều, cũng ko kh&eacute;o việc th&ugrave; tiếp cho lắm,cứ kh&eacute;p n&eacute;p ngồi tr&ecirc;n chiếc ghế gấp t&ocirc;i đưa, tay &ocirc;m khư khư c&aacute;i t&uacute;i da, ko n&oacute;i một lời n&agrave;o.</p>\r\n\r\n<p>T&ocirc;i liếc mắt nh&igrave;n c&aacute;i t&uacute;i da n&aacute;t, thầm nghĩ cha n&agrave;y chắc ko phỉa d&acirc;n đổ đấu đấy chứ, tr&ocirc;ng như vừa l&agrave;m việc g&igrave; tr&aacute;i v&oacute;i lương t&acirc;m th&igrave; phải,hay trong t&uacute;i kia c&oacute; m&oacute;n g&igrave; đ&aacute;ng tiền lắm?t&ocirc;i gắng sức l&agrave;m ra vẻ từ tốn hỏi: &quot;N&agrave;y &ocirc;ng anh, n&agrave;o , kh&aacute;ch s&aacute;o l&agrave;m g&igrave;, l&agrave;m điếu thuốc đi, thuốc V&acirc;n Y&ecirc;n đấy.Xưng h&ocirc; với &ocirc;ng anh thế n&agrave;o nhỉ?&quot;</p>\r\n\r\n<p>g&atilde; nh&agrave; qu&ecirc; trả lời: &quot;t&ecirc;n l&agrave; L&yacute; Xu&acirc;n Lai&quot;C&oacute; vẻ như ko quen ngồi ghế gấp, g&atilde; đẩy ghế ra ngồi xổm tr&ecirc;n đất, nh&igrave;n điệu bộ hắn ngồi xổm tr&ocirc;ng thoải m&aacute;i hơn nhiều, động t&aacute;c h&uacute;t thuốc cũng hoạt b&aacute;t hẳn l&ecirc;n.</p>\r\n\r\n<p>Răng V&agrave;ng v&agrave; tuyền b&eacute;o vẵn giả như đang chơi b&agrave;i.C&aacute;i ngh&agrave;nh n&agrave;y n&oacute; thế, l&uacute;c b&agrave;n chuyện ko được c&oacute; nhiều người, thứ nhất đ&acirc;y l&agrave; quy định chung, thứ nữa cũng l&agrave; e người chủ sợ chạy mất, thường th&igrave; những người mang đồ cổ đi b&aacute;n đều hơi lo lắng, sợ bị người kh&aacute;c theo d&otilde;i cướp mất.</p>\r\n\r\n<p>t&ocirc;i vừa h&uacute;t điếu thuốc vừa cười hỏi: &quot;Th&igrave; ra anh họ L&yacute;, tr&ocirc;ng anh chắc nhiều tuổi hơn t&ocirc;i, t&ocirc;i cứ gọi anh một tiếng anh ho phải ph&eacute;p.Anh Xu&acirc;n Lại n&agrave;y, anh vừa hỏi thằng em c&oacute; thu mua đồ cổ ko, thế n&agrave;o, &ocirc;ng anh c&oacute; minh kh&iacute; g&igrave; muốn ra h&agrave;ng &agrave;?&quot;</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai ko hiểu&quot;minh kh&iacute; g&igrave;?&quot;</p>\r\n\r\n<p>T&ocirc;i thấy h&oacute;a ra l&agrave; một ch&uacute; g&agrave;, b&egrave;n hỏi thẳng lu&ocirc;n :c&oacute; phải c&oacute; đồ cổ g&igrave; muốn b&aacute;n ko?cho thằng em xem một ch&uacute;t được ko?&quot;</p>\r\n\r\n<p>LXL nh&igrave;n trước ng&oacute; sau, n&oacute;i khẽ: &quot;...iem c&oacute; chiếc h&agrave;i, c&aacute;c anh định trả bao nhi&ecirc;u?&quot;</p>\r\n\r\n<p>t&ocirc;i nghe đến đ&acirc;y thiếu điều tức hộc m&aacute;u mồm, mỗi chiếc h&agrave;i r&aacute;ch m&agrave; m&agrave;y cũng đ&ograve;i b&aacute;n lấy tiền &agrave;, mẹ kiếp c&oacute; c&aacute;c th&ecirc;m tiềm cũng chẳng c&oacute; thằng n&agrave;o mua đ&acirc;u.Nhưng rồi lại nghĩ ngay , nội t&igrave;nh c&oacute; lẽ ko đơn giản như vậy, b&egrave;n hỏi: &quot;h&agrave;i g&igrave;?của ai vậy?&quot;</p>\r\n\r\n<p>LXL thấy t&ocirc;i kh&aacute; l&agrave; mềm mỏng, cũng bạo dạn hơn một ch&uacute;t, he he k&eacute;o miệng t&uacute;i ra bảo t&ocirc;i h&ograve;m v&agrave;o trong xem.T&ocirc;i rướn cổ nh&igrave;n v&agrave;o,trong t&uacute;i da n&aacute;t của LXL c&oacute; một chiếc h&agrave;i th&ecirc;u hoa g&oacute;t sen 3 tấc của phụ nữ thời xưa.</p>\r\n\r\n<p>ko để t&ocirc; kịp nh&igrave;n kỹ g&atilde; đ&atilde; vội bịt miệng t&uacute;i lại,cứ l&agrave;m như t&ocirc;i nh&igrave;n l&acirc;u th&ecirc;m ch&uacute;t nữa c&aacute;i h&agrave;i của g&atilde; sẽ bay mất ko bằng.</p>\r\n\r\n<p>T&ocirc;i bảo: &quot;C&oacute; đến nỗi phải thế ko hả, &ocirc;ng anh cứ bỏ ra đ&acirc;y để t&ocirc;i tr&ocirc;ng cho r&otilde;,t&ocirc;i c&ograve;n chưa kịp nh&igrave;n thấy c&aacute;i g&igrave; m&agrave;, thế &ocirc;ng anh lấy chiếc h&agrave;i n&agrave;y ở đ&acirc;u ra?&quot;</p>\r\n\r\n<p>LXL n&oacute;i: &quot;&Ocirc;ng chủ ạ, &ocirc;ng muốn mua th&igrave; cứ ra một c&aacute;i gia đi những việc kh&aacute;c khỏi phải bận t&acirc;m l&agrave;m g&igrave;?&quot;</p>\r\n\r\n<p>T&ocirc;i n&oacute;i: &quot;Anh Xu&acirc;n Lai n&agrave;y,anh phải cho t&ocirc;i cầm l&ecirc;n xem đ&atilde; chứ, ko xem h&agrave;ng kỹ th&igrave; t&ocirc;i ra gi&aacute; thế n&agrave;o đ&acirc;y?&quot; rồi t&ocirc;i hạ thấp giọng th&igrave; thầm: &quot;hay anh ngại chỗ n&agrave;y lắm người nhiều mắt?thế t&ocirc;i mời anh ra c&aacute;i qu&aacute;n gần đ&acirc;y ăn mấy b&aacute;nh chẻo nh&acirc;n thịt d&ecirc; nh&eacute;.T&ocirc;i vẫn thường ra đ&oacute; b&agrave;n chuyện l&agrave;m ăn, y&ecirc;n tĩnh lắm, đến l&uacute;c đ&oacute; t&ocirc;i xem nếu đ&uacute;ng l&agrave; h&agrave;ng tốt, ch&uacute;ng ta cũng dễ thỏa thuận gi&aacute; cả,anh thấy thế c&oacute; được ko?&quot;</p>\r\n\r\n<p>Vừa nghe đến b&aacute;nh chẻo nh&acirc;n thịt d&ecirc;, LXL đ&atilde; nuốt nước miếng đ&aacute;nh ực: &quot;dạ được qu&aacute; đi chứ ạ, chứ ngồi b&ecirc;u ở đ&acirc;y chỉ tổ chết nắng, c&oacute; chuyện g&igrave; qua đ&oacute; ăn canh chua b&aacute;nh chẻo xong ta b&agrave;n cũng chưa muộn, &ocirc;ng chủ nhỉ?&quot;</p>\r\n\r\n<p>T&ocirc;i đ&aacute;nh mắt ra hiệu với Tuyền b&eacute;o v&agrave; Răng v&agrave;ng, rồi đưa LXL tới qu&aacute;n b&aacute;nh chẻo ở phố b&ecirc;n cạnh.Qu&aacute;n b&aacute;nh chẻo nh&acirc;n thịt d&ecirc; n&agrave;y kh&aacute; nổi tiếng trong khu, vợ ch&ocirc;ng chủ qu&aacute;n đều l&agrave; người l&agrave;m ăn trung hậu, nh&acirc;n b&aacute;nh bao giờ cũng nhiều thịt.c&oacute; c&aacute;i phong vị rất ri&ecirc;ng, gi&aacute; cả phải chăng, m&agrave; qu&aacute;n x&aacute; cũng rất sạch sẽ gọn g&agrave;ng.</p>\r\n\r\n<p>L&uacute;c n&agrave;y trời sắp giữa trưa, một l&aacute;t th&ocirc;i l&agrave; đến giờ ăn, kh&aacute;ch đến qu&aacute;n mỗi l&uacute;c một đ&ocirc;ng hơn.T&ocirc;i thường đen đ&acirc;y ăn cơm n&ecirc;n đ&atilde; rất th&acirc;n quen với hai vợ chồng chủ qu&aacute;n, ch&agrave;o hỏi một c&acirc;u, rồi b&agrave; chủ dẫn thẳng ch&uacute;ng t&ocirc;i v&agrave;o gian nh&agrave; kho sau bếp, k&ecirc; một chiếc b&agrave;n, đặt v&agrave;i c&aacute;i ghế với b&aacute;t đũa, xong l&agrave; ra ngo&agrave;i b&aacute;n h&agrave;ng lu&ocirc;n.</p>\r\n\r\n<p>Chỗ n&agrave;y l&agrave; nơi t&ocirc;i d&ugrave;ng để b&agrave;n chuyện l&agrave;m ăn, trg kho ngo&agrave;i những bao bột chất đống ra th&igrave; chẳng c&ograve;n g&igrave; kh&aacute;c , mỗi lần đến ăn, t&ocirc;i đều ko lấy tiền thừa coi như ph&iacute; bao ph&ograve;ng lu&ocirc;n.</p>\r\n\r\n<p>T&ocirc;i n&oacute;i: &quot; Anh Xu&acirc;n lai n&agrave;y, anh thấy nơi n&agrave;y đ&atilde; đủ y&ecirc;n tĩnh chưa,giờ anh đưa h&agrave;i đ&acirc;y cho t&ocirc;i xem m&ocirc;t ch&uacute;t được rồi nhỉ!&quot;</p>\r\n\r\n<p>Nhưng hồn ph&aacute;ch L&yacute; Xu&acirc;n Lai đ&atilde; bị m&ugrave;i b&aacute;nh chẻo b&ecirc;n ngo&agrave;i bay v&agrave;o h&uacute;t mất, những lời t&ocirc;i n&oacute;i tựa như gi&oacute; thoảng b&ecirc;n tai, g&atilde; ta cứ nhấp nhổm chỉ chực đ&aacute;nh ch&eacute;n.</p>\r\n\r\n<p>Thấy vậy t&ocirc;i cũng chẳng biết l&agrave;m sao, đ&agrave;nh gượng cười hu&yacute;nh nhẹ c&aacute;nh tay g&atilde;: &quot;Đừng n&oacute;ng, một l&uacute;c nữa b&aacute;nh chẻo ch&iacute;n b&agrave; chủ tự khắc b&ecirc; v&agrave;o cho ch&uacute;ng ta.Chiếc h&agrave;i của &ocirc;ng anh m&agrave; b&aacute;n được gi&aacute; cao th&igrave; ng&agrave;y ng&agrave;y ăn cả mẻ b&aacute;nh chẻo nh&acirc;n thịt d&ecirc; cũng chẳng nhằm nh&ograve; g&igrave;&quot;&quot;</p>\r\n\r\n<p>C&Aacute;i hu&yacute;nh nhẹ của t&ocirc;i khiến L&yacute; Xu&acirc;n Lai như bừng tỉnh, nghe t&ocirc;i n&oacute;i xong g&atilde; v&ocirc;ị lắc đầu quầy quậy: &quot;ko được ko được b&aacute;n được tiền c&ograve;n phải lấy vợ sinh con nữa chứ.&quot;</p>\r\n\r\n<p>T&ocirc;i cười cười n&oacute;i;&quot;Th&igrave; ra &ocirc;ng anh chưa lấy vợ &agrave;?Thằng em đ&acirc;y cũng thế. Lấy vợ cần g&igrave; phải vội, đợi &ocirc;ng anh c&oacute; tiền rồi th&igrave; c&oacute; thể rước một c&ocirc; vợ g&aacute;i Mễ Chi ấy chứ.Chỗ &ocirc;ng anh chẳng phải c&oacute; c&acirc;u: trai Tuy Đức, g&aacute;i Mễ Chi hay sao? &Ocirc;ng anh n&oacute;i thử thằng em nghe xem g&aacute;i Mễ Chi đẹp thế n&agrave;o n&agrave;o?&quot;</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai c&oacute; vẻ kh&ocirc;ng c&ograve;n e d&egrave; với t&ocirc;i như l&uacute;c n&atilde;y nữa , nghe t&ocirc;i hỏi, g&atilde; liền trả lời: &quot;&Ocirc;i d&agrave;o! G&aacute;i Mễ Chi ấy &agrave;,đẹp như b&ocirc;ng hoa đỏ nở đầy tr&ecirc;n &ocirc; của sổ vậy, nếu rước được c&ocirc; vợ Mễ Chi về nh&agrave;, th&igrave; thế n&agrave;o cũng được hết.&quot;</p>\r\n\r\n<p>Trong khi chuyện tr&ograve;, b&agrave; chủ qu&aacute;n b&ecirc; l&ecirc;n một đĩa b&aacute;nh chẻo n&oacute;ng hổi, lại k&egrave;m th&ecirc;m cả hai chai bia, L&yacute; Xu&acirc;n Lai chẳng c&ograve;n t&acirc;m trạng chuyện tr&ograve; g&igrave; nữa, cứ gắp hết miếng n&agrave;y đến miếng kh&aacute;c, kh&ocirc;ng ngừng cho v&agrave;o mồm như băng chuyền vậy.</p>\r\n\r\n<p>T&ocirc;i tr&ocirc;ng c&aacute;ch g&atilde; ăn, chắc hai c&acirc;n b&aacute;nh chẻo n&agrave;y chẳng thấm v&agrave;o đ&acirc;u, liền vội k&ecirc;u b&agrave; chủ qu&aacute;n l&agrave;m th&ecirc;m hai c&acirc;n nữa, rồi r&oacute;t th&ecirc;m &iacute;t giấm v&agrave;o ch&eacute;n nhỏ trước mặt L&yacute; Xu&acirc;n Lai, n&oacute;i: &quot;Anh Xu&acirc;n Lai n&agrave;y, quanh đ&acirc;y kh&ocirc;ng c&oacute; qu&aacute;n n&agrave;o b&aacute;n m&oacute;n canh chua b&aacute;nh chẻo chỗ c&aacute;c anh hay ăn, d&ugrave;ng tạm m&oacute;n n&agrave;y vậy nh&eacute;, c&oacute; giấm đ&acirc;y, anh uống th&ecirc;m ch&uacute;t bia nữa n&agrave;y!&quot;</p>\r\n\r\n<p>Mồm L&yacute; Xu&acirc;n Lai một l&uacute;c nh&eacute;t liền mấy c&aacute;i b&aacute;nh chẻo, g&atilde; vẫn cắm c&uacute;i ăn, chẳng buồn đ&aacute;p lại một tiếng, t&ocirc;i đợi g&atilde; ăn uống h&ograve;m h&ograve;m, rồi mới bắt đầu hỏi chuyện chiếc h&agrave;i th&ecirc;u hoa.</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lại l&uacute;c n&agrave;y đ&atilde; rất tin tưởng t&ocirc;i, liền l&ocirc;i ngay chiếc h&agrave;i trong t&uacute;i ra đưa t&ocirc;i xem.</p>\r\n\r\n<p>Dạo n&agrave;y, t&ocirc;i cũng hay tiếp x&uacute;c với đồ cổ minh kh&iacute;, c&oacute; thể coi như một tay s&agrave;nh sỏi nửa m&ugrave;a rồi, t&ocirc;i cầm chiếc h&agrave;i l&ecirc;n quan s&aacute;t, phần ph&iacute;a trước chiếc h&agrave;i kh&ocirc;ng đến một nắm tay, mũi thon thon như b&uacute;p măng, gấm xanh l&agrave;m nền, b&ecirc;n tr&ecirc;n th&ecirc;u một đ&oacute;a hoa mẫu đơn bằng ba chỉ: đỏ, lam, v&agrave;ng; g&oacute;t h&agrave;i bằng gỗ đ&agrave;n hương, để rỗng ở giữa, b&ecirc;n trong c&oacute; thể nh&eacute;t hương liệu.</p>\r\n\r\n<p>Vể bề ngo&agrave;i v&agrave; hoa văn th&ecirc;u b&ecirc;n tr&ecirc;n cho thấy đ&acirc;y l&agrave; đồ thời Minh.Phụ nữ Thiểm T&acirc;y &iacute;t người b&oacute; ch&acirc;n, nếu c&oacute; th&igrave; qu&aacute; nửa cũng l&agrave; những tiểu thư nh&agrave; quyền qu&yacute;, cho n&ecirc;n kỹ thuật chế t&aacute;c của chiếc h&agrave;i n&agrave;y tương đối tinh xảo.</p>\r\n\r\n<p>Gi&aacute; c&oacute; Răng V&agrave;ng ở đ&acirc;y, hắn chỉ cần đưa l&ecirc;n mũi ngửi một c&aacute;i, l&agrave; đ&atilde; biết ngay lại lịch của chiếc h&agrave;i n&agrave;y rồi, nhưng t&ocirc;i th&igrave; kh&ocirc;ng c&oacute; bản lĩnh cao si&ecirc;u như vậy, đo&aacute;n bừa kh&ocirc;ng chuẩn lắm.Nh&igrave;n m&agrave;u sắc v&agrave; kỹ thuật th&ecirc;u may như vậy, cũng kh&ocirc;ng giống loại đồ nh&aacute;i. H&agrave;i thơm th&ecirc;u hoa g&oacute;t sen ba tấc hiện đang l&agrave; mặt h&agrave;ng n&oacute;ng tr&ecirc;n thị trường rất c&oacute; gi&aacute; trị sưu tập.</p>\r\n\r\n<p>T&ocirc;i hỏi L&yacute; Xu&acirc;n Lai lấy chiếc h&agrave;i n&agrave;y ở đ&acirc;u ra, g&atilde; cũng kh&ocirc;ng giấu giếm, th&agrave;nh thật kể r&otilde; ngọn ng&agrave;nh c&acirc;u chuyện.</p>\r\n\r\n<p>Ở c&aacute;i v&ugrave;ng qu&ecirc; g&atilde;, mười năm th&igrave; c&oacute; đến ch&iacute;n năm hạn, năm nay lại c&ograve;n hạn to, suốt mấy th&aacute;ng trời kh&ocirc;ng c&oacute; lấy một hạt mưa,d&acirc;n l&agrave;ng bị dồn v&agrave;o bước đường c&ugrave;ng, liền nghĩ ra một số qu&aacute;i chi&ecirc;u b&agrave;ng m&ocirc;n t&agrave; đạo.</p>\r\n\r\n<p>Để cầu mưa, d&acirc;n l&agrave;ng đ&atilde; giở c&aacute;c c&aacute;ch. Trong l&agrave;ng c&oacute; l&atilde;o thầy b&oacute;i m&ugrave; bảo rằng chuyện n&agrave;y l&agrave; do thần Hạn bạt g&acirc;y ra, phải đuổi Hạn bạt đi th&igrave; trời mới cho mưa xuống.</p>\r\n\r\n<p>&quot;Xua giặc hạn&quot; hay d&acirc;n gian c&ograve;n gọi l&agrave; &quot;xua hạn bạt&quot;, l&agrave; một tập tục thường thấy ở c&aacute;c v&ugrave;ng trung nguy&ecirc;n trước giải ph&oacute;ng, một v&agrave;i tỉnh v&ugrave;ng s&acirc;u v&ugrave;ng xa như H&agrave; Nam, Sơn Đ&ocirc;ng, Thiểm T&acirc;y cũng đều c&oacute; tập tục nay.</p>\r\n\r\n<p>Mọi người hỏi thần Hạn bạt ở đ&acirc;u, th&igrave; l&atilde;o m&ugrave; b&oacute;i m&atilde;i vẫn kh&ocirc;ng ra được.L&uacute;c bấy giờ c&oacute; một thằng nh&oacute;c chăn d&ecirc; kể, khi n&oacute; chăn d&ecirc; ngang qua b&atilde;i tha ma đ&atilde; bỏ hoang từ l&acirc;u ở ph&iacute;a Đ&ocirc;ng l&agrave;ng, tr&ocirc;ng thấy một đứa b&eacute; m&igrave;nh mẩy xanh l&eacute;t, chậy v&agrave;o cỗ quan t&agrave;i v&ocirc; chủ. Kh&ocirc;ng ai biết cỗ quan t&agrave;i ấy của nh&agrave; n&agrave;o, v&igrave; rằng trong l&agrave;ng từ l&acirc;u kh&ocirc;ng c&ograve;n ai ch&ocirc;n người chết ở đ&oacute; nữa, vả lại cũng kh&ocirc;ng hiểu v&igrave; l&yacute; do g&igrave; m&agrave; cỗ quan t&agrave;i n&aacute;t ấy đến nay vẫn chưa được chon xuống đất.</p>\r\n\r\n<p>L&atilde;o m&ugrave; nghe xong, lập tức ph&aacute;n thần Hạt bạt chắc trốn trong quan t&agrave;i đ&oacute;, d&acirc;n l&agrave;ng b&agrave;n bạc một hồi, chuẩn bị xắn tay bậy cỗ quan t&agrave;i l&ecirc;n, xem xem rốt cuộc c&oacute; thần Hạn bạt n&agrave;o kh&ocirc;ng.</p>\r\n\r\n<p>Trưởng th&ocirc;n nghe vậy kh&ocirc;ng bằng l&ograve;ng, bảo l&atilde;o m&ugrave; kia n&oacute;i nhăng n&oacute;i cuội.L&atilde;o m&ugrave; tức kh&iacute;, đ&aacute;nh cược với trưởng th&ocirc;n, nếu như kh&ocirc;ng t&igrave;m ra được thần Hạn bạt trong cỗ quan t&agrave;i v&ocirc; chủ kia, th&igrave; con trai l&atilde;o sẽ chăn d&ecirc; cho nh&agrave; trưởng th&ocirc;n một năm.</p>\r\n\r\n<p>Kết quả l&agrave; d&acirc;n trong l&agrave;ng tụ tập cả ở khu nghĩa địa hoang cuối th&ocirc;n, mọi người bảo l&agrave; l&agrave;m lu&ocirc;n, hợp sức bật nắp quan t&agrave;i l&ecirc;n.</p>\r\n\r\n<p>V&aacute;n quan t&agrave;i vừa mới bung ra, đ&atilde; ngửi thấy một m&ugrave;i h&ocirc;i tanh sặc sụa, giống như m&ugrave;i phả ra từ đống c&aacute; thối phơi l&acirc;u dưới &aacute;nh mặt trời, muốn kh&oacute; ngửi thế n&agrave;o th&igrave; kh&oacute; ngửi thế ấy.</p>\r\n\r\n<p>V&agrave;i g&atilde; to gan kh&ocirc;ng sợ chết, bịt chặt mũi tiến l&ecirc;n trước, vừa nh&igrave;n v&agrave;o b&ecirc;n trong liền giật bắn cả m&igrave;nh. Trong quan t&agrave;i l&agrave; x&aacute;c một người phụ nữ, quần &aacute;o trang sức đều được bảo quản ho&agrave;n hảo, tr&ocirc;ng hệt như đồ mới, song nh&igrave;n phục sức ấy, tuyệt nhi&ecirc;n chẳng phải thứ thời nay, đ&acirc;y l&agrave; một x&aacute;c cổ.</p>\r\n\r\n<p>Phục sức nguy&ecirc;n vẹn như mới, nhưng x&aacute;c chết đ&atilde; kh&ocirc; lại như đanh, da thịt quắt lại như vỏ c&acirc;y.</p>\r\n\r\n<p>Ngay tr&ecirc;n đỉnh đầu x&aacute;c chết , c&oacute; một con qu&aacute;i vật nhỏ h&igrave;nh dạng giống lo&agrave;i khỉ, t&ograve;an th&acirc;n mọc đầy l&ocirc;ng xanh, m&igrave;nh d&agrave;i hơn bảy tấc, con khỉ xanh n&agrave;y vẫn c&ograve;n sống, đang quận tr&ograve;n người nằm ngủ ngon l&agrave;nh.</p>\r\n\r\n<p>L&atilde;o m&ugrave; nghe d&acirc;n l&agrave;ng tả lại sự t&igrave;nh, liền khăng khăng ph&aacute;n, con qu&aacute;i vật l&ocirc;ng xanh n&agrave;y ch&iacute;nh l&agrave; Hạn bạt, nhất định phải giết n&oacute; ngay lập tức, sau đ&oacute; d&ugrave;ng gioi quật li&ecirc;n tiếp, động t&aacute;c phải thật mau le,kh&ocirc;ng th&igrave; trời tối n&oacute; chạy mất tăm, c&oacute; muốn bắt lại kh&oacute; lắm.</p>\r\n\r\n<p>V&agrave;i người l&agrave;ng lớn gan l&ocirc;i con qu&aacute;i vật mọc đầy l&ocirc;ng xanh ấy ra khỏi quan t&agrave;i, lấy b&uacute;a đập chết, sau đ&oacute; lại lấy roi quật x&aacute;c. Kỳ lại ở chỗ, con qu&aacute;i vật n&agrave;y kh&ocirc;ng hề chảy m&aacute;u, roi quật v&agrave;o đ&acirc;u th&igrave; chỗ đ&oacute; lại bốc l&ecirc;n rất nhiều kh&oacute;i đen, sau c&ugrave;ng x&aacute;c con qu&aacute;i vật bị quật n&aacute;t, cũng kh&ocirc;ng thấy kh&oacute;i đen bay ra nữa, l&uacute;c ấy mới chất củi đốt n&oacute; th&agrave;nh tro bụi.</p>\r\n\r\n<p>L&uacute;c n&agrave;y trời đ&atilde; xẩm tối, d&acirc;n l&agrave;ng hỏi l&atilde;o m&ugrave; xem phải xử tr&iacute; x&aacute;c người trong quan t&agrave;i kia ra sao. L&atilde;o m&ugrave; bảo, để lại sớm muộn ắt sinh họa lớn, chi bằng nh&acirc;n l&uacute;c c&ograve;n sớm đốt lu&ocirc;n cho nhẹ nợ, đồ đạc b&ecirc;n trong bất kể ai cũng kh&ocirc;ng được động v&agrave;o.</p>\r\n\r\n<p>L&uacute;c đầu d&acirc;n l&agrave;ng c&oacute; vẻ do dự, dẫu sao th&igrave; c&aacute;i th&acirc;y trong quan t&agrave;i cũng kh&ocirc;ng phải người thời nay, hơn nữa c&ograve;n c&oacute; cơ man v&agrave;ng bạc ch&acirc;u b&aacute;u như thế, đốt đi h&aacute; chẳng phải tiếc lắm ư.</p>\r\n\r\n<p>D&acirc;n l&agrave;ng đang ph&acirc;n v&acirc;n, m&acirc;y đen tr&ecirc;n trời mỗi l&uacute;c một d&agrave;y đặc, chốc chốc lại c&oacute; tiếng sấm vọng tới, xem ra trời sắp đổ mưa to, d&acirc;n l&agrave;ng mừng rơn nh&uacute;n nhảy, lời của l&atilde;o m&ugrave; l&uacute;c n&atilde;y c&ograve;n b&aacute;n t&iacute;n b&aacute;n nghi, giờ th&igrave; đ&atilde; coi lời th&aacute;nh ph&aacute;n.</p>\r\n\r\n<p>L&atilde;o m&ugrave; đ&atilde; n&oacute;i phải đốt, vậy th&igrave; phải đốt cho sạch. Cuối c&ugrave;ng l&atilde;o trưởng th&ocirc;n quyết định để L&yacute; Xu&acirc;n Lai ở lại ch&acirc;m lửa đốt quan t&agrave;i. L&yacute; Xu&acirc;n Lai vốn l&agrave; g&atilde; c&ugrave; lần, ng&agrave;y thường trưởng th&ocirc;n bảo g&igrave; th&igrave; l&agrave;m đấy, lần n&agrave;y tuy sợ, nhưng cũng đ&agrave;nh bặm m&ocirc;i đ&aacute;nh liều n&aacute;n lại.</p>\r\n\r\n<p>Để đốt được cỗ quan t&agrave;i trước khi trời mưa xuống, g&atilde; vội v&atilde; chạy đi &ocirc;m về mấy b&oacute; củi kh&ocirc;, chất bừa xuống b&ecirc;n dưới quan t&agrave;i, sau đ&oacute; ch&acirc;m một mồi lủa, bốc ch&aacute;y bừng bừng.</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai ngồi xổm b&ecirc;n cạnh nh&igrave;n chằm chằm. g&atilde; ta vốn l&agrave; kể khố r&aacute;ch &aacute;o &ocirc;m, gần bốn mươi tuổi đầu m&agrave; chưa c&oacute; tiền lấy vợ, bấy giờ nghĩ đến v&agrave;ng bạc trong quan t&agrave;i, kh&ocirc;ng sao tr&aacute;nh khỏi động l&ograve;ng, tiếc rằng ban n&atilde;y kh&ocirc;ng d&aacute;m lấy, giờ th&igrave; lửa đ&atilde; bắt v&agrave;o, c&oacute; muốn lấy th&igrave; cũng chẳng kịp, m&agrave; đồ ch&aacute;y đen cả rồi kh&ocirc;ng biết c&ograve;n b&aacute;n được tiền hay kh&ocirc;ng nữa.</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai đang ngậm ng&ugrave;i tiếc rẻ, bỗng thấy &aacute;nh chớp l&oacute;e s&aacute;ng, bầu kh&ocirc;ng li&ecirc;n tiếp dội xuống ba bốn ti&ecirc;ng sấm rền, mưa đổ sập xuống như tr&uacute;t, tức thời dập tắt ngọn lủa đang chấy rừng rực.</p>\r\n\r\n<p>Cả người L&yacute; Xu&acirc;n Lai đều bị nước mưa lạnh buốt thấm ướt sũng, g&atilde; nh&igrave;n chăm chăm v&agrave;o cỗ quan t&agrave;i đ&atilde; bị ch&aacute;y s&eacute;m một nửa, trong l&ograve;ng thấp thỏm, đ&acirc;y đ&uacute;ng l&agrave; cơ hội trời cho, lửa vẫn chưa l&agrave;m hỏng hết những thứ b&ecirc;n trong, muốn th&oacute; lấy v&agrave;i thứ th&igrave; phải tranh thủ nh&acirc;n l&uacute;c n&agrave;y th&ocirc;i.</p>\r\n\r\n<p>D&acirc;n l&agrave;ng đ&atilde; k&eacute;o về hết, kh&oacute; khăn lắm trời mới mưa to một trận, mọi người phải chuẩn bị rất nhiều việc, giờ ở c&aacute;i chốn đồn kh&ocirc;ng m&ocirc;ng quạnh n&agrave;y, chỉ c&ograve;n lại một m&igrave;nh L&yacute; Xu&acirc;n Lai g&atilde; m&agrave; th&ocirc;i, nghĩ đến c&aacute;i x&aacute;c đ&agrave;n b&agrave; cổ qu&aacute;i trong quan, quả l&agrave; cũng th&aacute;y sởn cả gai ốc.</p>\r\n\r\n<p>Nhưng r&ograve;i lại nghĩ đến chuyện lấy v&agrave;ng bạc ch&acirc;u b&aacute;u đổi lấy tiền l&agrave; cưới được một c&ocirc; vợ thắt đ&aacute;y lưng ong,g&atilde; nh&agrave; ngh&egrave;o L&yacute; Xu&acirc;n Lai kh&ocirc;ng đắn đo th&ecirc;m nữa, hai tay vung quốc l&ecirc;n bổ vỡ nắp quan t&agrave;i. Cỗ quan t&agrave;i vốn đ&atilde; bị chấy qu&aacute; nửa, giờ bật nắp l&ecirc;n chẳng mấy kh&oacute; khăn, được m&aacute;y nh&aacute;t, v&aacute;n quan t&agrave;i đ&atilde; cho&atilde;i ra một b&ecirc;n.</p>\r\n\r\n<p>L&uacute;c n&atilde;y khi d&acirc;n l&agrave;ng khai quan, L&yacute; Xu&acirc;n Lai chỉ đứng n&eacute;p trong đ&aacute;m đ&ocirc;ng th&ograve; mắt nh&igrave;n v&agrave;i lượt,kh&ocirc;ng d&aacute;m nh&igrave;n kỹ, giờ để gỡ lấy v&agrave;i m&oacute;n đồ trang sức đ&aacute;ng tiền tr&ecirc;n m&igrave;nh cỗ th&acirc;y, g&atilde; kh&ocirc;ng thể kh&ocirc;ng đ&aacute;nh liều bước l&ecirc;n quan s&aacute;t.</p>\r\n\r\n<p>M&ugrave;i h&ocirc;i thối trong quan tai vốn đ&atilde; tản đi gần hết, nhưng bị đốt nủa chừng, lại th&ecirc;m nước mưa ngấm v&agrave;o, c&aacute;c loại m&ugrave;i x&aacute;c thối, m&ugrave;i ẩm mốc, m&ugrave;i lửa kh&eacute;t quện lại với nhau, tạo th&agrave;nh một m&ugrave;i qu&aacute;i dị kh&oacute; ngửi v&ocirc; c&ugrave;ng, d&ugrave; trời mưa tầm t&atilde;, cũng chẳng thể &aacute;t đi được c&aacute;i m&ugrave;i qu&aacute;i đăn n&agrave;y.</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai bị hun đến nỗi đầu đau như b&uacute;a bổ. g&atilde; bịt mũi gắng gượng chịu đựng, rồi nh&ograve;m v&agrave;o trong cỗ quan t&agrave;i ch&aacute;y s&eacute;m kia, kh&ocirc;ng nh&igrave;n c&ograve;n đỡ, chứ đ&atilde; nh&igrave;n rồi th&igrave; kh&ocirc;ng thể nh&iacute;n nịn nổi, L&yacute; Xu&acirc;n Lai tức khắc th&aacute;o miệng n&ocirc;n ra ồng ộc.</p>\r\n\r\n<p>Mưa rơi mỗi l&uacute;c một nặng hạt, sắc trời d&atilde; tối hẳn, c&ograve;n kh&ocirc;ng h&agrave;nh động ngay th&igrave; e sẽ muộn mất, L&yacute; Xu&acirc;n Lai đưa tay quệt những thứ tanh dơ tr&ecirc;n m&eacute;p, mắt kh&ocirc;ng rời chiếc xuyến v&agrave;ng tr&ecirc;n cổ tay x&aacute;c chết, đang định th&ograve; tay gỡ ra, th&igrave; đột nhi&ecirc;n c&oacute; người vỗ v&agrave;o lưng một c&aacute;i.</p>\r\n\r\n<p>C&uacute; vỗ khiến L&yacute; Xu&acirc;n Lai giật th&oacute;t người, thiếu điều s&oacute;n ********* ra quần, tưởng rằng sấm chớp giật chớp l&ograve;e l&agrave;m cho x&aacute;c chết trong c&aacute;c mộ cạnh đ&oacute; biến th&agrave;nh quỷ nhập tr&agrave;ng. Trong v&ugrave;ng vẫn thường c&oacute; nhiều lời đồn đại về cương thi, chẳng ngờ chuyến n&agrave;y lại gặp phải thật.</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai ngo&aacute;i đầu nh&igrave;n lại, kh&ocirc;ng phải cương thi, m&agrave; l&agrave; tay h&agrave;ng x&oacute;m M&atilde; Thuận trong l&agrave;ng. M&atilde; Thuận nổi tiếng khắp th&ocirc;n với biệt danh l&agrave; M&atilde; Lớn Mật, người cao lớn vạm vỡ, d&aacute;ng to khỏe lại th&ecirc;m c&aacute;i t&iacute;nh n&oacute;ng nay, đ&atilde; đ&aacute;nh th&igrave; chẳng ki&ecirc;ng d&egrave; g&igrave; hết, cho n&ecirc;n ng&agrave;y thường d&acirc;n l&agrave;ng chẳng ai d&aacute;m g&acirc;y sự với hắn.</p>\r\n\r\n<p>M&atilde; Lớn Mật l&uacute;c chiều liếc thấy tr&ecirc;n m&igrave;nh th&acirc;y ma c&oacute; mấy m&oacute;n trang sức, sinh l&ograve;ng tham, muốn chiếm l&agrave;m của ri&ecirc;ng, nhưng khi đ&oacute; người đ&ocirc;ng kh&ocirc;ng tiện ra tay,lại thấy trưởng th&ocirc;n lệnh cho L&yacute; Xu&acirc;n Lai đốt quan t&agrave;i đi, cũng đ&agrave;nh từ bỏ &yacute; định.Đặt ch&acirc;n về đến nh&agrave; chưa được mấy chốc th&igrave; trời đ&atilde; đổ mưa to, M&atilde; Lớn Mật thấy thế, thầm nghĩ &ocirc;ng trời quả c&oacute; &yacute; gi&uacute;p m&igrave;nh đ&acirc;y, kh&ocirc;ng chừng giờ n&agrave;y quan tai vẫn chưa ch&aacute;y hết, b&egrave;n nh&acirc;n l&uacute;c kh&ocirc;ng ai để &yacute; chạy ngược về phia nghĩa địa.</p>\r\n\r\n<p>M&atilde; Thuận kh&ocirc;ng muốn n&oacute;i nhiều lời với g&atilde; l&yacute; Xu&acirc;n Lai c&ugrave; lần, thản nhi&ecirc;n tước hết từng m&oacute;n đồ trang sức tr&ecirc;n x&aacute;c chết, nh&eacute;t cả v&agrave;o một c&aacute;i t&uacute;i nhỏ, xong xu&ocirc;i hu&yacute;t s&aacute;o toan bỏ đi, chợt thấy L&yacute; Xu&acirc;n Lai ngồi thu lu một đống nh&igrave;n hắn chăm chăm.</p>\r\n\r\n<p>M&atilde; Lớn Mật bu&ocirc;ng v&agrave;i lời dọa nạt, dặn L&yacute; Xu&acirc;n Lai kh&ocirc;ng được tiết lộ chuyện n&agrave;y với ai, bằng kh&ocirc;ng sẽ quẳng g&atilde; đ&oacute; xuống vực cho s&oacute;i ăn. Sau đ&oacute; hắn lần m&ograve; trong t&uacute;i đồ, lấy ra một chiếc h&agrave;i ban n&atilde;y giật ở ch&acirc;n x&aacute;c chết, vứt cho L&yacute; Xu&acirc;n Lai coi như ph&iacute; bịt miệng.</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai cầm chiếc h&agrave;i nhỏ trong tay, trong l&ograve;ng bực bội thế n&agrave;o th&igrave; kh&ocirc;ng cần phải n&oacute;i cũng biết, nhưng g&atilde; lại kh&ocirc;ng d&aacute;m chọc giận họ M&atilde;, đ&agrave;nh phải nuốt cơn t&uacute;c lại m&agrave; n&iacute;n tiếng chấp nhận.Bấy giờ cỗ quan t&agrave;i đ&atilde; bị nước mưa th&aacute;m ướt, c&oacute; muốn đốt cũng kh&ocirc;ng đốt được, hai người liền hợp sức, đ&agrave;o lấy một c&aacute;i hố cạnh đ&oacute; rồi ch&ocirc;n xuống.</p>\r\n\r\n<p>Về đến l&agrave;ng, L&yacute; Xu&acirc;n Lai b&aacute;o với trưởng th&ocirc;n v&agrave; l&atilde;o m&ugrave;, rằng đ&atilde; theo lời dặn của họ, thi&ecirc;u rụi cả cỗ quan t&agrave;i lẫn c&aacute;i x&aacute;c b&ecirc;n trong. L&atilde;o m&ugrave; gật g&ugrave;, h&agrave;i l&ograve;ng n&oacute;i: &quot; Vậy th&igrave; tốt rồi, trước đ&acirc;y ta nghe sư phụ kể về chuyện đ&aacute;nh Hạn bạt, thường th&acirc;y ma mới ch&ocirc;n xuống, nếu vị tr&iacute; chon cất kh&ocirc;ng kh&eacute;o,x&aacute;c chết sẽ th&agrave;nh cương thi, cương thi lại rất dễ h&oacute;a ra Hạn bạt, nạn hạn h&aacute;n đều do lũ Hạn bạt g&acirc;y ra. Kẻ m&ugrave; n&agrave;y tuy kh&ocirc;ng nh&igrave;n được, nhưng l&ograve;ng lại rất s&aacute;ng, vừa nghe c&aacute;c &ocirc;ng nhắc đến quan t&agrave;i với c&aacute;i x&aacute;c b&ecirc;n trong,l&agrave; biết ngay kh&ocirc;ng phải chuyện thường rồi. Dễ chừng người đ&agrave;n b&agrave; kia l&uacute;c chết đang mang thai, ch&ocirc;n xuống rồi mới sinh con ra, vậy l&agrave; đứa trẻ bị ch&ocirc;n sống, sống thế n&agrave;o được, tự nhi&ecirc;n n&oacute; phải chết theo mẹ th&ocirc;i. Trẻ con m&agrave; biến th&agrave;nh Hạn bạt lại c&agrave;ng gh&ecirc; gớm, cả hai mẹ con đều biến th&agrave;nh cương thi, gọi l&agrave; mẫu tử hung,cực kỳ lợi hại, giờ đ&atilde; đốt ra tro, ch&uacute;ng kh&ocirc;ng thể hại người được nữa r&ocirc;i.&quot;</p>\r\n\r\n<p>C&agrave;ng nghe L&yacute; Xu&acirc;n Lai c&agrave;ng thấy bứt rứt, nhưng lại sợ nếu n&oacute;i ra sự thật sẽ bị trưởng th&ocirc;n tr&aacute;ch phạt, đ&agrave;ng ậm ừ v&agrave;i c&acirc;u cho ra chuyện, rồi về nh&agrave; đi ngủ.</p>\r\n\r\n<p>Đ&ecirc;m đến nằm tr&ecirc;n giường đất, L&yacute; Xu&acirc;n Lai trằn trọc m&atilde;i kh&ocirc;ng ngủ được, hễ nhắm mắt lại mơ thấy c&aacute;i th&acirc;y đ&agrave;n b&agrave; c&ugrave;ng đứa con đến b&oacute;p cổ m&igrave;nh, h&atilde;i đến to&aacute;t mồ h&ocirc;i lạnh.</p>\r\n\r\n<p>Mưa rơi tầm t&atilde; suốt đ&ecirc;m, đến khi trời tờ mờ s&aacute;ng, chợt nghe b&ecirc;n ngo&agrave;i nh&aacute;o nh&aacute;c, L&yacute; Xu&acirc;n Lai v&ocirc;i kho&aacute;c vội chiếc &aacute;o chạy ra xem.</p>\r\n\r\n<p>Th&igrave; ra nh&agrave; M&atilde; Lớn Mật bị s&eacute;t đ&aacute;nh tr&uacute;ng, cả vợ hắn v&agrave; hai đứa con, một nh&agrave; bốn người đều mất mạng.</p>\r\n\r\n<p>L&yacute; xu&acirc;n Lai biết chuyện chẳng l&agrave;nh, nhưng lại chẳng biết xử tr&iacute; ra sao, g&atilde; vốn l&agrave; kẻ nh&aacute;t gan, c&agrave;ng nghĩ lại c&agrave;ng hoảng sợ, sống lưng lạnh to&aacute;t, kh&ocirc;ng thể k&igrave;m được hơn nữa, g&atilde; v&aacute;i ********* ra quần từ l&uacute;c n&agrave;o kh&ocirc;ng hay.</p>\r\n\r\n<p>D&acirc;n l&agrave;ng t&igrave;m được trong nh&agrave; M&atilde; Thuận c&aacute;c thứ ch&acirc;u b&aacute;u của x&aacute;c chết cổ kia, trưởng th&ocirc;n thấy thế liền bức hỏi L&yacute; Xu&acirc;n Lai, g&atilde; đ&agrave;nh khai hết sự t&igrave;nh.</p>\r\n\r\n<p>Trưởng th&ocirc;n khẽ mắng L&yacute; Xu&acirc;n Lai mấy c&acirc;u,dặn g&atilde; phải dữ mồm dữ miệng, việc vừa rồi sống để bụng chết mang theo, cấm kh&ocirc;ng được lộ ra ngo&agrave;i. Chớ nh&igrave;n L&yacute; Xu&acirc;n Lai ng&agrave;y thường kh&ugrave; khờ m&agrave; lầm tưởng, thực t&igrave;nh g&atilde; cũng l&agrave; kẻ kh&aacute; t&iacute;nh to&agrave;n, chuyện m&igrave;nh c&oacute; dược chiếc h&agrave;i hắn giấu nhẹm đi kh&ocirc;ng cho ai biết, M&atilde; Lớn Mật cũng chết rồi, cứ đổ hết tội l&ecirc;n đầu hắn., khai hắn cưỡng &eacute;p m&igrave;nh l&agrave;m vậy. Ng&agrave;y thường g&atilde; c&ugrave; lần c&ugrave; lỳ, n&ecirc;n d&acirc;n l&agrave;ng cũng tin lời, kh&ocirc;ng truy cứu th&ecirc;m nữa, dầu sao c&aacute;i chết của bốn người nh&agrave; họ M&atilde; cũng do M&atilde; Thuận tham lam tự chuốc lấy.</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai kh&ocirc;ng d&aacute;m mang chiếc h&agrave;i th&ecirc;u hoa ấy cho ai xem, mặc d&ugrave; g&atilde; kh&ocirc;ng được học h&agrave;nh nhưng cũng biết chiếc h&agrave;i l&agrave; của triều đại trước, tiền lấy vợ của g&atilde; to&agrave;n bộ đều phải tr&ocirc;ng mong v&agrave;o thứ n&agrave;y cả. V&ugrave;ng Thiểm T&acirc;y trộm mộ dấy th&agrave;nh phong tr&agrave;o, bu&ocirc;n b&aacute;n đồ cổ rất tấp nập, trong l&agrave;ng thường xuy&ecirc;n c&oacute; những người từ nơi kh&aacute;c đến thu mua đồ cũ,L&yacute; Xu&acirc;n Lai nh&aacute;t gan, lại sợ d&acirc;n l&agrave;ng ph&aacute;t gi&aacute;c, n&ecirc;n từ bấy vẫn kh&ocirc;ng mang ra b&aacute;n.</p>\r\n\r\n<p>Cho đến một h&ocirc;m, một người họ h&agrave;ng xa của L&yacute; Xu&acirc;n lai ở huyện b&ecirc;n cạnh l&ecirc;n Bắc Kinh l&agrave;m cửu vạn, g&atilde; n&oacute;i ngon n&oacute;i ngọt một hồi, rồi b&aacute;m c&agrave;ng theo l&ecirc;n Bắc Kinh, nghe ng&oacute;ng được khu Phan Gia Viễn c&oacute; thu mua đồ cổ, liền hỏi đường t&igrave;m đến. Kể ra cũng l&agrave; c&oacute; duy&ecirc;n, người m&agrave; g&atilde; mở miệng hỏi đầu ti&ecirc;n lại l&agrave; t&ocirc;i.</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai nom bề ngo&agrave;i thật th&agrave; nhu nhược, nhưng b&ecirc;n trong lại ẩn chứa sự tinh ranh rất kh&oacute; ph&aacute;t gi&aacute;c, g&atilde; uống kh&aacute; nhiều bia, đến khi mặt đỏ t&iacute;a tai, mới mượn hơi men, h&eacute; ra lai lịch chiếc h&agrave;i,c&oacute; đoạn chỉ n&oacute;i lướt qua, giọng điệu ậm ừ, c&oacute; chỗ n&oacute;i kh&ocirc;ng r&otilde; nghĩa, t&ocirc;i nghe chỉ hiểu được t&aacute;m ch&iacute;n phần.</p>\r\n\r\n<p>T&ocirc;i n&oacute;i với L&yacute; Xu&acirc;n Lai: &quot;Lai lịch chiếc h&agrave;i của anh cũng lắm &eacute;o le thật, ban n&atilde;y t&ocirc;i xem qua, thấy loại h&agrave;i thơm th&ecirc;u hoa đế gỗ d&aacute;n n&agrave;y cũng kh&ocirc;ng tồi, phải n&oacute;i l&agrave; h&agrave;i th&ecirc;u từ mấy trăm năm trước,m&agrave; bảo quản được tốt thế nay, quả l&agrave; hiếm gặp.dạo trước thằng em cũng c&oacute; sang tay mấy đ&ocirc;i, nhưng lớp mặt vải đều kh&ocirc; như vỏ c&acirc;y cả, tuy nhi&ecirc;n... &quot;</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai sợ t&ocirc;i n&oacute;i chiếc h&agrave;i của g&atilde; kh&ocirc;ng đ&aacute;ng gi&aacute;, tỏ ra hết sức căng thẳng vội n&oacute;i: &quot;Th&ocirc;i &ocirc;ng chủ ơi, rốt cuộc th&igrave; c&aacute;i n&agrave;y b&aacute;n được mấy tiền thế?&quot;</p>\r\n\r\n<p>T&ocirc;i ra vẻ hết c&aacute;ch, chẹp chẹp miệng n&oacute;i: &quot;Anh Xu&acirc;n lai ạ, chiếc h&agrave;i gi&aacute; như c&oacute; cả đ&ocirc;i, th&igrave; cũng đ&aacute;ng gi&aacute; lắm, ngặt nỗi c&oacute; mỗi một chiếc..&quot;</p>\r\n\r\n<p>X&eacute;t t&igrave;nh h&igrave;nh thị trường hiện giờ, loại h&agrave;i th&ecirc;u g&oacute;t sen ba tấc thời Minh v&agrave; đầu thời Thanh n&agrave;y l&agrave; một m&oacute;n đồ cổ rất tuyệt d&agrave;nh cho những người y&ecirc;u th&iacute;ch văn h&oacute;a d&acirc;n gian cũng như những nh&agrave; sưu tập đồ cổ, hơn nữa, mặc d&ugrave; tr&ecirc;n thị trường c&oacute; kh&ocirc;ng &iacute;t h&agrave;i th&ecirc;u hoa vẫn c&ograve;n giữ được nguy&ecirc;n vẹn, nhưng hầu hết đều l&agrave; đồ cuối Thanh, đầu D&acirc;n quốc cả.</p>\r\n\r\n<p>T&ocirc;i hỏi L&yacute; Xu&acirc;n Lai xem g&atilde; c&oacute; kiếm được chiếc nữa về kh&ocirc;ng, một chiếc tr&ocirc;ng hơi trơ trọi. Nghề chơi đồ cổ n&agrave;y trọng sự to&agrave;n vẹn, m&oacute;n đồ c&agrave;ng ho&agrave;n chỉnh th&agrave;nh bộ lại c&agrave;ng đ&aacute;ng tiền, nhiều khi một hai c&aacute;i chẳng đ&aacute;ng ngh&eacute; mắt nh&igrave;n, nhưng nếu gom đủ cả bộ gi&aacute; trị c&oacute; thể n&ecirc;n gấp mấy lần.</p>\r\n\r\n<p>Sắc mặt L&yacute; Xu&acirc;n Lai lộ vẻ kh&oacute; xử, chiếc c&ograve;n lại từ l&acirc;u đ&atilde; chẳng biết lưu lạc phương n&agrave;o, m&agrave; chỉ ri&ecirc;ng chiếc n&agrave;y th&ocirc;i g&atilde; cũng phải giấu giếm m&atilde;i mới mang l&ecirc;n được Bắc kinh.</p>\r\n\r\n<p>T&ocirc;i n&oacute;i: &quot; Thế n&agrave;y đi, thằng em ấy &agrave;, chẳng giấu g&igrave; anh,thằng em vốn c&oacute; cảm t&igrave;nh đặc biệt với anh em n&ocirc;ng d&acirc;n, năm xưa cha t&ocirc;i cũng đấu tranh cho sự nghiệp giải ph&oacute;ng n&ocirc;ng d&acirc;n TQ m&agrave; ki&ecirc;n quyết th&ocirc;i học để đi theo c&aacute;ch mạng, người gắn b&oacute; cả đời m&igrave;nh với c&aacute;ch mạng,&agrave; m&agrave; th&ocirc;i, đừng nhắc dến &ocirc;ng ấy nữa, &yacute; thằng em l&agrave; ngay cả c&aacute;ch mạng trung Quốc c&ograve;n đi theo con đường n&ocirc;ng th&ocirc;n vậy th&agrave;nh thị mới d&agrave;nh chiến thắng, cho n&ecirc;n thằng em đ&acirc;y c&oacute; thể vỗ ngực n&oacute;i rằng thằng em quyết kh&ocirc;ng v&igrave; anh ở n&ocirc;ng th&ocirc;n m&agrave; gạt anh đ&acirc;u.Chiếc h&agrave;i n&agrave;y mang ra chợ b&agrave;n chắc cũng được s&aacute;u bảy trăm, b&aacute;n cao hơn nữa cũng kh&oacute;, nếu &ocirc;ng anh đ&ocirc;ng &yacute; th&igrave; thằng em mua s&aacute;u trăm rệ, coi như ta kết bạn, sau n&agrave;y &ocirc;ng anh c&oacute; m&oacute;n n&agrave;o hay, cứ trực tiếp mang đến chỗ thằng em được kh&ocirc;ng?&quot;</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai trố mắt kinh ngạc: &quot;anh bẩu sao?s&aacute;u trăm? Iem kh&ocirc;ng nghe nhầm đ&aacute;y chứ?&quot;</p>\r\n\r\n<p>T&ocirc;i n&oacute;i: &quot;Sao?Ch&ecirc; &iacute;t &agrave;?Vậy trả th&ecirc;m anh năm chục nữa nhớ?&quot;</p>\r\n\r\n<p>L&yacute; Xu&acirc;n Lai liến thoắng xua tay: &quot;Kh&ocirc;ng kh&ocirc;ng, kh&ocirc;ng &iacute;t, l&uacute;c đầu iem cứ tưởng cung lắm l&agrave; được ba trăm, ai ngờ..&quot;</p>\r\n\r\n<p>L&uacute;c ấy t&ocirc;i trả ngay cho g&atilde; s&aacute;u trăm rưỡi, L&yacute; Xu&acirc;n Lai đếm đi đếm lại hơn chục lần, rồi mới cẩn thận cất kỹ v&agrave;o trong người, t&ocirc;i dặn g&atilde; phải cẩn thận, uống nhiều bia rượu như vầy, chớ c&oacute; lơ đ&atilde;ng để rồi đ&aacute;nh mất tiền.</p>\r\n\r\n<p>Sau đ&oacute; t&ocirc;i v&agrave; L&yacute; Xu&acirc;n Lai ngồi bu&ocirc;n chuyện ở qu&ecirc; g&atilde;, L&yacute; Xu&acirc;n Lai qu&ecirc; ở Cam Nguy&ecirc;n C&acirc;u ven s&ocirc;ng Ho&agrave;ng H&agrave; thuộc tỉnh Thiểm T&acirc;y, l&agrave; huyện ngh&egrave;o nhất trong v&ugrave;ng ấy, gần đ&aacute;y c&oacute; huyện Long Tường, n&uacute;i non tr&ugrave;ng điệp, nghe kể ng&agrave;y xưa nơi đ&acirc;y l&agrave; v&ugrave;ng quốc tang,mộ cổ nhiều kh&ocirc;ng đếm xuể.</p>\r\n\r\n<p>Cổ mộ ở huyện Long Tường nhiều đến mức độ n&agrave;o chứ,cứ h&igrave;nh dung tr&ecirc;n mảnh đất độ một mẫu c&oacute; tới s&aacute;u bảy ng&ocirc;i mộ, m&agrave; đấy chỉ l&agrave; những ng&ocirc;i lộ thi&ecirc;n, nằm s&acirc;u ph&iacute;a dưới c&ograve;n nhiều nữa.</p>\r\n\r\n<p>Đồ gốm thời Đương t&igrave;m thấy trong những ng&ocirc;i mộ n&agrave;y c&oacute; thể l&ecirc;n đến cả vạn tệ một chiếc, n&ocirc;ng d&acirc;n trong v&ugrave;ng rất nhiều nh&agrave; c&oacute; v&agrave;i m&oacute;n như vậy, họ đều nhờ những đồ khai quật ngo&agrave;i đồng ruộng m&agrave; phất l&ecirc;n cả. Từ thời D&acirc;n quốc, đ&atilde; c&oacute; nhi&ecirc;u kẻ bu&ocirc;n b&aacute;n đồ vật đến đ&acirc;y thu mua, th&agrave;nh ra đến giờ chẳng c&ograve;n mấy thứ ra hồn.</p>\r\n\r\n<p>Nghe kể, ở mạn ph&iacute;a Nam d&atilde;y Tần Lĩnh c&ograve;n c&oacute; nhiều mộ lớn hơn, chỉ c&oacute; điều kh&ocirc;ng dễ t&igrave;m ch&uacute;t n&agrave;o, ng&ocirc;i n&agrave;o dễ t&igrave;m th&igrave; đều bị bật săng quăng nắp hết cả r&ocirc;i, nỏi tiếng nhất l&agrave; ng&ocirc;i mộ thời H&aacute;n, chỉ t&iacute;nh ri&ecirc;ng hốc do d&acirc;n trộm mộ đ&agrave;o l&ecirc;n cũng phải hơn hai trăm t&aacute;m mươi c&aacute;i, dư cả hốc mới lẫn hốc cũ.</p>\r\n\r\n<p>Ở v&ugrave;ng n&agrave;y tuồn ra nhiều m&oacute;n cổ vật gi&aacute; trị li&ecirc;n th&agrave;nh, nhưng cụ thể l&agrave; g&igrave;, th&igrave; L&yacute; Xu&acirc;n Lai kh&ocirc;ng r&otilde;, những chuyện n&agrave;y g&atilde; ta cũng chỉ nghe h&oacute;ng từ người kh&aacute;c m&agrave; th&ocirc;i.</p>\r\n\r\n<p>Sắc trời l&uacute;c n&agrave;y kh&ocirc;ng c&ograve;n sớm sủa g&igrave;,men bia rong người L&yacute; Xu&acirc;n lai cũng đ&atilde; gần hết, g&atilde; liền đứng dậy xin ph&eacute;p, trước khi đi c&ograve;n khẩn thiết mời t&ocirc;i khi n&agrave;o c&oacute; cơ hội nhất định phải đến nh&agrave; g&atilde; d&ugrave;ng cơm, t&ocirc;i cũng phải x&atilde; giao m&atilde;i, mới tiễn được g&atilde; ra về.</p>\r\n\r\n<p>Quay lại chợ đồ cổ,Tuyền b&eacute;o v&agrave; Răng V&agrave;ng đ&atilde; kh&ocirc;ng c&ograve;n ki&ecirc;n nhẫn được hơn, vừa nh&igrave;n thấy t&ocirc;i, liền liếng thoắng hỏi han xem thu hoạch được g&igrave;.</p>\r\n\r\n<p>T&ocirc;i l&ocirc;i chiếc h&agrave;i th&ecirc;u hoa ra đưa cho hai người xem, Tuyền b&eacute;o lớn giọng chửi lu&ocirc;n: &quot;Thằng tổ n&agrave;y &ocirc;m đầu ch&oacute; m&agrave; cứ như &ocirc;m v&agrave;ng ấy, nh&uacute;ng nhắng cả ng&agrave;y m&agrave; chỉ c&oacute; mỗi chiếc gi&agrave;y n&agrave;y th&ocirc;i &aacute;?&quot;</p>\r\n\r\n<p>Răng V&agrave;ng hỏi t&ocirc;i: &quot;Ấy! Chiếc h&agrave;i n&agrave;y tinh xảo ra phết, anh trả bao nhi&ecirc;u thế?&quot;</p>\r\n\r\n<p>T&ocirc;i n&oacute;i gi&aacute; tiền xong Răng V&agrave;ng khen nức nở: &quot;Anh Nhất dạo n&agrave;y tiến bộ gh&ecirc;, chiếc h&agrave;i th&ecirc;u hoa n&agrave;y b&aacute;n gi&aacute; hai ngh&igrave;n tuyệt đối kh&ocirc;ng vấn đề g&igrave;.&quot;</p>\r\n\r\n<p>T&ocirc;i lấy l&agrave;m hối hận: &quot;N&oacute;i sao nhỉ, biết trước b&aacute;n được gi&aacute; như vậy, l&uacute;c n&atilde;y t&ocirc;i đ&atilde; trả cho &ocirc;ng anh kia th&ecirc;m một ch&uacute;t rồi, cứ tưởng chỉ b&aacute;n đựơc s&aacute;u bảy trăm tệ l&agrave; c&ugrave;ng, xem ra vẫn c&ograve;n phải học hỏi nhiều?&quot;</p>\r\n\r\n<p>Răng V&agrave;ng n&oacute;i: &quot;H&ocirc;m nay thứ hai, thứ hai đầu tuần thưa kh&aacute;ch ba ch&uacute;ng ta đừng ngồi đ&acirc;y m&atilde;i, l&acirc;u rồi chưa đi ăn lẩu d&ecirc;, hai &ocirc;ng anh thấy sao, dọn h&agrave;ng đi Đ&ocirc;ng tứ th&ocirc;i chứ!&quot;</p>\r\n\r\n<p>Tuyền b&eacute;o t&aacute;n th&agrave;nh: &quot;Đ&uacute;ng l&agrave; c&aacute;c bộ &oacute;c vĩ đại hay nghĩ giống nhau, mấy ng&agrave;y nay t&ocirc;i cũng đang th&egrave;m c&aacute;i m&oacute;n n&agrave;y, m&agrave; anh bảo sao c&aacute;i thứ ấy ăn m&atilde;i kh&ocirc;ng ch&aacute;n thế nhỉ?&quot;</p>\r\n\r\n<p>Vẫn l&agrave; c&aacute;i qu&aacute;n quen ở Đ&ocirc;ng Tứ, mới bốn giờ chiều trong qu&aacute;n kh&ocirc;ng một b&oacute;ng kh&aacute;ch ăn, ba bọn t&ocirc;i chọn một b&agrave;n ngồi ở g&oacute;c s&aacute;t tường cạnh cửa sổ.mấy em chạy b&agrave;n vội đặt nồi nẩu, ch&acirc;m bếp ,đặt đồ ăn,rồi lui về quầy t&uacute;m năm tụm ba ngồi bu&ocirc;n chuyện.</p>\r\n\r\n<p>T&ocirc;i r&uacute;t thuốc mời Răng V&Aacute;ng v&agrave; Tuyền b&eacute;o đoạn hỏi: &quot;B&aacute;c n&agrave;y b&aacute;c thử n&oacute;i xem chiếc h&agrave;i n&agrave;y đ&aacute;ng gi&aacute; chỗ n&agrave;o?&quot;</p>\r\n\r\n<p>Răng V&agrave;ng cầm chiếc h&agrave;i th&ecirc;u l&ecirc;n n&oacute;i: &quot;thứ n&agrave;y kh&ocirc;ng phải của hạng b&igrave;nh d&acirc;n đ&acirc;u, &ocirc;ng anh c&oacute; nh&igrave;n ra kh&ocirc;ng, đ&acirc;y l&agrave; hoa mẫu đơn.Từ thời Đường trở lại người ta coi hoa mẫu đơn l&agrave; biểu tượng của gi&agrave;u sang ph&uacute; qu&yacute;.D&acirc;n thường tuy cũng c&oacute; người th&ecirc;u mẫu đơn l&ecirc;n gi&agrave;y d&eacute;p nhưng chắc chắn kh&ocirc;ng thể viền kim tuyến như thế n&agrave;y được.Ngo&agrave;i ra , &ocirc;ng anh xem xem , phần nhụy hoa n&agrave;y c&ograve;n đ&iacute;nh th&ecirc;m 6 hạt ch&acirc;u nhỏ, d&ugrave; kh&ocirc;ng qu&yacute; lắm nhưng gi&aacute; trị nghệ thuật tổng thể được tăng l&ecirc;n rất nhiều. Quan trọng nhất l&agrave; chủ nh&acirc;n của chiếc h&agrave;i, &ocirc;ng anh kia l&agrave; người đến từ Thiểm tay, m&agrave; Thiểm T&acirc;y vốn l&agrave; v&ugrave;ng ch&acirc;n qu&ecirc; mộc mạc, từ xưa người d&acirc;n đ&atilde; kh&ocirc;ng ưa tục b&oacute; ch&acirc;n rồi, t&ocirc;i nghĩ chủ nh&acirc;n của chiếc h&agrave;i n&agrave;y, rất c&oacute; thể l&agrave; gia quyến của một quan vi&ecirc;n ở tỉnh kh&aacute;c được điều đến, hoặc tiểu thư con nh&agrave; quyền qu&yacute; được gả tới, t&oacute;m lại kh&ocirc;ng phải danh gia cũng l&agrave; vọng tộc.V&igrave; thế chiếc h&agrave;i n&agrave;y cũng c&oacute; gi&aacute; trị sưu tập,ở ngo&agrave;i chợ t&ocirc;i n&oacute;i hai ngh&igrave;n, l&agrave; v&igrave; kh&ocirc;ng d&aacute;m ph&ocirc; trương đ&oacute; th&ocirc;i,chứ t&ocirc;i tr&ocirc;ng ch&iacute; &iacute;t cũng phải s&aacute;u ngh&igrave;n, nếu c&oacute; cả đ&ocirc;i, gi&aacute; phải gấp 4,5 lần thế nữa&quot;</p>\r\n\r\n<p>T&ocirc;i v&agrave; Tuyền b&eacute;o đều phải le le lưỡi ra,thật kh&ocirc;ng ngờ chiếc h&agrave;i b&eacute; t&iacute; kia lại c&oacute; thể b&aacute;n với gi&aacute; cao như vậy,t&ocirc;i định bụng sau n&agrave;y nhất định phải đi Thiểm T&acirc;y một chuyến, b&ugrave; th&ecirc;m cho tay L&yacute; Xu&acirc;n Lai &iacute;t tiền, bằng kh&ocirc;ng thiệt th&ograve;i cho g&atilde; qu&aacute;.</p>\r\n\r\n<p>Vừa ăn vừa t&aacute;n dốc, bất gi&aacute;c chủ đề quay sang mộ cổ ở Thiểm T&acirc;y.</p>\r\n\r\n<p>Răng V&agrave;ng n&oacute;i: &quot;Tuy t&ocirc;i chưa dến Thiểm T&acirc;y bao giờ, nhưng cũng c&oacute; nghe anh em trong nghề đi thu mua đồ cũ ở đ&oacute; kể chuyện,t&aacute;m trăm dặm đất Tần Xuy&ecirc;n l&agrave; chốn địa linh nh&acirc;n kiệt, dải Tam Tần nước s&acirc;u đất d&agrave;y,h&agrave;ng ngon ch&ocirc;n dưới đất nhiều kh&ocirc;ng kể xiết. Chỉ ri&ecirc;ng một huyện Long Tường, cũng đ&atilde; c&oacute; đ&eacute;n kh&ocirc;ng dưới chục vạn ng&ocirc;i mộ cổ, c&oacute; những nơi mộ n&agrave;y chồng l&ecirc;n mộ kh&aacute;c, tạo th&agrave;nh mấy tầng văn h&oacute;a kh&aacute;c nhau, tương truyền v&ugrave;ng n&uacute;i Đại ba trong d&atilde;y Tần Lĩnh cũng c&oacute; kh&ocirc;ng &iacute;t mộ cổ lớn. T&ocirc;i vẫn thường nghĩ nếu c&oacute; cơ hội nhất định sẽ đi 1 chuyến,thu thập &iacute;t đồ tốt, m&agrave; d&ugrave; kh&ocirc;ng được g&igrave;, coi như đi mở mang tầm mắt cũng được, nhưng sức khỏe kh&ocirc;ng tốt, n&ecirc;n m&atilde;i đến giờ vẫn chưa c&oacute; cơ hội đi.&quot;</p>\r\n\r\n<p>T&ocirc;i n&oacute;i: &quot;T&ocirc;i vừa n&atilde;y cũng nghĩ đến chuyện khi n&agrave;o rảnh rỗi th&igrave; đi 1 chuyến, chi bằng 3 ch&uacute;ng ta c&ugrave;ng đi chơi 1 chuyến, nh&acirc;n tiện thu mua th&ecirc;m ch&uacute;t h&agrave;ng,&ocirc;ng anh đi c&ugrave;ng hai ch&uacute;ng t&ocirc;i đi,tr&ecirc;n đường cũng tiện coi s&oacute;c lẫn nhau&quot;</p>\r\n\r\n<p>Cả ba &yacute; hợp t&acirc;m đầu, liền b&agrave;n bạc ngay giờ xuất ph&aacute;t.t&ocirc;i đ&atilde; nghe n&oacute;i d&atilde;y tần Lĩnh c&oacute; nhiều long mạch từ l&acirc;u,muốn đi khảo s&aacute;t thực địa một chuyến, tốt nhất l&agrave; t&igrave;m một c&aacute;i đấu lớn, trả cho song khoản vay nặng l&atilde;i của c&ocirc; ả người Mỹ, những ng&agrave;y th&aacute;ng mang nợ thật chẳng dễ sống ch&uacute;t n&agrave;o.</p>\r\n\r\n<p>Răng V&agrave;ng n&oacute;i: &quot;đồ ở đ&oacute; đ&agrave;o l&ecirc;n đều phải giao dich qua cửa trong, đ&atilde; th&agrave;nh một tr&igrave;nh tự nhất định rồi, người ngo&agrave;i kh&oacute; chen ch&acirc;n v&agrave;o lắm.Ch&uacute;ng ta muốn thu mua được đồ c&oacute; gi&aacute;,e phải đến những nơi hẻo l&aacute;nh, kh&ocirc;ng c&oacute; th&igrave; đ&agrave;nh chịu, nếu c&oacute; th&igrave; nhất định kiếm được một mẻ lớn.&quot;</p>\r\n\r\n<p>Tuyền b&eacute;o chợt nhớ ra chuyện, liền hỏi: &quot;Liệu c&oacute; n&ecirc;n mang theo m&oacute;ng lừa đen kh&ocirc;ng nhỉ?Nghe n&oacute;i v&ugrave;ng đ&oacute; lắm cương thi nhất đấy.&quot;</p>\r\n\r\n<p>T&ocirc;i đ&aacute;p: &quot;Ch&uacute;ng ta chủ yếu đi chơi, tiện th&igrave; thu thập một &iacute;t đồ về, kh&ocirc;ng lo gặp b&aacute;nh t&ocirc;ng bự đ&acirc;u.&quot;</p>\r\n\r\n<p>G&atilde; răng v&agrave;ng chợt hỏi: &quot;Anh Nhất n&agrave;y, anh l&agrave; bậc thầy xem phong thủy, anh thử n&oacute;i xem người ta vẫn nhắc v&ugrave;ng ấy c&oacute; lắm hắc hung bạch hung, phong thủy học th&igrave; l&yacute; giải thế n&agrave;o nhỉ?&quot;</p>\r\n\r\n<p>T&ocirc;i n&oacute;i: &quot;Hung c&oacute; thể coi l&agrave; chỉ cương thi, hắc v&agrave; bạch chỉ l&agrave; h&igrave;nh thức thi biến kh&aacute;c nhau, ch&uacute;ng ta đ&atilde; nhắc chuyện n&agrave;y th&igrave; t&ocirc;i xin giải th&iacute;ch theo g&oacute;c độ phong thủy vậy.&quot;</p>', 1, 'chuong-1-chiec-hai-thom', NULL, NULL);
INSERT INTO `chapters` VALUES (8, 'Chapter 1', 10, 'Chapter 1 của bộ truyện tui đọc khá thích', '<p>Sau chuyến đi d&agrave;i 3 th&aacute;ng v&agrave; đặt được ch&acirc;n v&agrave;o &ldquo;Động tận c&ugrave;ng ph&iacute;a Bắc&rdquo;, Young Woo l&uacute;c n&agrave;y nh&igrave;n thấy một cảnh tượng cho&aacute;ng ngợp của v&ocirc; số loại trang bị xếp chồng chất l&ecirc;n nhau như một ngọn n&uacute;i.</p>\r\n\r\n<p>&ldquo;Ồ, g&igrave; thế n&agrave;y?&rdquo;</p>\r\n\r\n<p>T&ecirc;n của c&aacute;c trang bị s&aacute;ng lấp l&aacute;nh với c&aacute;c m&agrave;u xanh, v&agrave;ng v&agrave; t&iacute;m. Chỉ cần lấy được v&agrave;i m&oacute;n đồ th&ocirc;i l&agrave; m&igrave;nh sẽ th&agrave;nh người gi&agrave;u c&oacute;! M&igrave;nh rất muốn lấy hết đống n&agrave;y nhưng m&agrave; kh&ocirc;ng thể!</p>\r\n\r\n<p>[Kh&ocirc;ng thể nhặt được trang bị n&agrave;y.]</p>\r\n\r\n<p>&ldquo;Thật keo kiệt v&agrave; bẩn thỉu.&rdquo;</p>\r\n\r\n<p>Một đống trang bị nằm ngay trước mặt m&igrave;nh đ&acirc;y m&agrave; lại kh&ocirc;ng thể nhặt được! Nếu m&igrave;nh thử bỏ ch&uacute;ng v&agrave;o t&uacute;i th&igrave; ch&uacute;ng lại tự trở về chỗ cũ, như l&agrave; ảo ảnh vậy. Kh&ocirc;ng kh&aacute;c g&igrave; cố lấy c&aacute;i b&aacute;nh treo tr&ecirc;n trời hoặc t&aacute;n tỉnh phụ nữ đ&atilde; c&oacute; chồng. Kh&ocirc;ng, thực ra th&igrave; phụ nữ đ&atilde; c&oacute; chồng lại kh&aacute;c so với c&aacute;i b&aacute;nh treo tr&ecirc;n trời, v&igrave; vẫn c&oacute; thể t&aacute;n được họ nếu như cố gắng đủ mức.</p>\r\n\r\n<p>Cuối c&ugrave;ng th&igrave; m&igrave;nh vẫn chỉ c&oacute; thể nh&igrave;n đống trang bị n&agrave;y với ham muốn v&agrave; tiếc nuối m&agrave; th&ocirc;i.</p>\r\n\r\n<p>&lsquo;Đ&uacute;ng thật&hellip; Nếu như người n&agrave;o c&oacute; thể lấy hết đống n&agrave;y th&igrave; sự c&acirc;n bằng của hệ thống kinh tế trong Satisfy sẽ bị sụp đổ. Thật l&agrave; kh&ocirc;ng may, nhưng m&igrave;nh cũng hiểu điều n&agrave;y.&rsquo;</p>\r\n\r\n<p>M&agrave; m&igrave;nh cũng đ&acirc;u đến đ&acirc;y để t&igrave;m trang bị đ&acirc;u. T&ocirc;i tự an ủi bản th&acirc;n v&agrave; chuyển hướng ch&uacute; &yacute; ra khỏi đống trang bị. C&oacute; một quyển s&aacute;ch cũ kỹ nằm tr&ecirc;n một chiếc b&agrave;n v&agrave;ng l&oacute;ng l&aacute;nh.</p>\r\n\r\n<p>&ldquo;Cuối c&ugrave;ng th&igrave;&hellip;&rdquo;</p>\r\n\r\n<p>Tất cả mọi kh&oacute; khăn t&ocirc;i đ&atilde; chịu đựng để đến được đ&acirc;y hiện ra trong đầu t&ocirc;i. Tiếng cười ph&aacute;t ra khi mũi t&ocirc;i chảy nước v&agrave; mắt t&ocirc;i rơi lệ. C&aacute;m ơn Ch&uacute;a v&igrave; m&igrave;nh đ&atilde; ki&ecirc;n tr&igrave; v&agrave; bền bỉ, nếu kh&ocirc;ng th&igrave; m&igrave;nh đ&atilde; bỏ game v&igrave; c&aacute;i nhiệm vụ qu&aacute; kh&oacute; khăn n&agrave;y. T&ocirc;i cảm thấy tự h&agrave;o gh&ecirc; gớm khi nghĩ đến việc ho&agrave;n th&agrave;nh xong nhiệm vụ. .</p>\r\n\r\n<p>&ldquo;Kukuk&hellip;puhahahat!! Cuối c&ugrave;ng m&igrave;nh đ&atilde; thấy n&oacute;!! Oh, yehhhhhh!&rdquo;</p>\r\n\r\n<p>C&aacute;i nhiệm vụ khốn nạn cấp S kh&ocirc;ng cho hủy n&agrave;y! Khiến m&igrave;nh chết bao nhi&ecirc;u lần v&igrave; phải l&agrave;m nhiệm vụ một m&igrave;nh rồi? Lại c&ograve;n mất bao nhi&ecirc;u trang bị khi hết độ bền nữa?</p>\r\n\r\n<p>L&agrave; một thợ r&egrave;n con người m&agrave; c&oacute; kỹ năng vượt qua cả kỹ năng của người l&ugrave;n, Pagma đ&atilde; đạt tới đỉnh cao kỹ năng như một vị thần vậy!</p>\r\n\r\n<p>Khu&ocirc;n mặt của B&aacute; tước Ashur, người khiến t&ocirc;i phải đến 5 khu vực cấm của vương quốc Vĩnh hằng hiện ra trong đầu t&ocirc;i.</p>\r\n\r\n<p>&ldquo;Nếu đưa quyển s&aacute;ch n&agrave;y cho &ocirc;ng ta th&igrave; m&igrave;nh sẽ nhận được Kiếm Mặt trời? Chỉ cần c&oacute; n&oacute;, sẽ kh&ocirc;ng ai c&oacute; thể đọ được với m&igrave;nh nữa, bọn khốn nạn! Tao sẽ bắt ch&uacute;ng m&agrave;y trả gi&aacute; v&igrave; đ&atilde; đ&aacute;nh tao!&rdquo;</p>\r\n\r\n<p>Niềm vui d&acirc;ng tr&agrave;o như thủy triều khi t&ocirc;i cầm cuốn s&aacute;ch l&ecirc;n.</p>\r\n\r\n<p>[Bạn đ&atilde; nhận được cuốn s&aacute;ch hiếm của thợ chế tạo huyền thoại!]</p>\r\n\r\n<p>[Quyển s&aacute;ch c&oacute; trạng th&aacute;i kh&ocirc;ng b&igrave;nh thường. Bạn c&oacute; thể kiểm tra th&ocirc;ng tin bằng c&aacute;ch sử dụng Con mắt của Pallalian.]</p>\r\n\r\n<p>[Bạn c&oacute; muốn sử dụng Con mắt của Pallalian?]</p>\r\n\r\n<p>&ldquo;Con mắt của Pallalian? Cần một trang bị cấp cao để kiểm tra c&aacute;i n&agrave;y &agrave;?&rdquo;</p>\r\n\r\n<p>Con mắt của Pallalian l&agrave; trang bị đắt nhất thuộc loại thẩm định. 6 th&aacute;ng trước t&ocirc;i đ&atilde; mua n&oacute; ph&ograve;ng trường hợp gặp may mắn n&agrave;o đ&oacute;, tuy nhi&ecirc;n tới giờ t&ocirc;i vẫn chưa c&oacute; cơ hội để d&ugrave;ng n&oacute; n&ecirc;n n&oacute; vẫn đang trong h&ograve;m đồ.</p>\r\n\r\n<p>&ldquo;M&igrave;nh kh&ocirc;ng muốn bỏ lỡ cơ hội ng&agrave;n v&agrave;ng n&agrave;y&hellip;&rdquo;</p>\r\n\r\n<p>T&ocirc;i ho&atilde;n việc thẩm định lại v&agrave; kiểm tra c&aacute;i b&agrave;n bằng v&agrave;ng. T&ocirc;i nghĩ rằng m&igrave;nh c&oacute; thể lấy v&agrave;ng ra được nhưng c&aacute;i b&agrave;n kh&ocirc;ng suy chuyển g&igrave; cả. K&eacute;o, đ&aacute; v&agrave; thậm ch&iacute; lấy răng cạp cũng v&ocirc; dụng. C&aacute;i b&agrave;n n&agrave;y giống như đống trang bị l&uacute;c trước m&agrave; t&ocirc;i kh&ocirc;ng thể lấy.</p>\r\n\r\n<p>&ldquo;Haiz, m&igrave;nh kh&ocirc;ng lấy được g&igrave; cả v&igrave; hệ thống game n&agrave;y hoạt động hiệu quả qu&aacute;. C&aacute;i gi&aacute; phải trả cho 3 th&aacute;ng l&agrave;m việc của m&igrave;nh cao thật.&rdquo;</p>\r\n\r\n<p>T&ocirc;i nh&igrave;n chằm chằm v&agrave;o cuốn s&aacute;ch trong tay m&igrave;nh.</p>\r\n\r\n<p>&lsquo;M&igrave;nh sẽ đưa quyển s&aacute;ch n&agrave;y cho Ashur n&ecirc;n m&igrave;nh c&oacute; cần phải d&ugrave;ng vật phẩm đắt tiền để thẩm định n&oacute; nữa kh&ocirc;ng nhỉ?&rsquo;</p>\r\n\r\n<p>T&ocirc;i dằn vặt một l&uacute;c trước khi mang Mắt của Pallalian ra. Thật ra th&igrave; cũng l&agrave; b&igrave;nh thường th&ocirc;i khi phải băn khoăn về nguy&ecirc;n nh&acirc;n cho 3 th&aacute;ng khổ sở vừa qua.</p>\r\n\r\n<p>&ldquo;Thẩm định.&rdquo;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"color:#f1c40f\">[Cuốn s&aacute;ch Hiếm của Pagma]<br />\r\n			Xếp hạng: Huyền thoại</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"color:#f1c40f\">Đ&acirc;y l&agrave; cuốn s&aacute;ch chứa đựng những tuyệt kỹ của Pagma &ndash; thợ r&egrave;n đ&atilde; vượt qua được giới hạn của con người. Ai mở quyển s&aacute;ch n&agrave;y ra c&oacute; thể trở th&agrave;nh thợ r&egrave;n huyền thoại.<br />\r\n			Hiệu ứng: Trở th&agrave;nh Hậu duệ của Pagma.<br />\r\n			Điều kiện sử dụng: Kh&ocirc;ng.<br />\r\n			* Cấp độ v&agrave; c&aacute;c chỉ số sẽ bị thay đổi khi sử dụng.</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>[Một vật phẩm huyền thoại đ&atilde; được t&igrave;m thấy!]</p>\r\n\r\n<p>[Danh vọng tr&ecirc;n to&agrave;n lục địa sẽ tăng 500.]</p>\r\n\r\n<p>Cơ thể t&ocirc;i bắt đầu run rẩy..</p>\r\n\r\n<p>&ldquo;Whoa&hellip;tr&uacute;ng số rồi&hellip;&rdquo;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; một vật phẩm huyền thoại! Danh vọng tr&ecirc;n to&agrave;n lục địa của m&igrave;nh tăng 500 chỉ với việc t&igrave;m ra n&oacute;! Thật l&agrave; một c&uacute; thu hoạch ngoạn mục v&igrave; việc tăng danh vọng trong một th&agrave;nh phố l&ecirc;n 100 đ&atilde; kh&oacute; rồi.</p>\r\n\r\n<p>&lsquo;L&agrave; danh vọng thật &agrave;&hellip;&rsquo;</p>\r\n\r\n<p>T&ocirc;i tự hỏi rằng m&igrave;nh c&oacute; đọc sai g&igrave; kh&ocirc;ng. Nhưng khi xem kỹ lại th&ocirc;ng tin vật phẩm th&igrave; kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c cả.</p>\r\n\r\n<p>&ldquo;Tr&uacute;ng~~số!!&rdquo;</p>\r\n\r\n<p>T&ocirc;i kh&ocirc;ng bị hoang tưởng. T&ocirc;i phấn kh&iacute;ch tột độ v&agrave; c&oacute; thể nghe thấy tiếng tim m&igrave;nh đập th&igrave;nh thịch. T&ocirc;i đ&atilde; tưởng n&oacute; chỉ l&agrave; một cuốn s&aacute;ch kỹ năng, nhưng thực ra lại l&agrave; một cuốn s&aacute;ch chuyển nghề? Hơn nữa c&ograve;n l&agrave; hạng huyền thoại! Nghĩa rằng đ&acirc;y l&agrave; nghề mạnh nhất tr&ecirc;n thế giới!</p>\r\n\r\n<p>&ldquo;Ngo&agrave;i ra, c&ograve;n kh&ocirc;ng c&oacute; điều kiện sử dụng nữa&hellip;&rdquo;</p>\r\n\r\n<p>Nước mắt t&ocirc;i chảy d&agrave;n dụa. Suốt năm vừa qua, t&ocirc;i đ&atilde; phải trả tiền l&atilde;i vay v&agrave; tiền t&agrave;i khoản Satisfy v&agrave; t&ocirc;i cũng phải bỏ học. Bạn b&egrave; từng người một bỏ đi, c&aacute;c bạn học th&igrave; cười nhạo t&ocirc;i, mọi người nghĩ t&ocirc;i l&agrave; một thằng lười&hellip;</p>\r\n\r\n<p>Kế hoạch ban đầu của t&ocirc;i l&agrave; b&aacute;n c&aacute;c vật phẩm kiếm được trong game v&agrave; lấy tiền để trả nợ, trả cho t&agrave;i khoản game v&agrave; trả học ph&iacute;. Tuy nhi&ecirc;n, Satisfy kh&ocirc;ng phải l&agrave; một thế giới c&ocirc;ng bằng. Rất l&agrave; kh&oacute; để kiếm tiền từ n&oacute; v&agrave; t&ocirc;i chỉ kiếm được vừa đủ đồ để mặc chứ n&oacute;i g&igrave; đến b&aacute;n lấy tiền.</p>\r\n\r\n<p>Nhưng giờ th&igrave; đ&atilde; kh&aacute;c. Một cảm gi&aacute;c hồi hộp xuất hiện khi t&ocirc;i đang r&ugrave;ng m&igrave;nh..</p>\r\n\r\n<p>&ldquo;Kết th&uacute;c rồi&hellip; Giờ m&igrave;nh c&oacute; thể tạm biệt kiếp sống địa ngục n&agrave;y!&rdquo;</p>\r\n\r\n<p>Một vật phẩm chuyển nghề huyền thoại! Chỉ cần mang n&oacute; l&ecirc;n đấu gi&aacute; online th&ocirc;i th&igrave; n&oacute; đ&atilde; đ&aacute;ng gi&aacute; gần 30 triệu vnđ rồi. (*ND: đọc chương giới thiệu để hiểu c&aacute;ch t&iacute;nh tiền nh&eacute;!)</p>\r\n\r\n<p>Kh&ocirc;ng, n&oacute; l&agrave; một vật phẩm cho ra nghề ngon nhất trong số hai tỷ người d&ugrave;ng, l&agrave;m sao m&agrave; chỉ đ&aacute;ng gi&aacute; 30 triệu được? M&igrave;nh chắc chắn rằng n&oacute; trị gi&aacute; &iacute;t nhất v&agrave;i trăm triệu đồng. M&igrave;nh c&oacute; thể đang chạm tới một đống tiền m&agrave; m&igrave;nh kh&ocirc;ng bao giờ tưởng tượng ra được!</p>\r\n\r\n<p>&ldquo;Puhahahahat !! Mẹ ơi! Bố ơi! Thằng ăn b&aacute;m n&agrave;y cuối c&ugrave;ng đ&atilde; l&agrave;m được! B&acirc;y giờ l&agrave; l&uacute;c quit game v&agrave; qu&ecirc;n đi việc đi l&agrave;m c&ocirc;ng ăn lương! Bố mẹ sẽ kh&ocirc;ng phải xấu hổ về con trước mặt h&agrave;ng x&oacute;m nữa! Sehee &agrave;! Cuối c&ugrave;ng anh cũng đ&atilde; l&agrave;m được! B&acirc;y giờ em sẽ kh&ocirc;ng phải xấu hổ mỗi khi gặp anh tr&ecirc;n đường nữa! Nếu bạn b&egrave; muốn đến nh&agrave; m&igrave;nh chơi, m&igrave;nh cũng kh&ocirc;ng phải từ chối v&agrave; viện đủ l&yacute; do nữa! V&agrave; bọn bạn m&igrave;nh! Cả bọn học sinh nữa! Ch&uacute;ng n&oacute; sẽ kh&ocirc;ng thể coi thường m&igrave;nh như một thằng chơi game thất bại nữa! Tao đ&atilde; th&agrave;nh c&ocirc;ng với game! Tao đ&atilde; đi trước ch&uacute;ng m&agrave;y một bước trong x&atilde; hội rồi! Puhahahat !!&rdquo;</p>\r\n\r\n<p>T&ocirc;i đ&atilde; bỏ 30 triệu để mua một c&aacute;i m&aacute;y chơi game, v&agrave; chỉ t&iacute;nh tiền l&atilde;i th&ocirc;i đ&atilde; l&agrave; h&agrave;ng triệu đồng mỗi th&aacute;ng.</p>\r\n\r\n<p>&lsquo;Ahyoung vẫn đẹp tuyệt vời&hellip;&rsquo;</p>\r\n\r\n<p>T&ocirc;i tưởng tượng đến việc tham dự cuộc họp lớp cấp 3 của t&ocirc;i lần đầu ti&ecirc;n sau 2 năm trong một chiếc &ocirc; t&ocirc; ngoại. Bọn bạn học th&agrave;nh c&ocirc;ng sẽ kh&ocirc;ng thể l&agrave;m lơ t&ocirc;i nữa. T&ocirc;i bỗng đỏ mặt khi tưởng tượng thấy mối t&igrave;nh đầu của m&igrave;nh, Ahyoung xuất hiện.</p>\r\n\r\n<p>&ldquo;Okay, vậy m&igrave;nh sẽ b&aacute;n n&oacute; đi!&rdquo;</p>\r\n\r\n<p>Nhiệm vụ &agrave;? T&ocirc;i kh&ocirc;ng quan t&acirc;m nữa. R&otilde; r&agrave;ng th&ocirc;i, phần thưởng nhiệm vụ chỉ l&agrave; vũ kh&iacute; hạng Sử thi, ho&agrave;n to&agrave;n kh&ocirc;ng thể so s&aacute;nh được với gi&aacute; trị của cuốn s&aacute;ch n&agrave;y. Điểm th&acirc;n mật của t&ocirc;i với B&aacute; tước Ashur sẽ giảm v&agrave; t&ocirc;i sẽ kh&ocirc;ng nhận được nhiệm vụ từ hắn ta nữa. Nhưng chẳng c&oacute; l&yacute; do g&igrave; để sợ khi đứng trước h&agrave;ng tỷ đồng cả.</p>\r\n\r\n<p>&ldquo;Đăng xuất!&rdquo;</p>\r\n\r\n<p>T&ocirc;i tự tin k&ecirc;u l&ecirc;n. Rồi một cửa sổ th&ocirc;ng b&aacute;o l&oacute;e l&ecirc;n trước mắt t&ocirc;i.</p>\r\n\r\n<p>[Bạn kh&ocirc;ng thể đăng xuất khỏi tr&ograve; chơi l&uacute;c n&agrave;y.]</p>\r\n\r\n<p>[B&aacute; tước Ashur đ&atilde; xuất hiện.]</p>\r\n\r\n<p>&ldquo;&hellip;&hellip;?&rdquo;</p>\r\n\r\n<p>H&agrave;ng chục kị sĩ bỗng nhảy v&agrave;o hang trong khi t&ocirc;i đang hoang mang kh&ocirc;ng hiểu v&igrave; sao. Rồi B&aacute; tước Ashur quen thuộc xuất hiện. T&ocirc;i hốt hoảng khi hắn thấy t&ocirc;i v&agrave; khu&ocirc;n mặt hắn cau c&oacute;.</p>\r\n\r\n<p>&ldquo;Lữ kh&aacute;ch ngu ngốc, c&aacute;c người đều tham lam giống nhau.&rdquo;</p>\r\n\r\n<p>[Nhiệm vụ &lsquo;Y&ecirc;u cầu b&iacute; mật của B&aacute; tước Ashur&rsquo; (S) đ&atilde; chuyển th&agrave;nh &lsquo;Sự giận dữ của B&aacute; tước Ashur (SS).]</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"color:#f1c40f\">[&lsquo;Sự giận dữ của B&aacute; tước Ashur]<br />\r\n			Độ kh&oacute;: SS</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"color:#f1c40f\">B&aacute; tước Ashur đ&atilde; chọn bạn do khả năng tầm thường v&agrave; sự ngu ngốc của bạn v&agrave; bởi v&igrave; bạn dễ bị thao t&uacute;ng. Ng&agrave;i giao cho bạn nhiệm vụ t&igrave;m kiếm cuốn s&aacute;ch hiếm của Pagma m&agrave; ng&agrave;i ấy kh&ocirc;ng chắc n&oacute; c&oacute; tồn tại.<br />\r\n			Ng&agrave;i đ&atilde; theo d&otilde;i bạn v&agrave; h&agrave;nh động ngay lập tức ngay khi biết bạn t&igrave;m ra &ldquo;Động Tận c&ugrave;ng ph&iacute;a Bắc&rdquo;. B&aacute; tước Ashur đ&atilde; chứng kiến việc bạn trở n&ecirc;n m&ugrave; qu&aacute;ng bởi l&ograve;ng tham với cuốn s&aacute;ch hiếm của Pagma v&agrave; Ng&agrave;i kh&ocirc;ng c&oacute; &yacute; định tha thứ cho bạn. Ng&agrave;i quyết định giết bạn v&agrave; lấy đi cuốn s&aacute;ch hiếm của Pagma.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"color:#f1c40f\">* Điểm th&acirc;n mật với B&aacute; tước Ashur giảm 100.<br />\r\n			* V&igrave; h&agrave;nh động của bạn, tất cả danh vọng tại th&agrave;nh phố Patrian đ&atilde; biến mất v&agrave; bạn trở n&ecirc;n v&ocirc; danh. Điểm th&acirc;n mật với cư d&acirc;n Patrian giảm 40. Họ sẽ gọi bạn l&agrave; kẻ cắp khi gặp bạn.<br />\r\n			Điều kiện ho&agrave;n th&agrave;nh: C&aacute;i chết của B&aacute; tước Ashur v&agrave; c&aacute;c kị sĩ hộ tống.<br />\r\n			Phần thưởng: Nhận được danh hiệu &lsquo;Kẻ s&aacute;t hại Qu&yacute; tộc.&rsquo;<br />\r\n			* Kẻ s&aacute;t hại Qu&yacute; tộc: Tr&iacute; tuệ -50.<br />\r\n			Giới qu&yacute; tộc của vương quốc sẽ coi thường v&agrave; &aacute;p bức bạn. C&ocirc;ng việc của bạn sẽ bị hạn chế. Điểm th&acirc;n mật với một số thường d&acirc;n v&agrave; ho&agrave;ng gia sẽ tăng.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"color:#f1c40f\">Nhiệm vụ thất bại: Cấp độ -2.<br />\r\n			Bạn sẽ kh&ocirc;ng thể v&agrave;o Patrian.<br />\r\n			Mọi binh l&iacute;nh Patrian sẽ giết bạn khi thấy bạn.</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Patrian l&agrave; th&agrave;nh phố m&agrave; t&ocirc;i tạo nh&acirc;n vật. T&ocirc;i đ&atilde; x&acirc;y dựng được một lượng danh vọng đ&aacute;ng kể sau khi ho&agrave;n th&agrave;nh c&aacute;c nhiệm vụ ở Patrian từ cấp 1 cho đến nay, v&agrave; điểm th&acirc;n mật của t&ocirc;i với cư d&acirc;n cũng đang ở mức tối đa.</p>\r\n\r\n<p>Mọi thương nh&acirc;n sẽ giảm gi&aacute; 20% khi t&ocirc;i mua h&agrave;ng h&oacute;a v&agrave; nếu t&ocirc;i b&aacute;n th&igrave; họ sẽ trả th&ecirc;m 15% gi&aacute; th&ocirc;ng thường. Cư d&acirc;n thi thoảng sẽ cho t&ocirc;i nhiệm vụ ẩn. Nhưng b&acirc;y giờ mọi nỗ lực n&agrave;y đ&atilde; tan th&agrave;nh m&acirc;y kh&oacute;i sau nhiệm vụ n&agrave;y.</p>\r\n\r\n<p>&ldquo;&hellip;Ah, V&atilde;i cả ***.&rdquo;</p>\r\n\r\n<p>Một c&acirc;u chửi thề tự nhi&ecirc;n ph&aacute;t ra.</p>\r\n\r\n<p>&ldquo;B&aacute; tước Ashur&hellip; kh&ocirc;ng, con ch&oacute; n&agrave;y! M&agrave;y d&aacute;m bắt tao đi t&igrave;m c&aacute;i vật phẩm m&agrave; m&agrave;y cũng kh&ocirc;ng chắc n&oacute; c&oacute; tồn tại hay kh&ocirc;ng! Nếu cuốn s&aacute;ch n&agrave;y kh&ocirc;ng tồn tại th&igrave; tao đ&atilde; ph&iacute; ho&agrave;i mấy th&aacute;ng v&ocirc; &iacute;ch! M&agrave;y l&agrave; người lừa tao trước, vậy m&agrave; m&agrave;y lại khiến tao mang tiếng xấu ở Patrian? Con ch&oacute; bẩn thỉu n&agrave;y!&rdquo;</p>\r\n\r\n<p>Ngay khi chủ của m&igrave;nh bị x&uacute;c phạm, những kị sĩ nổi đi&ecirc;n v&agrave; ngay lập tức r&uacute;t kiếm ra. B&aacute; tước Ashur ngăn họ lại v&agrave; n&oacute;i lạnh l&ugrave;ng với t&ocirc;i: &ldquo;Ngươi đ&atilde; chọn phản bội ta ngay khi ngươi biết ngươi bị lừa. Vậy m&agrave; ngươi vẫn ra vẻ trong sạch sao.&rdquo;</p>\r\n\r\n<p>&ldquo;Chẳng lẽ l&agrave; tham lam sao khi tao chỉ muốn một phần thưởng tương xứng với c&ocirc;ng sức của tao? Sự phản bội của tao kh&ocirc;ng phải đ&atilde; quyết từ trước!&rdquo;</p>\r\n\r\n<p>Hắn ta cười khẩy với t&ocirc;i.</p>\r\n\r\n<p>&ldquo;Chẳng lẽ ta chưa hứa sẽ thưởng cho ngươi sao? Ch&uacute;ng ta đ&atilde; c&oacute; giao ước v&agrave; ngươi lại giẫm l&ecirc;n l&ograve;ng tốt của ta v&igrave; l&ograve;ng tham của ngươi.&rdquo;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; chuyện m&agrave; t&ocirc;i kh&ocirc;ng thể giải quyết bằng lời. Thấy t&ocirc;i c&acirc;m lặng, B&aacute; tước Ashur tiếp tục n&oacute;i,</p>\r\n\r\n<p>&ldquo;Ngươi đ&uacute;ng l&agrave; một kẻ tội nghiệp. C&agrave;ng nh&igrave;n ngươi ta c&agrave;ng cảm thấy điều đ&oacute;. Thi thoảng cũng c&oacute; l&uacute;c ngươi tỏa s&aacute;ng, nhưng ta kh&ocirc;ng hề tr&ocirc;ng đợi ngươi c&oacute; thể t&igrave;m thấy hang động đ&atilde; biến mất khỏi lịch sử n&agrave;y. Ta sẽ trừng phạt v&igrave; sự lỗ m&atilde;ng của ngươi, nhưng ta sẽ cố gắng giết ngươi kh&ocirc;ng đau đớn để thưởng cho sự ki&ecirc;n tr&igrave; của ngươi. .&rdquo;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:1332px; top:2672.56px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 1, 'chapter-1', NULL, '2023-02-04 16:19:59');
INSERT INTO `chapters` VALUES (9, 'Chapter 2', 10, 'Chapter 2 của truyện', '<p>&lsquo;Con ch&oacute; n&agrave;y!&rsquo;</p>\r\n\r\n<p>T&ocirc;i chỉ muốn đ&acirc;m kiếm v&agrave;o miệng t&ecirc;n Ashur ngay l&uacute;c n&agrave;y nhưng đ&oacute; l&agrave; điều kh&ocirc;ng thể. Level của hắn ta l&agrave; 300 v&agrave; cũng l&agrave; một ph&aacute;p sư vĩ đại đ&atilde; ho&agrave;n th&agrave;nh Nhiệm vụ 3 chuyển. Đ&aacute;m kị sĩ hộ tống cũng c&oacute; level trung b&igrave;nh l&agrave; 180.</p>\r\n\r\n<p>Mặt kh&aacute;c, t&ocirc;i chỉ c&oacute; level 73 v&agrave; cũng kh&ocirc;ng c&oacute; khả năng chiến đấu 1 chọi 1 với t&ecirc;n kị sĩ trẻ nhất l&agrave;m nhiệm vụ tr&agrave; nước nữa.</p>\r\n\r\n<p>Nhưng nh&igrave;n v&agrave;o điều kiện của nhiệm vụ th&igrave; t&ocirc;i phải giết hết bọn ch&uacute;ng.</p>\r\n\r\n<p>Kể cả vậy! Nếu t&ocirc;i ho&agrave;n th&agrave;nh nhiệm vụ n&agrave;y, phần thưởng của t&ocirc;i cũng chỉ l&agrave; r&aacute;c rưởi?</p>\r\n\r\n<p>&lsquo;Tr&iacute; tuệ bị trừ 50 điểm? Mẹ, giờ t&ocirc;i c&oacute; mỗi 30 Tr&iacute; tuệ m&agrave; bị trừ mất 50 điểm th&igrave; t&ocirc;i th&agrave;nh thằng ngu c&ograve;n g&igrave;? V&agrave; tất cả Qu&yacute; tộc trong vương quốc sẽ gh&eacute;t t&ocirc;i? Thế th&igrave; t&ocirc;i tho&aacute;t khỏi ho&agrave;n cảnh n&agrave;y bằng c&aacute;ch n&agrave;o?? Tại sao lại c&oacute; c&aacute;i nhiệm vụ như n&agrave;y nhỉ? Ahhh!!&rsquo;</p>\r\n\r\n<p>Chỉ c&ograve;n một c&aacute;ch. Đ&oacute; l&agrave; chết. T&ocirc;i sẽ mất 2 level, v&igrave; t&ocirc;i cũng c&oacute; thắng được đ&acirc;u. M&agrave; thắng để l&agrave;m g&igrave; với c&aacute;i nhiệm vụ ng&aacute;o ngơ n&agrave;y?</p>\r\n\r\n<p>&ldquo;Hu&hellip;&rdquo;</p>\r\n\r\n<p>T&ocirc;i muốn bỏ cuộc. &Aacute;p lực tỏa ra từ đội kị sĩ kh&ocirc;ng thể coi thường.</p>\r\n\r\n<p>&lsquo;Nghĩa l&agrave; t&ocirc;i sẽ mất 8 level chỉ v&igrave; c&aacute;i nhiệm vụ n&agrave;y&hellip; Ashur&hellip; Ng&agrave;y n&agrave;o đ&oacute; tao sẽ l&ecirc;n level cao v&agrave; trả th&ugrave;. Chắc chắn l&agrave; thế.&rsquo;</p>\r\n\r\n<p>T&ocirc;i lườm Ashur v&agrave; thề sẽ trả th&ugrave;, sau đ&oacute; t&ocirc;i nhắm mắt trước những thanh gươm chĩa v&agrave;o người m&igrave;nh. Bất chợt, t&ocirc;i nhớ ra cuốn s&aacute;ch của Pagma trong tay m&igrave;nh.</p>\r\n\r\n<p>&lsquo;Ơ, c&ograve;n c&aacute;i n&agrave;y th&igrave; sao?&rsquo;</p>\r\n\r\n<p>Nếu cất v&agrave;o h&ograve;m đồ th&igrave; liệu n&oacute; c&ograve;n ở trong đ&oacute; sau khi m&igrave;nh chết kh&ocirc;ng? Đ&acirc;y kh&ocirc;ng phải vật phẩm nhiệm vụ th&ocirc;ng thường n&ecirc;n t&ocirc;i kh&ocirc;ng chắc lắm.</p>\r\n\r\n<p>Mục đ&iacute;ch của Ashur ban đầu l&agrave; g&igrave;? L&agrave; cuốn s&aacute;ch hiếm của Pagma. L&iacute; do hắn ta muốn giết t&ocirc;i l&agrave; để cướp cuốn s&aacute;ch. Vậy theo logic c&acirc;u chuyện, cuốn s&aacute;ch phải biến mất khỏi h&ograve;m đồ của t&ocirc;i nếu hắn ta giết t&ocirc;i th&igrave; mới rơi v&agrave;o tay hắn được.</p>\r\n\r\n<p>&ldquo;Mẹ!&rdquo;</p>\r\n\r\n<p>Đằng n&agrave;o cũng mất cuốn s&aacute;ch, vậy tốt nhất t&ocirc;i n&ecirc;n sử dụng n&oacute; thay v&igrave; b&aacute;n! T&ocirc;i nghĩ đến một lựa chọn kh&aacute;c, đ&oacute; l&agrave; t&ocirc;i c&oacute; thể sử dụng cuốn s&aacute;ch hiếm của Pagma</p>\r\n\r\n<p>Tuy nhi&ecirc;n, t&ocirc;i ngập ngừng. T&ocirc;i gh&eacute;t c&aacute;c nghề về chế tạo. Chẳng phải mục ti&ecirc;u của chơi game l&agrave; tận hưởng sự th&iacute;ch th&uacute; của việc đ&aacute;nh nhau? Vậy nếu t&ocirc;i trở th&agrave;nh thợ r&egrave;n th&igrave; t&ocirc;i sẽ phải đổ mồ h&ocirc;i cả ng&agrave;y với việc nện b&uacute;a v&agrave;o sắt b&ecirc;n cạnh đống lửa. T&ocirc;i cũng c&oacute; thể cần phải cầm r&igrave;u để đi đ&agrave;o mỏ thu thập nguy&ecirc;n liệu nữa.</p>\r\n\r\n<p>Chắc l&agrave; vậy rồi&hellip;</p>\r\n\r\n<p>&ldquo;Lao động ch&acirc;n tay&rdquo;</p>\r\n\r\n<p>L&uacute;c n&agrave;y, t&ocirc;i r&ugrave;ng m&igrave;nh. V&igrave; trước đ&oacute; t&ocirc;i vẫn l&agrave;m phụ hồ ở c&ocirc;ng trường x&acirc;y dựng ngo&agrave;i đời thực, v&agrave; b&acirc;y giờ t&ocirc;i cũng phải lao động ch&acirc;n tay trong game nữa!! Chỉ nghĩ th&ocirc;i cũng khiến t&ocirc;i ch&oacute;ng mặt.</p>\r\n\r\n<p>&ldquo;Kuock&hellip;&rdquo;</p>\r\n\r\n<p>T&ocirc;i vẫn muốn m&igrave;nh l&agrave; một chiến binh; ước mơ của t&ocirc;i l&agrave; vung kiếm v&agrave;o qu&acirc;n địch v&agrave; trở th&agrave;nh đại tướng qu&acirc;n đội một ng&agrave;y n&agrave;o đ&oacute;. Nhưng giờ t&ocirc;i phải từ bỏ giấc mơ ấy. Một vật phẩm trị gi&aacute; mấy trăm triệu đồng sẽ bị một NPC lấy mất! Bởi thằng ch&oacute; Ashur ấy!</p>\r\n\r\n<p>&ldquo;Th&agrave; d&ugrave;ng n&oacute; c&ograve;n hơn bị hắn ta lấy!&rdquo;</p>\r\n\r\n<p>T&ocirc;i đau đớn mở cuốn s&aacute;ch của Pagma. Ashur sốc v&agrave; vội v&atilde; giơ tay l&ecirc;n niệm một c&acirc;u thần ch&uacute;.</p>\r\n\r\n<p>&ldquo;Qu&aacute; muộn rồi, đồ mất dạy.&rdquo;</p>\r\n\r\n<p>T&ocirc;i cười nhạo hắn trước khi im lặng nh&igrave;n những &ocirc; th&ocirc;ng b&aacute;o xuất hiện trước mắt.</p>\r\n\r\n<p>[Bạn đ&atilde; trở th&agrave;nh Hậu duệ của Pagma.]</p>\r\n\r\n<p>[Level của bạn đ&atilde; giảm xuống.]</p>\r\n\r\n<p>[Bạn đạt level 1.]</p>\r\n\r\n<p>[Mọi kỹ năng v&agrave; chỉ số đ&atilde; bị x&oacute;a.]</p>\r\n\r\n<p>&ldquo;&hellip;&hellip;?&rdquo;</p>\r\n\r\n<p>T&ocirc;i kh&ocirc;ng tin v&agrave;o mắt m&igrave;nh nữa. Kh&ocirc;ng phải chuyển nghề sẽ gi&uacute;p bạn mạnh l&ecirc;n bằng c&aacute;ch tăng th&ecirc;m v&agrave;o chỉ số, level, kỹ năng hiện c&oacute; sao? Do vậy t&ocirc;i kh&ocirc;ng hiểu v&igrave; sao level, chỉ số v&agrave; kỹ năng của t&ocirc;i bị x&oacute;a sạch thay v&igrave; giữ nguy&ecirc;n.</p>\r\n\r\n<p>&ldquo;Chuyện g&igrave; đ&acirc;y?&rdquo;</p>\r\n\r\n<p>Trong khi t&ocirc;i vẫn đang lo lắng th&igrave; th&ocirc;ng b&aacute;o mới xuất hiện.</p>\r\n\r\n<p>Chỉ số Kh&eacute;o l&eacute;o l&agrave; chỉ số cơ bản m&agrave; nghề n&agrave;o li&ecirc;n quan chế tạo cũng sẽ c&oacute;. Vậy c&ograve;n c&aacute;c chỉ số Điềm tĩnh, Bất khuất, Uy quyền v&agrave; S&aacute;ng suốt th&igrave; sao. Ch&uacute;ng đều lạ l&ugrave;ng v&agrave; kh&ocirc;ng biết c&oacute; tốt kh&ocirc;ng?</p>\r\n\r\n<p>&ldquo;Kh&ocirc;ng tốt!&rdquo;</p>\r\n\r\n<p>T&ocirc;i tuyệt vọng. T&ocirc;i c&oacute; 15 kỹ năng bị động v&agrave; 22 kỹ năng chủ động khi c&ograve;n l&agrave; 1 chiến binh. B&acirc;y giờ th&igrave; t&ocirc;i mất hết c&aacute;c kỹ năng n&agrave;y. Kết quả của việc chuyển nghề l&agrave; t&ocirc;i ho&agrave;n to&agrave;n trở th&agrave;nh một cửu vạn rồi.</p>\r\n\r\n<p>Đ&atilde; thế, t&ocirc;i chỉ c&ograve;n 1 level. Bao nhi&ecirc;u c&ocirc;ng sức 1 năm qua đ&atilde; biến mất, giờ t&ocirc;i phải l&agrave;m lại từ đầu? T&ocirc;i phải leo lại thang level từ đầu?!</p>\r\n\r\n<p>&ldquo;Sao t&ocirc;i lại nhận phải c&aacute;i nhiệm vụ n&agrave;y nhỉ? M&oacute;a!@&hellip;.~#@&hellip;.$@&hellip;.^%$!!&rdquo;</p>\r\n\r\n<p>T&ocirc;i lẩm bẩm v&agrave;i c&acirc;u chửi thề trong khi kiếm của một kị sĩ nhanh ch&oacute;ng đ&acirc;m tới.</p>\r\n\r\n<p>[Bạn đ&atilde; bị ch&eacute;m tr&uacute;ng!]</p>\r\n\r\n<p>[Bạn đ&atilde; chết.]</p>\r\n\r\n<p>Stamina của level 1 lập tức giảm xuống 0 chỉ với 1 hit của kị sĩ level 180.</p>\r\n\r\n<p>[Nhiệm vụ thất bại!]</p>\r\n\r\n<p>[Bạn sẽ kh&ocirc;ng được ph&eacute;p v&agrave;o Patrian nữa.]</p>\r\n\r\n<p>[Mọi binh l&iacute;nh Patrian sẽ giết bạn khi nh&igrave;n thấy bạn.]</p>\r\n\r\n<p>[Level của bạn giảm 2.]</p>\r\n\r\n<p>[Bạn đang ở level -1.]</p>\r\n\r\n<p>[Bạn chưa đăng k&yacute; tạm tr&uacute; ở đ&acirc;u. Bạn c&oacute; muốn hồi sinh tại đền thờ gần nhất kh&ocirc;ng?]</p>\r\n\r\n<p>&ldquo;&hellip;&hellip;?&rdquo;</p>\r\n\r\n<p>T&ocirc;i nh&igrave;n v&agrave;o level của m&igrave;nh v&agrave; thất vọng.</p>\r\n\r\n<p>&ldquo;&Acirc;m? Level của m&igrave;nh bị &acirc;m??&rdquo;</p>\r\n\r\n<p>T&ocirc;i chắc chắn rằng m&igrave;nh đ&atilde; bị nguyền rủa v&igrave; lỗi lầm n&agrave;o đ&oacute; từ kiếp trước. T&ocirc;i vừa đến được đ&acirc;y sau 3 th&aacute;ng khổ sở v&agrave; đ&acirc;y l&agrave; những g&igrave; t&ocirc;i nhận được? T&ocirc;i kh&ocirc;ng muốn chơi nữa. T&ocirc;i quyết định tho&aacute;t game thay v&igrave; chọn hồi sinh.</p>\r\n\r\n<p>Sau khi tho&aacute;t game, t&ocirc;i truy cập internet v&agrave; t&igrave;m kiếm th&ocirc;ng tin tr&ecirc;n c&aacute;c diễn đ&agrave;n li&ecirc;n quan đến Satisfy để t&igrave;m hiểu về level &acirc;m. Nhưng t&ocirc;i kh&ocirc;ng thể thấy bất kỳ th&ocirc;ng tin n&agrave;o n&ecirc;n t&ocirc;i đ&atilde; tạo một c&acirc;u hỏi tr&ecirc;n diễn đ&agrave;n.</p>\r\n\r\n<p>T&ocirc;i nhấn OK để đăng c&acirc;u hỏi v&agrave; ra khỏi m&aacute;y chơi game. H&igrave;nh ảnh t&ocirc;i trong gương giống một bệnh nh&acirc;n hốc h&aacute;c d&iacute;nh một căn bệnh nan y. Đ&oacute; l&agrave; hậu quả của việc thiếu ngủ trong suốt 3 th&aacute;ng l&agrave;m nhiệm vụ n&agrave;y. Vậy m&agrave; điều duy nhất t&ocirc;i nhận được l&agrave; mất mấy trăm triệu đồng v&agrave; level &acirc;m.</p>\r\n\r\n<p>&ldquo;3 th&aacute;ng c&ocirc;ng lao tan th&agrave;nh mấy kh&oacute;i&hellip; 3 th&aacute;ng chăm chỉ. Hahaha&hellip;&rdquo;</p>\r\n\r\n<p>T&ocirc;i đ&atilde; định đi l&agrave;m phụ hồ nhưng rồi lại quyết định đi đ&agrave;o m&acirc;y. T&ocirc;i đ&atilde; mất thời gian v&agrave; level. T&ocirc;i mất năng lượng v&agrave; nằm gục xuống s&agrave;n. T&ocirc;i thấy buồn, trống rỗng, hối hận v&agrave; kh&oacute; chịu. C&aacute;c cảm x&uacute;c ti&ecirc;u cực cứ thế tr&agrave;n đến, khiến t&ocirc;i c&oacute; cảm gi&aacute;c bất lực. Nếu t&ocirc;i yếu đuối hơn một ch&uacute;t chắc tối sẽ đi tự tử mất.</p>\r\n\r\n<p>&ldquo;&hellip; M&igrave;nh phải l&agrave;m g&igrave; đ&oacute;.&rdquo;</p>\r\n\r\n<p>T&ocirc;i đ&atilde; chơi game rất l&acirc;u rồi, vậy n&ecirc;n t&ocirc;i v&agrave;o nh&agrave; tắm khi cảm thấy m&igrave;nh cần n&oacute;. Đang l&agrave; 3 giờ s&aacute;ng. Mọi th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh t&ocirc;i đang ngủ. Nếu t&ocirc;i l&agrave;m họ thức dậy họ sẽ chửi t&ocirc;i mất.</p>\r\n\r\n<p>T&ocirc;i r&oacute;n r&eacute;n đi qua ph&ograve;ng kh&aacute;ch, nhẹ nh&agrave;ng tắm rửa, rồi quay lại nh&agrave; bếp v&agrave; lấy ra một đĩa thức ăn từ tủ lạnh. T&ocirc;i mất một l&uacute;c l&acirc;u để t&igrave;m c&aacute;i th&igrave;a trong b&oacute;ng tối v&agrave; trở về ph&ograve;ng với một b&aacute;t cơm.</p>\r\n\r\n<p>&ldquo;Oh, cơm rang x&uacute;c x&iacute;ch.&rdquo;</p>\r\n\r\n<p>Hương vị của thịt! Việc ăn uống ngon l&agrave;nh v&agrave; l&agrave;m đầy dạ d&agrave;y của m&igrave;nh khiến t&ocirc;i thư gi&atilde;n v&agrave; c&oacute; thể suy nghĩ s&aacute;ng suốt hơn.</p>\r\n\r\n<p>&ldquo;Sao m&agrave; level của m&igrave;nh bị &acirc;m nhỉ?&rdquo;</p>\r\n\r\n<p>T&ocirc;i nghĩ về n&oacute; trước khi ngồi v&agrave;o m&aacute;y để xem c&acirc;u hỏi m&igrave;nh đ&atilde; đăng l&uacute;c trước.</p>\r\n\r\n<p>&ldquo;G&igrave; c&oacute;?&rdquo;</p>\r\n\r\n<p>L&uacute;c n&agrave;y, lượt xem b&agrave;i đăng của t&ocirc;i đ&atilde; tr&ecirc;n 10,000. Lượt nhận x&eacute;t l&agrave; tr&ecirc;n 2,000!</p>\r\n\r\n<p>&ldquo;Chẳng..lẽ..?&rdquo;</p>\r\n\r\n<p>T&ocirc;i kh&ocirc;ng thể t&igrave;m thấy th&ocirc;ng tin, nhưng chắc l&agrave; việc &acirc;m level rất nổi tiếng v&agrave; mọi người rất phấn kh&iacute;ch v&igrave; n&oacute; mang lại nhiều lợi &iacute;ch? Giấc mở của t&ocirc;i vỡ tan khi t&ocirc;i đọc c&aacute;c nhận x&eacute;t.</p>\r\n\r\n<p>2,000 nhận x&eacute;t như vậy l&agrave;m t&ocirc;i cảm thấy m&igrave;nh giống một thằng ngu.</p>\r\n\r\n<p>&lsquo;Đ&uacute;ng l&agrave; th&ocirc;ng tin về level &acirc;m chưa được biết đến&hellip; vậy m&igrave;nh l&agrave; người đầu ti&ecirc;n?&rsquo;</p>\r\n\r\n<p>Hệ thống của Satisfy được thiết lập để người chơi tự kh&aacute;m ph&aacute; v&agrave; ph&aacute;t triển. Kh&ocirc;ng hề c&oacute; Bộ phận CSKH như c&aacute;c game truyền thống kh&aacute;c. 2 tỷ người chơi của Satisfy tự ph&aacute;t triển thế giới trong năm qua bằng c&aacute;ch từ trao đổi v&agrave; t&igrave;m t&ograve;i kiến thức với nhau.</p>\r\n\r\n<p>Nhưng trong một thế giới rộng lớn như vậy, cũng c&oacute; nhưng th&ocirc;ng tin kh&ocirc;ng ai biết đến, v&iacute; dụ như vấn đề level &acirc;m m&agrave; t&ocirc;i gặp phải.</p>\r\n\r\n<p>R&agrave;o r&agrave;o r&agrave;o&hellip;</p>\r\n\r\n<p>Mưa đang rơi ngo&agrave;i cửa sổ. Theo dự b&aacute;o thời tiết th&igrave; sẽ mưa cả ng&agrave;y, vậy th&igrave; sẽ kh&oacute; m&agrave; đi phụ hồ rồi. T&ocirc;i ăn nốt miếng cơm v&agrave; đăng nhập v&agrave;o game.</p>', 1, 'chapter-2', NULL, NULL);

-- ----------------------------
-- Table structure for comic_users
-- ----------------------------
DROP TABLE IF EXISTS `comic_users`;
CREATE TABLE `comic_users`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `comic_id` bigint(20) UNSIGNED NOT NULL,
  `user_id` bigint(20) UNSIGNED NOT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `comic_users_comic_id_foreign`(`comic_id`) USING BTREE,
  INDEX `comic_users_user_id_foreign`(`user_id`) USING BTREE,
  CONSTRAINT `comic_users_comic_id_foreign` FOREIGN KEY (`comic_id`) REFERENCES `comics` (`id`) ON DELETE CASCADE ON UPDATE RESTRICT,
  CONSTRAINT `comic_users_user_id_foreign` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 19 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of comic_users
-- ----------------------------
INSERT INTO `comic_users` VALUES (12, 1, 2, NULL, NULL);
INSERT INTO `comic_users` VALUES (18, 2, 2, NULL, NULL);

-- ----------------------------
-- Table structure for comics
-- ----------------------------
DROP TABLE IF EXISTS `comics`;
CREATE TABLE `comics`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `author` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `status` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `likes` bigint(20) UNSIGNED NOT NULL DEFAULT 0,
  `views` bigint(20) UNSIGNED NOT NULL DEFAULT 0,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 11 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of comics
-- ----------------------------
INSERT INTO `comics` VALUES (1, 'Ma thổi đèn - Thành Cổ Tinh Tuyệt', 'ma-thoi-den-thanh-co-tinh-tuyet', 'Thiên Hạ Bá Xướng', 'Bùa mô kim, ấn quật mộ, dỡ núi dời non tìm quyết chú\r\nNgười thắp nến, quỷ thổi đèn, dò mồ đổ đấu kiếm vạn tiền...\r\nTừ ngàn năm trước, ở đất nước Trung Hoa đã có nghề trộm mộ, còn gọi là Mô Kim hiệu úy. Dám hành nghề đào mộ quật mả, can trường là chuyện nhỏ, còn phải là tay sâu dày về huyền học, phong thủy... cũng như các loại cạm bẫy.', '1671892418.jpg', '0', 1, 41, '2022-12-24 14:33:38', '2023-02-11 17:00:19');
INSERT INTO `comics` VALUES (2, 'Ma thổi đèn - Mê Động Long Lĩnh', 'ma-thoi-den-me-dong-long-linh', 'Thiên Hạ Bá Xướng', 'Mê Động Long Lĩnh là phần thứ 2 trong bộ truyện Ma Thổi Đèn của tác giả Thiên Hạ Bá Xướng, nói tiếp câu chuyện sau chuyến đi thành cổ Tinh Tuyệt. Ra khỏi ngôi mộ cổ, trên lưng những kẻ còn sống sót bỗng dưng xuất hiện một dấu ấn hình con mắt. Phải chăng đó là lời nguyền từ mấy ngàn năm trước? Bởi giáo sư Tôn, người duy nhất có thể giải được bí mật, vửa nhìn thấy dấu ấn đã kinh hãi thốt lên:\"Thiên cơ không thể tiết lộ...\"', '1671896198.jpg', '0', 1, 4, '2022-12-24 15:36:38', '2023-02-04 15:52:32');
INSERT INTO `comics` VALUES (3, 'Ma thổi đèn - Trùng Cốc Vân Nam', 'ma-thoi-den-trung-coc-van-nam', 'Thiên Hạ Bá Xướng', 'Sau hai chuyến phiêu lưu tới thành cổ Tinh Tuyệt và mê động Long Lĩnh, lần này cả ba người họ lên đường đến Trùng Cốc Vân Nam để tìm Mộc Trần Châu ẩn trong mộ Hiến Vương, một vị vua bí ẩn và đầy tài phép của nước Điền Cổ', '1671896288.jpg', '0', 0, 2, '2022-12-24 15:38:08', '2023-01-31 08:58:25');
INSERT INTO `comics` VALUES (4, 'Ma thổi đèn - Thần Cung Côn Luân', 'ma-thoi-den-than-cung-con-luan', 'Thiên Hạ Bá Xướng', 'Thần Cung Côn Luân là phần thứ 4 trong loạt truyện Ma Thổi Đèn của tác giả Thiên Hạ Bá Xướng. Nối tiếp phần 3 sẽ là cuộc phiêu lưu mạo hiểm đến vùng đất Tây Tạng lạnh lẽo.\r\nMộc trần châu đã có trong tay, nhưng làm thế nào để biết được bí mật ẩn chứa bên trong? Mọi manh mối lần này lại hướng về vùng tuyết phủ Tây Tạng', '1671896988.jpg', '0', 0, 8, '2022-12-24 15:49:48', '2023-02-09 17:11:37');
INSERT INTO `comics` VALUES (5, 'Tổng Tài, Đưa Cục Cưng Cho Tôi', 'tong-tai-dua-cuc-cung-cho-toi', 'Mạc Ngôn Biệt Trí', 'Màn đêm thanh tĩnh, trên bầu trời chi chít những những ngôi sao lấp lánh tinh quang khiến màn đêm càng trở nên lộng lẫy, đó cũng chính là tâm tình hiện tại của Trình Mộ Thanh, hứng khởi, hồi hộp, lại sáng ngời...', '1675187575.jpg', '0', 0, 10, '2023-01-31 17:52:55', '2023-02-09 17:17:53');
INSERT INTO `comics` VALUES (6, 'Đoàn Tàu Thủy Tinh', 'doan-tau-thuy-tinh', 'Khang Thành', 'Từ sáu giờ rưỡi sáng đến tám giờ tối, mỗi chuyến phà ở đây cách nhau một tiếng.\r\nGần tối, người và xe lên đảo rất nhiều, ô-tô xếp thành hai hàng ở giữa, xe máy, người đi bộ chia ra con đường nhỏ ở hai bên. Chung Đình đặt vé xong vào mười mấy phút trước, cô đeo kính râm ngồi trong xe.\r\nPhía ngoài cửa sổ âm u, phong cảnh tạp nham không có trật tự.', '1675187803.jpg', '0', 0, 1, '2023-01-31 17:56:43', '2023-01-31 18:10:02');
INSERT INTO `comics` VALUES (7, 'Thiên Sư Chấp Vị', 'thien-su-chap-vi', 'Phàn Lạc', 'Cậu bây giờ biết cũng không muộn, tôi lấy thân phận chủ tịch cảnh cáo cậu, không được tiếp tục ở đây mê hoặc người khác, trên đời này căn bản không có quỷ thần, cái gọi là thần thánh bói toán đều là vô căn cứ.', '1675188117.jpg', '0', 0, 2, '2023-01-31 18:01:57', '2023-01-31 18:28:38');
INSERT INTO `comics` VALUES (8, 'Sinh Tiếu Thủ Hộ Thần', 'sinh-tieu-thu-ho-than', 'Đường Gia Tam Thiếu', 'Nói về Sinh Tiếu Thủ Hộ Thần, thật ra đây là tập hợp của 13 linh vật: Kỳ Lân (đứng đầu trong Sinh Tiếu Thủ Hộ Thần) và 12 con giáp: Tử Thử, Sửu Ngưu, Dần Hổ, Mão Thỏ, Thần Long, Tị Xà, Ngọ Mã, Vị Dương, Thân Hầu, Dậu Kê, Tuất Cẩu và Hợi Trư.\r\nTuy nhiên một mình hắn là chưa đủ, để làm nên chuyện lớn, hắn buộc phải đi tìm kiếm 12 Sinh Tiếu Thủ Hộ Thần còn lại. Bước đầu tiên hắn làm là thức tỉnh nguồn năng lực đang ngủ sâu trong bản thân mình…', '1675188377.jpg', '0', 0, 1, '2023-01-31 18:06:17', '2023-01-31 18:10:09');
INSERT INTO `comics` VALUES (9, '20 Cách Tỏ Tình Bá Đạo', '20-cach-to-tinh-ba-dao', 'Cú già', '“20 Cách Tỏ Tình Bá Đạo” là tổng hợp của những mẩu chuyện ngắn dễ thương được viết bởi tác giả Cú già. Tên truyện nói về tình yêu và những cách tỏ tình nhưng nội dung dường như lại không giống như vậy.', '1675188767.jpg', '1', 0, 4, '2023-01-31 18:12:47', '2023-02-09 16:56:32');
INSERT INTO `comics` VALUES (10, 'Thợ rèn huyền thoại - Overgeared', 'tho-ren-huyen-thoai-overgeared', 'Dong Wook Lee', 'Shin Youngwoo là nhân vật chính có cuộc sống không may mắn. Cậu đam mê game và lâm vào cảnh nợ nần. Cậu đã phải đi phụ hồ để có tiền trả nợ và chơi game.\r\nCuộc đời cậu thay đổi sau khi nhận được Nghề Thợ Rèn Huyền Thoại trong Satisfy – game thực tế ảo nổi tiếng trong lương lai với hơn 2 tỷ người chơi\r\nTruyện là hành trình trưởng thành về mọi mặt của Youngwoo thông qua các cuộc phiêu lưu và khám phá thế giới Satisfy đầy mê hoặc!', '1675501394.jpg', '0', 0, 21, '2023-02-04 15:47:43', '2023-02-08 16:34:26');

-- ----------------------------
-- Table structure for failed_jobs
-- ----------------------------
DROP TABLE IF EXISTS `failed_jobs`;
CREATE TABLE `failed_jobs`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `uuid` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `connection` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `queue` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `payload` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `exception` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `failed_at` timestamp(0) NOT NULL DEFAULT current_timestamp(0),
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `failed_jobs_uuid_unique`(`uuid`) USING BTREE
